void sortLayers2(out ivec2 a, out ivec2 b) {
    if (a.x > b.x) {
        ivec2 tmp = b; b = a; a = tmp;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 scaledCoord = fragCoord / min(iResolution.x, iResolution.y);
    //    scaledCoord = mix(vec2(-1.), vec2(1.), scaledCoord);
    scaledCoord *= 22.;
    scaledCoord += iTime * normalize(vec2(100., 1.)) * 2.;

    // UNIT GRID
    vec2 unitGridDistances = scaledCoord - round(scaledCoord);
    if (abs(unitGridDistances.x) < .01 || abs(unitGridDistances.y) < .01) {
        fragColor = vec4(scaledCoord, 0., 1.);
        //        return;
    }

    // TRIANGLE CENTER CIRCLES
    vec3 tri = xy2tri(scaledCoord);
    vec3 triTriangleCenter = triCenter(tri);
    vec2 xyTriangleCenter = tri2xy(triTriangleCenter);
    if (distance(xyTriangleCenter, scaledCoord) <.1) {
        fragColor = black;
        //                return;
    }
    vec3 triFract = fract(tri);
    // TRIANGLE GRID
    float w = .01;
    if (abs(triFract.x-.5) > .5-w
    || abs(triFract.y-.5) > .5-w
    || abs(triFract.x + triFract.y - 1.) <w) {
        fragColor = black;
        //                        return;
    }

    // RENDER HEXAGONS
    // As hexagons can potentially overlap each other, we cannot just calculate the nearest hexagon center and get the
    // distance from that. Instead, we use the fact that each corner of a hex touches at most 3 triangles. Each corner
    // of a hex matches the center of a triangle in a triangle grid (see above).
    // For every triangle, we calculate the nearest three hex centers, and calculate the color of that hex and the
    // distance to it.

    // Depending on if we are in a up or down triangle, the offsets are differnt.
    // Mobile browsers do NOT like vec2[3]:
    // vec2[3] offsets = vec2[3](vec2(1, 0), vec2(0, 1), mix(vec2(0), vec2(1), tri.z));
    mat3x2 offsets = mat3x2(vec2(1, 0), vec2(0, 1), mix(vec2(0), vec2(1), tri.z));
    vec4[3] colors;
    ivec2[3] order; // (z-index, i)
    for (int i = 0; i < 3; i++) {
        vec3 hexHexagonCenter = hexFix(floor(tri.xy) + offsets[i]);
        vec2 xyHexagonCenter = hex2xy(hexHexagonCenter);
        vec2 xyDiff = scaledCoord - xyHexagonCenter;
        vec4 c = getColorAt(ivec2(hexHexagonCenter));
        uvec2 upos = uvec2(ivec2(hexHexagonCenter));
        uvec3 rnd = uvec3(upos, upos.x^upos.y);
        vec3 rndf = pcgf(rnd);
        // Calculate a smaller random int, so we can trigger random effects on specific values.
        uint rndx = rnd.x >> 22;
        //                uint rndx = rnd.x >> 28;
        if (rndx == 7u) {
            // angle wrong
            xyDiff.xy *= rot2d(rndf.y);
        }
        if (rndx == 3u) {
            // wrong position
            xyDiff += .2*randomVector(rndf.yz);
        }
        if (rndx == 2u) {
            // grayscale
            c.xyz = c.xxx;
        }
        float d = hexSdf(xy2hex(xyDiff));

        if (rndx == 4u) {
            // wrong size
            d += mix(-.15, .15, rndf.y);
        }
        if (rndx == 5u) {
            // wrong shape: circle
            d = length(xyDiff)*.9;
        }
        if (rndx == 6u) {
            // wrong shape: square
            d = max(abs(xyDiff.x), abs(xyDiff.y));
        }
        colors[i] = c*(calculateHexFalloff(1.-d));
        order[i] = ivec2(hexHexagonCenter.x  + hexHexagonCenter.y*1000., i);
    }

    // Sort the hexagons into a consistent order, so there are no discontinuities at the triangle borders.
    sortLayers2(order[0], order[1]);
    sortLayers2(order[1], order[2]);
    sortLayers2(order[0], order[1]);

    // Blend colors together according to sorted order.
    fragColor = black;
    for (int i = 0; i < 3; i++) {
        int j = order[i].y;
        fragColor = blend(colors[j], fragColor);
    }

    // gamma correction
    fragColor = vec4(pow(fragColor.xyz, vec3(1.0/2.2)), 1.);
}

