vec2 rotate(vec2 v, float a)
{
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

bool onRight(vec2 a, vec2 b, vec2 x)
{
    return cross(vec3(a - b, 0), vec3(x - b, 0)).z > 0.;
}

float cr(vec2 a, vec2 b)
{
    return a.x * b.y - b.x * a.y;
}

float cr(vec2 a, vec2 b, vec2 c)
{
    return cr(b - a, c - b);
}

