uint hash(uint x) {
    x = x * 0xA789BCFDu;
    x = (x ^ (x >> 16)) * 0xA789BCFDu;
    return x;
}

float rand01(uint seed) {
    return float(hash(seed) & 0x7FFFFFFFu) / float(0x7FFFFFFFu);
}

vec3 randvec3m11(uint seed) {
    uint h = hash(seed);
    return vec3(
        float(h & 0xFFFFFFu),
        float((h >> 1) & 0xFFFFFFu),
        float((h >> 2) & 0xFFFFFFu)
    ) / float(0xFFFFFFu) * 2.0 - 1.0;
}

float WorleyNoise(vec3 p, uint seed) {
    ivec3 currentCell = ivec3(floor(p));
    float minDistSq = 1e38;
    for (int x=-1; x<=1; x++)
    for (int y=-1; y<=1; y++)
    for (int z=-1; z<=1; z++) {
        ivec3 cell = currentCell + ivec3(x, y, z);
        uint cellId = uint(cell.z * 0xF54B6267 + cell.y * 0xDE54 + cell.x) + seed;
        vec3 offset = 0.5 + randvec3m11(cellId) * 0.5;
        vec3 p2cp = vec3(cell) + offset - p;
        float distSq = dot(p2cp, p2cp);
        minDistSq = min(minDistSq, distSq);
    }
    return minDistSq;
}

Ray CameraRay(vec2 pix, vec3 camPos, vec3 camTarget) {
    vec2 pixm11 = 2.0 * pix / iResolution.xy - 1.0;
    vec3 f = normalize(camTarget - camPos);
    vec3 r = cross(f, vec3(0, 0, 1));
    vec3 up = cross(r, f);
    float ar = iResolution.y / iResolution.x;
    return Ray(camPos, normalize(pixm11.x * r + pixm11.y * ar * up + f));
}

float SphereDist(vec3 center, float r, vec3 p) {
    return length(p - center) - r;
}

float AABBDist(vec3 lower, vec3 upper, vec3 p) {
    p -= (lower+upper) * 0.5;
    vec3 b = upper - lower;
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

//https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float OpSmoothUnion(float d1, float d2, float k) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float SpheresUnion(vec3 p) {
    vec3 s1c = vec3(0, sin(iTime / 2.0), cos(0.5*iTime));
    vec3 s2c = vec3(0, cos(iTime / 10.0), sin(iTime));
    vec3 s3c = vec3(sin(iTime), sin(iTime), 0.5);
    float r = 1.0;
    float d1 = SphereDist(s1c, 1.0, p);
    float d2 = SphereDist(s2c, 1.0, p);
    float d3 = SphereDist(s3c, 0.8, p);
    float d4 = AABBDist(vec3(-0.25, -0.25, 0.25), vec3(0.25, 0.25, 0.75), p);
    return OpSmoothUnion(OpSmoothUnion(OpSmoothUnion(d1, d2, 0.5), d3, 0.5), d4, 0.5)
        + WorleyNoise(p * 10.0, 1u) * 0.05;
}

#define f(p) SpheresUnion(p)

//https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 CalcNormal( in vec3 p ) // for function f(p)
{
    const float h = 0.0001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*f( p + k.xyy*h ) + 
                      k.yyx*f( p + k.yyx*h ) + 
                      k.yxy*f( p + k.yxy*h ) + 
                      k.xxx*f( p + k.xxx*h ) );
}

Inter RayMarch(Ray r) {
    const int maxIter = 50;
    vec3 p = r.o;
    float t = 0.0;
    float prevMinDist = 1e9;
    for (int i=0; i<maxIter; i++) {
        p = r.o + t * r.d;
        float minDist = SpheresUnion(p);
        if (minDist < 1e-2) {
            return Inter(t, CalcNormal(p), vec3(0, 1, 0));
        }
        if (minDist - prevMinDist > 10.0) {
            return Inter(1e9, vec3(0), vec3(0));
        }
        prevMinDist = minDist;
        t += minDist;
    }
    return Inter(1e9, vec3(0), vec3(0));
}

vec4 RayMarchVolume(Ray r, Inter i, vec3 alb, float absCoef) {
    const int maxIter = 30;
    vec3 p = r.o + i.t * r.d;
    float stepSize = 0.01;
    float prevMinDist = 1e9;
    vec3 col = vec3(0);
    float transmittance = 1.0;
    float prevTransmittance = 0.0;
    for (int i=0; i<maxIter; i++) {
        float t = float(i) * stepSize;
        p += t * r.d;
        float minDist = SpheresUnion(p);
        if (minDist < 0.0) {
            prevTransmittance = transmittance;
            transmittance *= exp(-absCoef * stepSize);
            float currentTr = prevTransmittance - transmittance;
            col += alb * currentTr * 3.0 * (10.0 / length(r.o - p));
        }
        if (minDist - prevMinDist > 10.0) {
            vec4(col, clamp(transmittance, 0.0, 1.0));
        }
    }
    return vec4(col, clamp(transmittance, 0.0, 1.0));
}

