// -- some helper functions for complex numbers

vec2 cdiv(vec2 z, vec2 w) {
    float v = (w.x * w.x + w.y * w.y);
    vec2 r = vec2(z.x * w.x + z.y * w.y, z.y * w.x - z.x * w.y);
    return r / v;
}

// Taken from 
// https://www.shadertoy.com/view/sdt3R2

vec2 cmul(vec2 z, vec2 w) {
    vec2 r = vec2(z.x * w.x - z.y * w.y, z.x * w.y + z.y * w.x);
    return r;
}

vec2 pol(vec2 z) {
    float size = sign(z.x) * length(z);
    float angle = angle(z);
    return vec2(length(z), angle);
}

vec2 cart(vec2 pol) {
    return pol.x * vec2(cos(pol.y), sin(pol.y));
}

vec2 cpow(vec2 z, vec2 w) {
    vec2 p1 = pol(z);
    vec2 p = vec2(pow(p1.x, w.x) * exp(-w.y * p1.y), p1.y * w.x + log(p1.x) * w.y);
    vec2 r = cart(p);
    return r;
}

vec2 cpow(vec2 z, float w) {
    return cpow(z, vec2(w, 0.0));
}

vec2 cpow(vec2 z, int w) {
    return cpow(z, float(w));
}


