#define NEWTON_ITER 20
#define MOVE_ROOTS


// P(z)
vec2 func(vec2 z) {
    return cpow(z, iTime / 4.0) + cpow(z, 2) - z + vec2(1.0, 0.0);
}

// P'(z)
vec2 funcd(vec2 z) {
    return cmul(vec2(iTime / 4.0, 0.0), cpow(z, 4)) + cmul(vec2(2.0, 0.0), z) - vec2(1.0, 0.0);
}

vec2 newton(vec2 z) {
    vec2 t = func(z);
    vec2 td = funcd(z);
    return z - cdiv(t, td);
}

