        vec2(0.0, -1.0) + 0.5 * vec2(cos(iTime * 0.55 + 11.5), sin(iTime * 0.618)),
        vec2(0.0, 1.0) + 0.5 * vec2(cos(iTime + 15.5), sin(iTime * 0.18)), 
        vec2(0.6623, 0.5622), 
        vec2(0.6623, -0.5622)
    );

#else


    // actual roots of function
    const vec2 roots[5] =  vec2[5](
        vec2(-1.324, 0.0),
        vec2(0.0, -1.0),
        vec2(0.0, 1.0),
        vec2(0.6623, 0.5622),
