vec2 sdf(vec3 p){
  p.xy *=rot(p.z*.3);
  p.y = -(abs(p.y)-1.);

  vec2 h;

  vec3 hp= p;
  h.x = dot(p,vec3(0.,1.,0.))+elev(hp)*2.0;;
