  vec3 rd = mat3(x,y,z)*normalize(vec3(uv,1.));
  rd.xy*=rot(iTime*.1);
  vec3 rp =ro;

  vec3 light = ro+vec3(1.,2.,-1.);
  float dd = 0.;
  float side = 1.;
  vec3 acc = vec3(0.);
  float sat = 0.;
  for(float i=0.;i<=128.;i++){
    vec2 d = sdf(rp);
    dd +=d.x;
    d.x *=side;
    if(dd>50.) break;
    if(d.x < .001){
      vec3 n = norm(rp,.005);
      float diff = max(0.,dot(normalize(light-rp),n));
        if(d.y == 1.){

          col = vec3(.02)*diff;
          break;
        } 
        if(d.y == 2.){
            vec3 n2 = norm(rp,.001);
          float l = length(n-n2);
            float di = step(.1,l);
            acc += vec3(di)*pal(+fract(rp.z*.1));
            sat = min(1.,sat+sqrt(di));

          side *=-1.;

          rp +=rd*.05;


          }
      }
    rp +=rd*d.x;
    }
 col +=acc;

    fragColor = vec4(sqrt(col),1.0);
}

