void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    bool hit;
	vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5,iMouse.z-.5);
    mat3 m     = rotateX ( 6.0*mouse.y ) * rotateY ( 6.0*mouse.x);
    vec2 scale = 9.0 * iResolution.xy / max ( iResolution.x, iResolution.y ) ;
    vec2 uv    = scale * ( fragCoord/iResolution.xy - vec2 ( 0.5 ) );
	vec3 dir   = normalize ( vec3 ( uv, 0 ) - eye );
    vec4 color = vec4 ( 0, 0, 0, 1 );
    vec3 p     = trace ( eye, dir, hit, m );


	if ( hit )
	{
        vec3  l  = normalize        ( light - p );
        vec3  v  = normalize        ( eye - p );
		vec3  n  = generateNormal   ( p, 0.001, m );

        //n = bump( p.xy );

		float nl = max ( 0.0, dot ( n, l ) );
        vec3  h  = normalize ( l + v );
        float hn = max ( 0.0, dot ( h, n ) );
        float sp = pow ( hn, 150.0 );

        color = cookTorrance ( p, n, l, v );

	}

    // Background
    vec2 uv1 = fragCoord/iResolution.xy;
    uv1-=vec2(0.5,1.1);
    uv1.x*=asp;
    vec2 st = uv1;
    float d = length(uv1);
    float radius = 0.5;
    float blur = 0.49;
    float c = smoothstep(radius+sin(iTime)*.1,radius-blur+sin(iTime)*.05,d);
    float dd = smoothstep(radius-0.3,radius-0.3-0.04,d);

	float n = c-dd;
    fragColor +=clamp(n,0.,1.)*1.2*vec4(0.2,0.5,0.9,1.);
    fragColor += dd*vec4(0.2,0.5,(1.-dd),1.);
    fragColor+=dd;
    fragColor +=vec4( st.y)*vec4(0.2,0.5,0.9,1.)*0.6*(1.-fragColor.b);

    // Output to screen
    color *= 1.5*textureLod(iChannel0, p.xy, 0.0);

    fragColor = max(color, fragColor);
}

/*
constructs an arc given 3 points of two intersecting lines that will be tangent to the arc. 
Assigns the arc end point to currentPoint so that it can be used to continue the contour.
clockwise order = 1
counter-clockwise order = -1
*/

float arc(vec2 position, inout vec2 currentPoint, vec2 tangent1End, vec2 tangent2End, float radius, float windingOrder) {
    position = position - tangent1End;
    vec2 a = currentPoint - tangent1End;
    vec2 b = tangent2End - tangent1End;
    float length1 = length(a);
    float length2 = length(b);
    a /= length1;
    b /= length2;
    vec2 center = (a + b) * radius / (windingOrder * (a.x * b.y - a.y * b.x));
    float s1 = dot(position - center, a);
    float s2 = dot(position - center, b);
    vec2 d1 = position - a * clamp(dot(position, a), 0.0, length1);
    length2 = dot(center, b);
    vec2 d2 = position - b * clamp(dot(position, b), 0.0, length2);
    if (s1 < 0.0 && s2 < 0.0) {
        currentPoint = tangent1End + b * length2;
        return length(position - center) - radius;
    }
    float s3 = windingOrder * (a.y * position.x - a.x * position.y);
    float s4 = windingOrder * (b.x * position.y - b.y * position.x); 
    float s = (s3 < 0.0 && s4 < 0.0) ? -1.0 : 1.0;
    currentPoint = tangent1End + b * length2;
    return sqrt(min(dot(d1, d1), dot(d2, d2))) * s;
}

/*
constructs an arc given 3 points of two intersecting lines that will be tangent to the arc. 
Assigns the arc end point to currentPoint so that it can be used to continue the contour.
assumes a clockwise winding order.
*/

float arc(vec2 position, inout vec2 currentPoint, vec2 tangent1End, vec2 tangent2End, float radius) {
    position = position - tangent1End;
    vec2 a = currentPoint - tangent1End;
    vec2 b = tangent2End - tangent1End;
    float length1 = length(a);
    float length2 = length(b);
    a /= length1;
    b /= length2;
    vec2 center = (a + b) * radius / (a.x * b.y - a.y * b.x);
    float s1 = dot(position - center, a);
    float s2 = dot(position - center, b);
    vec2 d1 = position - a * clamp(dot(position, a), 0.0, length1);
    length2 = dot(center, b);
    vec2 d2 = position - b * clamp(dot(position, b), 0.0, length2);
    if (s1 < 0.0 && s2 < 0.0) {
        currentPoint = tangent1End + b * length2;
        return length(position - center) - radius;
    }
    float s3 = a.y * position.x - a.x * position.y;
    float s4 = b.x * position.y - b.y * position.x; 
    float s = (s3 < 0.0 && s4 < 0.0) ? -1.0 : 1.0;
    currentPoint = tangent1End + b * length2;
    return sqrt(min(dot(d1, d1), dot(d2, d2))) * s;
}

/* 
constructs a straight line segment given two points.
assumes a clockwise winding order.
*/

float line(vec2 position, inout vec2 currentPoint, vec2 end) {
    position = position - currentPoint;
    end = end - currentPoint;
    float height = clamp(dot(position, end) / dot(end, end), 0.0, 1.0);
    float d = length(position - end * height);
    float s = sign(end.x * position.y - end.y * position.x); 
    currentPoint = end + currentPoint;
    return d * s;
}

// example
float trapezoid(vec2 position, float halfWidth1, float halfWidth2, float halfHeight, float cornerRadius) {
    position.x = abs(position.x);
    vec2 currentPoint = vec2(0.0, halfHeight);
    float d1 = arc(position, currentPoint, vec2(halfWidth2, halfHeight), vec2(halfWidth1, -halfHeight), cornerRadius);
    float d2 = line(position, currentPoint, vec2(halfWidth1, -halfHeight));
    float d3 = line(position, currentPoint, vec2(0.0, -halfHeight));
    return min(min(abs(d1), abs(d2)), abs(d3)) * sign(max(max(d1, d2), d3));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = fragCoord / iResolution.xy;
    position = (position * 2.0) - 1.0;
    position.x *= iResolution.x / iResolution.y;

    vec2 currentPoint = vec2(0.0, -0.5);
    vec2 tangentEnd1 = vec2(0.0, 0.5);
    float angle = (sin(iTime + 1.0) + 1.0) * 0.5 * 3.14159;
    vec2 tangentEnd2 = tangentEnd1 + 2.0 * vec2(cos(angle), sin(angle));
    tangentEnd2 = tangentEnd1 + vec2(cos(angle), sin(angle));
    float radius = 0.5;
    float windingOrder = sign(tangentEnd2.x);
    float d = arc(position, currentPoint, tangentEnd1, tangentEnd2, radius, windingOrder);
    d *= windingOrder;

    // d = trapezoid(position, 0.65, 0.35, 0.5, 0.25); // example 

    // same colorization that Inigo Quilez uses in his shaders
    vec3 color = vec3(1.0) - sign(d) * vec3(0.1, 0.4, 0.7); // base color
    color *= 1.0 - exp(-4.0 * abs(d)); // gradient
	color *= 0.8 + 0.2 * cos(120.0 * d); // ripples
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.015, abs(d)));
    if (iMouse.z > 0.001) {
        vec2 mousePosition = iMouse.xy / iResolution.xy;
        mousePosition = (mousePosition * 2.0) - 1.0;
        mousePosition.x *= iResolution.x / iResolution.y;
        vec2 currentPoint = vec2(0.0, -0.5);
        float d = arc(mousePosition, currentPoint, tangentEnd1, tangentEnd2, radius, windingOrder);
        color = mix(color, vec3(1.0, 1.0, 0.0), 1.0 - smoothstep(0.0, 0.0025, abs(length(position - mousePosition) - abs(d)) - 0.0025));
    }
    fragColor = vec4(color, 1.0);
}

float smin( float a, float b, float k ){
    float h = max(k-abs(a-b),0.0);
    return min(a, b) - h*h*0.25/k;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 total = vec3(0);

    float v = 1./3.;
    vec2 AAoffsets[4] = vec2[](
        vec2(v,v),
        vec2(v,-v),
        vec2(-v,v),
        vec2(-v,-v)
     );


    for(int i = 0; i < AAoffsets.length(); i++){
        vec2 uv =  (2. * (fragCoord + AAoffsets[i]) - iResolution.xy) / iResolution.xy;


        uv.y /= iResolution.x / iResolution.y;

        vec2 P = vec2(0,0);
        vec2 R = vec2(0.2,0.1);

        float t = fract(iTime);
        t = t * t * t * (t * (t * 6. - 15.) + 10.);

        uv.x += 
            float(int(iTime) & 1) * (t*2.5f - 1.25) +
            float(int(iTime)+1 & 1) * ((1.-t)*2.5f - 1.25);

        uv.y += sin(iTime*10.)/3.5;

        float d = length(max(abs(uv)-R, 0.));

        total += vec3(fract(d*15.));
    }

    total /= float(AAoffsets.length());

    fragColor = vec4(total, 1.0);  
}

#define TAU 6.283185307
#define sint(x) (sin((x) * TAU))

float stairs(float x, float max_) {
    return min(1.0, fract(x) / max_);
}

float stripes(float x, float thres) {
    return step(fract(x), thres);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime / 5.);

    // normalizes coords so that center is (0,0) and diagonals are length 1
    vec2 uv = (fragCoord / length(iResolution.xy)
	- (iResolution.xy / length(iResolution.xy) / 2.)) * 2.;
    float dist = 0.8 * log(uv.x * uv.x + uv.y * uv.y);
    float angle = atan(uv.y, uv.x) / TAU;

    float dist_ = stairs(t + 2. * sint(t) + dist, 0.6) - dist;
    float spiral = stripes(2. * t + 1.3 * dist_ + angle * 3., 0.4 + 0.3 * sint(t + 0.25));
    float circle = stripes(3. * t + 0.3 * (dist + sint(dist)), 0.4);

    vec3 col_base = vec3(79, 71, 137) / 255.;
    vec3 col_stripe = vec3(32, 19, 53) / 255.;
    vec3 col_bolt = vec3(252, 231, 98) / 255.;

    vec3 col = mix(
        mix(
            col_base,
            col_stripe,
            circle
        ),
        col_bolt,
        spiral
    );
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define _LinesWidth (0.8) //越小越宽
#define _Amount (0.3) //控制线条数量，1就无了
#define _Offset (0.9) //控制线条的强度
#define _Alpha (0.9) //混合比例

float randomNoise(vec2 seed)
{
    return fract(sin(dot(seed, vec2(1.9898, 7.233))) * 43758.5453123);
}

float trunc(float x, float num_levels)
{
    return floor(x * num_levels) / num_levels;
}

vec2 trunc(vec2 x, vec2 num_levels)
{
    return floor(x * num_levels) / num_levels;
}

vec3 rgb2yuv(vec3 rgb)
{
    vec3 yuv;
    yuv.x = dot(rgb, vec3(0.299, 0.587, 0.114));
    yuv.y = dot(rgb, vec3(-0.14713, -0.28886, 0.436));
    yuv.z = dot(rgb, vec3(0.615, -0.51499, -0.10001));
    return yuv;
}

vec3 yuv2rgb(vec3 yuv)
{
    vec3 rgb;
    rgb.r = yuv.x + yuv.z * 1.13983;
    rgb.g = yuv.x + dot(vec2(-0.39465, -0.58060), yuv.yz);
    rgb.b = yuv.x + yuv.y * 2.03211;
    return rgb;
}

vec3 LineBlockGlitch(vec2 uv)
{
    float truncTime = trunc(iTime, 4.0);
    float uv_trunc = randomNoise(trunc(uv.yy, vec2(8., 8.)) + vec2(100.0 * truncTime));
    //return vec3(uv_trunc); ------------------------------1

    float uv_randomTrunc = 6. * trunc(iTime, 24. * uv_trunc);
    //return vec3(uv_randomTrunc); ------------------------2

    float blockLine_random = 0.5 * randomNoise(trunc(uv.yy + uv_randomTrunc, vec2(8. * _LinesWidth, 8. * _LinesWidth)));
    blockLine_random += 0.5 * randomNoise(trunc(uv.yy + uv_randomTrunc, vec2(7., 7.)));
    blockLine_random = blockLine_random * 2.0 - 1.0;	
    blockLine_random = sign(blockLine_random) * clamp((abs(blockLine_random) - _Amount) / (0.4), 0., 1.);
    blockLine_random = mix(0., blockLine_random, _Offset);
    //return vec3(blockLine_random);-----------------------3

    vec2 uv_blockLine = uv;
    uv_blockLine = clamp(uv_blockLine + vec2(0.1 * blockLine_random, 0.), 0., 1.);

    vec3 col = texture(iChannel0, abs(uv_blockLine)).xyz;
    //return col; -----------------------------------------4

    col = rgb2yuv(col);
    col.y /= 2. - 3. * abs(blockLine_random) * clamp(0.5 -  blockLine_random, 0., 1.); //Chrominance
    col.z += 3.25 *  blockLine_random * clamp( blockLine_random - .5, 0., 1.);  //Chroma
    col = yuv2rgb(col);

    vec3 ori_col = texture(iChannel0, uv).xyz;
    return mix(ori_col, col, vec3(_Alpha));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = LineBlockGlitch(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define TAU (6.283185307)

// A single iteration of Bob Jenkins' One-At-A-Time hashing algorithm.
uint hash(uint x) {
    x &= 65535u;
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x & 65535u;
}
// fade function defined by ken perlin
vec2 fade(vec2 t) {
  return t * t * t * (t * (t * 6. - 15.) + 10.);
}
// corner vector
vec2 cvec(vec2 uv, float time) {
  uint x = uint(mod(uv.x, 256.));
  uint y = uint(mod(uv.y, 256.));
  float n = (float(hash(x + hash(y))) / 65535. + time) * TAU;
  return vec2(
      sin(n), cos(n)
  );
}
// perlin generator
float perlin(vec2 uv, float offset) {
  vec2 i = floor(uv);
  vec2 f = fract(uv);

  vec2 u = fade(f);
  offset = fract(offset);

  return
  mix(
    mix(
      dot( cvec(i + vec2(0.0,0.0), offset ), f - vec2(0.0,0.0) ),
      dot( cvec(i + vec2(1.0,0.0), offset ), f - vec2(1.0,0.0) ),
    u.x),
    mix(
      dot( cvec(i + vec2(0.0,1.0), offset ), f - vec2(0.0,1.0) ),
      dot( cvec(i + vec2(1.0,1.0), offset ), f - vec2(1.0,1.0) ),
    u.x),
  u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float time = fract(iTime / 2.5);
  // Normalized pixel coordinates (from 0 to 1)
  float scale = min(iResolution.x, iResolution.y);
  vec2 uv = (gl_FragCoord.xy - 0.50 * iResolution.xy) / scale;
  float dist = log(uv.x * uv.x + uv.y * uv.y);
  float angle = atan(uv.y, uv.x) / TAU;

  float noise = perlin(uv * 10.5, time);
  noise += 0.5  * perlin(uv * 34.0, -2. * time);
  noise *= max(0.0, dist * 0.2 + 2.);

  vec3 colBase = vec3(88, 125, 113) / 255.;
  vec3 colSp1 = vec3(181, 221, 164) / 255.;
  vec3 colSp2 = vec3(117, 70, 104) / 255.;

  float spiral1 = step(fract(0.2 * noise + 2. * angle + 0.5 * dist + time), 0.4);
  float spiral2 = step(fract(0.2 * noise + 2. * angle + 0.5 * dist + 2. * time + 0.5), 0.2);
  vec3 col = mix(
      mix(
          colBase, colSp1, spiral1
      ), colSp2, spiral2
  );
  // Output to screen
  fragColor = vec4(
    col, 1.0
  );
}

float hash11(float p) {

    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

vec2 hash( vec2 p ) // replace this by something better
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;

    float te = 0.0;

    for( int i = -5; i < 35; ++i ) {

        float height = float(i)/40.0;

        vec2  auv = vec2( uv.x+height*1.7, height*1.8 ); 
        float s = height + noise((auv + iTime*0.032)*5.0) * 0.02 + noise(auv + iTime*0.05) * 0.02;
        float d = (uv.y - s) * 100.0;

        float e = (10.3*d / ( 0.2 + abs(d) ) + d) * pow(4.0, -abs(d)) * 0.1;
        te += e;
    }

    vec3 c = vec3( 0.64, 0.65, 0.6 ) + te;
    fragColor += vec4(c,1.0);
}

/*
Mostly a rip off of https://iquilezles.org/www/articles/fbmsdf/fbmsdf.htm
*/
float box3(vec3 p,vec3 b){
    vec3 q = abs(p)-b;
     return length(max(vec3(0.),q))+min(0.,max(q.x,max(q.z,q.y)));
}
mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}

float smin(float a, float b, float k)
{
    float h=clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
    return mix(b, a, h)-k*h*(1.0-h);
}

float smax( float a, float b, float k)
{
    return -smin(-a,-b,k);
}

float qqq(vec3 p){
    vec3 id = floor(p);
    p = fract(p)-.5;
    float h = length(p)-(.25-.15*fract(10.*sin(100.*id.x*id.y-id.z*100.)) );

    return h;
}

vec3 op;
// Iq sdFbm
float sdFbm( vec3 p, float d )
{
   float s = 1.0;
   for( int i=0; i<11; i++ )
   {
       // evaluate new octave
       float n = s*qqq(p);
      //p.xy*=rot(-.785*float(i)+float(i));
       // add
       n = smax(n,d-0.1*s,0.3*s);
       d = smin(n,d      ,0.3*s);

       // prepare next octave
       p = mat3( 0.00, 1.60, 1.20,
                -1.80, 0.72,-0.96,
                -1.20,-1.96, 1.28 )*p;
       s = 0.5*s;
   }
   return d;
}
vec3 tp;
vec2 sdf(vec3 p){
   tp =p;
   p.z +=iTime*.01;
   op =p ;
    vec2 h;
    h.x = dot(p,vec3(0.,1.,0.))+.2;

    float q = sdFbm(p,h.x); 
