//Voxel map (approximate SDF)
float map(vec3 pos)
{
    return length(mod(pos,18.0)-9.0) - 9.5+sin(pos.z*0.3-iTime*0.1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 res = iResolution.xy;   //View resolution

    vec3 cam = vec3(sin(iTime*0.2+res), iTime),         //Camera position
    pos = cam,                                          //Pixel position
    ray = normalize(vec3(fragCoord*2.0 - res, res.y)),  //Ray direction
    cell = vec3(0,0,0);                                 //Voxel cell

    //Step up to 100 voxels.
    for(int i = 0; i<100; i++)
    {
        //Axis distance to nearest cell (with a small bias).
        vec3 dist = fract(-pos * sign(ray)) + 1e-4,
        //Alternative version (produces artifacts after a while)
        //vec3 dist = 1.0-fract(pos * sign(ray)),
        //Raytraced distance to each axis.
        leng = dist / abs(ray),
        //Nearest axis' raytrace distance (as a vec3).
        near = min(leng.xxx, min(leng.yyy, leng.zzz));

        //Step to the nearest voxel cell.
        pos += ray * near;
        //Get the cell position (center of the voxel).
        cell = ceil(pos) - 0.5;
        //Stop if we hit a voxel.
        if (map(cell) < 0.0) break;
    }

    //Rainbow color based off the voxel cell position.
    vec3 color = sin(cell.z+vec3(0,2,4)) * 0.5 + 0.5;
    //Square for gamma encoding.
    color *= color;

    //Compute cheap ambient occlusion from the SDF.
    float ao = smoothstep(-1.0, 1.0, map(pos)),
    //Fade out to black using the distance.
    fog = min(1.0, exp(1.0 - length(pos-cam)/8.0));

    //Output final color with ao and fog (sqrt for gamma correction).
    fragColor = vec4(sqrt(color * ao * fog),1);
}

#define UV_ORIGIN 0.5
#define LINE_THICKNESS 0.0025

const vec3 red = vec3(1., 0., 0.);
const vec3 green = vec3(0., 1., 0.);
const vec3 blue = vec3(0., 0., 1.);

float df_line(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p - a, ba = b - a;
	float h = clamp(dot(pa,ba) / dot(ba,ba), 0.0, 1.0);	
    float edge = 1.0/min(iResolution.x, iResolution.y);
    return smoothstep(edge, -edge, length(pa - ba * h) - LINE_THICKNESS);
}

float drawCircle(vec2 position, float radius)
{
    float smaller = min(iResolution.x, iResolution.y);
    float edge = 1.0/smaller;
    return smoothstep(edge, -edge, length(position) - radius);
}

vec2 FindNearestPointOnLine(vec2 origin, vec2 end, vec2 point)
{
    //Get heading
    vec2 heading = (end - origin);
    float magnitudeMax = length(heading);
    heading = normalize(heading);

    //Do projection from the point but clamp it
    vec2 lhs = point - origin;
    float dotP = dot(lhs, heading);
    dotP = clamp(dotP, 0.0f, magnitudeMax);
    return origin + heading * dotP;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);
    // Basic setup stuff
    float smaller = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord-UV_ORIGIN*iResolution.xy)/smaller;

    // Define some points to draw a white line
    vec2 pointA = vec2(-0.35, 0.0);
    vec2 pointB = vec2( 0.35, 0.0);

    vec2 greenCircle = vec2( 0.0, sin(iTime) * 0.1);
    float greenRadius = 0.05;

    vec2 blueCircle = (iMouse.xy-UV_ORIGIN*iResolution.xy)/min(iResolution.x, iResolution.y);
    float blueRadius = 0.025;

    vec2 redCircle = FindNearestPointOnLine(pointA, pointB, blueCircle);
    float redRadius = 0.05;

    vec2 pureCircle = redCircle;

    // If red circle is inside green circle
    if( distance(redCircle, greenCircle) < greenRadius + redRadius )
    {
        vec2 closestFromGreen = FindNearestPointOnLine(pointA, pointB, greenCircle);
        float aLength = distance(greenCircle, closestFromGreen);
        float cLength = redRadius + greenRadius;
        float bLength = sqrt(pow(cLength, 2.0) - pow(aLength, 2.0));
        redCircle = closestFromGreen + (normalize(redCircle - closestFromGreen) * bLength);
    }

    // Draw all the stuff, basically. None of this has anything to do with the math involved in finding these points.
    col += df_line(uv, pointA, pointB);
    col += drawCircle(uv - greenCircle, greenRadius) * green;
    col += drawCircle(uv - blueCircle, blueRadius) * blue;
    col += drawCircle(uv - redCircle, redRadius) * red;
    col = sqrt(col);
    fragColor = vec4(col,1.0);
}

