// revised affine form of rank 1
struct raf1 {
    float x0;
    float x1;
    float e;
};

struct raf1x3 {
    raf1 x;
    raf1 y;
    raf1 z;
};

raf1 ra_const(float x) {
    return raf1(x, 0.0, 0.0);
}
float ra_radius(raf1 a) {
    return abs(a.x1) + a.e;
}
vec2 ra_interval(raf1 a) {
    float r = ra_radius(a);
    return vec2(a.x0 - r, a.x0 + r);
}

raf1 ra_add(raf1 a, raf1 b) {
	return raf1(a.x0 + b.x0, a.x1 + b.x1, a.e + b.e);
}
raf1 ra_add(raf1 a, float b) {
	return raf1(a.x0 + b, a.x1, a.e);
}
raf1 ra_add(float a, raf1 b) {
	return raf1(a + b.x0, b.x1, b.e);
}
raf1x3 ra_add(raf1x3 a, vec3 b) {
	return raf1x3(ra_add(a.x,b.x),ra_add(a.y,b.y),ra_add(a.z,b.z));
}

raf1 ra_sub(raf1 a, raf1 b) {
	return raf1(a.x0 - b.x0, a.x1 - b.x1, a.e + b.e);
}
raf1 ra_sub(raf1 a, float b) {
	return raf1(a.x0 - b, a.x1, a.e);
}
raf1 ra_sub(float a, raf1 b) {
	return raf1(a - b.x0, -b.x1, b.e);
}
raf1x3 ra_sub(raf1x3 a, vec3 b) {
	return raf1x3(ra_sub(a.x,b.x),ra_sub(a.y,b.y),ra_sub(a.z,b.z));
}

raf1 ra_neg(raf1 a) {
	return raf1(-a.x0, -a.x1, a.e);
}

raf1 ra_mul(raf1 a, raf1 b) {
    float s = abs(a.x1);
    float t = abs(b.x1);
    float w = dot(a.x1, b.x1);
    float u = s;
    float v = t;
    return raf1(
        a.x0 * b.x0 + 0.5 * w,
        a.x0 * b.x1 + a.x1 * b.x0,
        a.e * b.e
        + b.e * (abs(a.x0) + u)
        + a.e * (abs(b.x0) + v)
        + u * v
        - 0.5 * dot(s, t));
}
raf1 ra_mul(raf1 a, float b) {
    return raf1(
        a.x0 * b,
        a.x1 * b,
        a.e * abs(b));
}
raf1 ra_mul(float a, raf1 b) {
    return ra_mul(b, a);
}
raf1x3 ra_mul(raf1 a, vec3 b) {
	return raf1x3(ra_mul(a,b.x),ra_mul(a,b.y),ra_mul(a,b.z));
}

raf1 ra_rcp(raf1 a) {
    vec2 i = ra_interval(a);
    float i0i1 = i[0]*i[1];
    if (i0i1 < 0.0) {
        return raf1(1.0/a.x0, 0.0, 1.0/0.0);
    } else {
        vec2 ab = 1.0 / i;
        float h = sign(i[0]) / sqrt(i0i1);
        float c = (ab[0]+ab[1]) * 0.5;
        float nalpha = ab[0] * ab[1];
        float alpha = -nalpha;
        float zeta = c + h;
        float delta = abs(c-h);
        return raf1(
            alpha * a.x0 + zeta,
            alpha * a.x1,
            nalpha * a.e + delta);
    }
}

raf1 ra_div(raf1 a, raf1 b) {
    return ra_mul(a, ra_rcp(b));
}

raf1 ra_pow2(raf1 a) {
    float w = 0.5 * a.x1 * a.x1;
    return raf1(
        a.x0 * a.x0 + w,
        2.0 * a.x0 * a.x1,
        a.e * (1.0 + 2.0 * (abs(a.x0) + abs(a.x1))) + w);
}

raf1 ra_dot(raf1x3 a, raf1x3 b) {
    return ra_add(ra_add(ra_mul(a.x, b.x),ra_mul(a.y, b.y)),ra_mul(a.z, b.z));
}

raf1 ra_dot(raf1x3 a, vec3 b) {
    return ra_add(ra_add(ra_mul(a.x, b.x),ra_mul(a.y, b.y)),ra_mul(a.z, b.z));
}

raf1 ra_dot(raf1x3 a) {
    return ra_add(ra_add(ra_pow2(a.x),ra_pow2(a.y)),ra_pow2(a.z));
}

raf1 ra_sqrt(raf1 x) {
    vec2 i = ra_interval(x);
    if (i[1] < 0.0) return ra_const(0.0);
    i[0] = max(i[0], 0.0);
    vec2 sq = sqrt(i);
    float c = sq[1] + sq[0];
    float h = sq[1] - sq[0];
    float alpha = 1.0 / c;
    float dzeta = c / 8.0 + 0.5 * sq[0] * sq[1] / c;
    float delta = h * h / (8.0 * c);
    return raf1(
        alpha * x.x0 + dzeta,
        alpha * x.x1,
        alpha * x.e + delta);
}

raf1 ra_length(raf1x3 a) {
    return ra_sqrt(ra_dot(a));
}

raf1 ra_abs (raf1 a) {
    vec2 i = ra_interval(a);
    if (i[0]*i[1] >= 0.0) {
        return raf1(abs(a.x0), a.x1 * sign(a.x0), a.e);
    } else {
    #if 0
        // keep ambiguous case in a box
        vec2 j = abs(i);
        i = (i[0]>=0.0)?j:(i[1]<0.0)?j.yx:vec2(0.0,max(j[0],j[1]));
        return raf1((i[0]+i[1])*0.5, 0.0, (i[1]-i[0])*0.5);
    #else
        vec2 ab = abs(i);
        float alpha = (ab[1] - ab[0]) / (i[1] - i[0]);
        float zeta = (ab[0] - i[0] * alpha) * 0.5;
        float delta = zeta;

        return raf1(
            alpha * a.x0 + zeta,
            alpha * a.x1,
            abs(alpha) * a.e + delta);
	#endif
    }
}

// crude approximation for min/max
// there are more opportunities for truncation here, as only
// the overlapping parallelogram and either one or both top parts (max)
// or bottom parts (min) of each argument need to be bounded.
// e.g. if all minimum values of a are above the minimum values of b,
// regardless of any overlapping, only a needs to be considered for max(a,b).

raf1 ra_max(raf1 a, raf1 b) {
    vec2 ia = ra_interval(a);
    vec2 ib = ra_interval(b);
    if (ia[0] >= ib[1])
        return a;
    else if (ib[0] >= ia[1])
        return b;
    else {
	    return ra_mul(ra_add(ra_add(a,b),ra_abs(ra_sub(a, b))),0.5);
    }
}

raf1 ra_min(raf1 a, raf1 b) {
    vec2 ia = ra_interval(a);
    vec2 ib = ra_interval(b);
    if (ia[1] <= ib[0])
        return a;
    else if (ib[1] <= ia[0])
        return b;
    else {
	    return ra_mul(ra_sub(ra_add(a,b),ra_abs(ra_sub(a, b))),0.5);
    }
}

raf1 ra_zero(raf1 x, raf1 y) {
    float dxdy = x.x1 / y.x1;
    return raf1(x.x0 - dxdy * y.x0, dxdy * y.e, 0.0);
}

vec2 get_range(uint c, uint level) {
#if 1
    float r = exp2( -float(level) );
    const float k = exp2( -float(MAX_LEVEL) );
    float center = (float(c)*k) * 2.0 - 1.0 + r;
#else
    float r = 1.0 / float(1u << level);
    float center = (float(c) / float(1u << MAX_LEVEL)) * 2.0 - 1.0 + r;
#endif
    center += 1.0;
    return vec2(center, r);
}

uint find_lsb(uint value) {
#if 1
    // findLSB() hack
    return uint(log2(float(value & -value)));
#else
    // with GL4, this loop can be replaced with
    // a single use of findLSB()
    uint count = 0u;
    for (uint i = 0u; i < MAX_LEVEL; ++i) {
        if ((value & 1u) != 0u)
            break;
        count += 1u;
        value >>= 1u;
    }
    return count;
#endif
}

uint lowest_level(uint p) {
    uint used_levels = min(MAX_LEVEL, find_lsb(p));
    return MAX_LEVEL - used_levels;
}


