float calcIntersectionRA( in vec3 ro, in vec3 rd, vec2 pixel, float bias, out int steps )
{
    float threshold = get_range(0u, MAX_LEVEL).y;        
    uint p = 0u;
    uint level = 0u;
    float maxd = 10.0;
    for (int i = 0; i < MAX_STEPS; ++i) {
        steps = i;
        const uint topstep = 1u << MAX_LEVEL;
        if (p >= topstep)
            break;
        vec2 rc = get_range(p, level);
        raf1 t = raf1(rc.x, rc.y, 0.0);
        raf1 d = ra_map(ra_add(ra_mul(t, rd * maxd), ro));
        uint stepsz = topstep >> level;
        // visualize interval
        vec2 iv = ra_interval(d);
        // interval crosses zero?
        bool hit = (iv.x*iv.y < 0.0);
        if (hit) {
            // if we have prematurely reached our
            // desired precision, exit early                
            if ((d.e <= threshold) || (level == MAX_LEVEL)) {
                vec2 pr = ra_interval(ra_zero(raf1(rc.x,rc.y,0.0), d));
                return maxd * (pr[0]+pr[1])/2.0;
            }
            // reached maximum precision
            if (level == MAX_LEVEL)
                break;
        }
        if (hit && (level < MAX_LEVEL)) {
            level += 1u;
        } else {
            p += stepsz;
            level = lowest_level(p);
        }
    }
    return -1.0;
}


mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

