#define pi 3.1415926535
#define STEPS 500.0
#define MDIST 200.0
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pmod(p,x) (mod(p,x)-0.5*(x))
float h21(vec2 a){
    return fract(sin(dot(a,vec2(453.2734,255.4363)))*994.3434);
}
vec3 rdg;
float box(vec3 p, vec3 b){
    vec3 d= abs(p)-b;
    return max(d.x,max(d.y,d.z));
}
vec3 map(vec3 p){
    vec3 po = p;
    vec2 a = vec2(1);
    //vec2 b = vec2(2);
    float t =iTime;
    p.x+=t;

    float m = 2.; //Adjust the sphere size here :)

    vec2 id3 = floor(p.xz/m)+0.5;
    p.x+=id3.y;
    vec2 id2 = floor(p.xz/m)+0.5;
    float hash =h21(mod(id2,100.0));
    p.y+=(hash-0.5);
    id2*=rot(-pi/6.0);
    p.y+=sin(id2.x+t)*0.7;
    p.y+=sin(id2.x*0.6+t)*0.4;
    p.y+=sin(id2.x*0.3+t)*0.2;
    id2*=rot(pi/6.0)*0.3;
    p.y+=sin(id2.y+t)*0.7;
    po = p;
    float dc = 0.;
    {
        vec3 p2=p/vec3(m);
        vec3 id = floor(p2);
        vec3 dir = sign(rdg)*.5;
        vec3 q = fract(p2)-.5;
        vec3 rc = (dir-q)/rdg;
        rc*=m;
        dc = min(rc.x,min(rc.y,rc.z))+0.01;
    }
    p.xz = pmod(p.xz,m);
    t+=hash*200.;
    //t*=-1.;
    //////////////////////////////////
    //////////////////////////////////
    float spd = .025;
    t*=spd;
    float lscl = m;
    float le = -mod(t * lscl,lscl); 
    float tscl = 650.; 
    float te = tscl - mod(t * tscl,tscl); 
    float scl = 0.; 
    float id = 0.;
    float npy = 0.;
    bool mid = false;
        if(p.y > le && p.y < te){ 
            npy = mod(p.y-le,tscl);
            scl = mix(tscl,lscl,min(fract(t)*2.0,1.0));
            mid = true;
            id = floor(t);
        }
        if(p.y<le){ 
            npy = mod(p.y-le,lscl);
            id = floor((p.y-le)/lscl)+floor(t);
            scl = lscl;
        }
        if(p.y>te){ 
            npy = mod(p.y-te,tscl);           
            id = floor((p.y-te)/tscl)+floor(t)+1.0; 
            scl = tscl;
            //mid = true;
        }
        npy-=scl*0.5;
        p.y = npy;
    //////////////////////////////////
    //////////////////////////////////

    a.x = length(p)-m*0.98*0.5;

    a.x = max(-po.z-16.,a.x);
    float c = a.x;
    a.x = min(a.x,dc);

    if(mid)a.x = min(a.x,max(-(-po.y+le),0.1));
    a.y = id;
    return vec3(a,c);
}
vec3 norm(vec3 p){
    vec2 e = vec2(0.01,0.);
    return normalize(map(p).x-vec3(
    map(p-e.xyy).x,
    map(p-e.yxy).x,
    map(p-e.yyx).x));
}
vec3 light(vec3 p, vec3 rd){
    vec3 col = vec3(0);
    vec3 ld = normalize(vec3(1,1,-1));
    vec3 n = norm(p);
    float fres = 1.-abs(dot(rd,n))*.6;
    col = vec3(clamp(fres,0.,1.));

    float sss = 0.6;
        for(float i=1.; i<40.; ++i){
        float dist = i*0.1;
        sss += 0.04*smoothstep(0.,1.,map(p+ld*dist).z/dist);
    }
    col*=sss;

    vec3 p2 = p;
    p2.xz*=rot(0.1+cos(iTime*0.2)*0.3);
    col*=mix(vec3(0.310,0.471,0.953),vec3(0.106,0.137,0.553),sin(p2.z+p2.x)*0.5+0.5);
    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(1,10,-25);
    vec3 lk = vec3(1.01,2,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = normalize(f*0.95+uv.x*r+uv.y*cross(f,r));
    rdg=rd;
    vec3 p = vec3(0);
    vec3 d = vec3(0);
    float dO = 0.;
    bool hit = false;
    for(float i =0.; i<STEPS; i++){
        p = ro+rd*dO;
        d = map(p);
        dO += d.x*1.25;
        if(abs(d.x)<0.005){
            hit =true;
            break;
        }
        if(dO>MDIST){
            dO=MDIST;
            p = ro+rd*dO;
            break;
        }   
    }
    if(hit){
        col = light(p,rd);
    }

    //Fog
    col*=clamp(p.y*0.15+1.0,-1.,1.);
    col = pow(col,vec3(0.6));;
    vec3 bg = mix(vec3(0.000,0.12,0.400),vec3(0.439,0.784,1.000),max(rd.y+0.6,0.));
    col = mix(col,bg,clamp(length(p-ro*0.5)/60.,0.,1.));

    fragColor = vec4(col,0.0);
}

const vec3 eye = vec3(0,0,10);
const vec3 light = vec3(0.0,5.0,10.0);
const int maxSteps = 100;
const float eps = 0.01;


float smin (float a, float b, float k)
{
    float res = exp(-k*a)+exp(-k*b);
    return -log(res)/k;
}

float dCone ( vec3 p, vec2 c )
{
    float q = length( p.yz );
    return dot ( c, vec2 ( q, p.x ) );
}

float dCyl ( vec3 p, vec3 c )
{
return length ( p.xz - c.xy ) - c.z+0.03* cos(5.0*p.y + iTime)+0.02 * sin(20.0*p.z + iTime) ;
}

float dSphere (vec3 p, vec3 c)
{
    return length(p - c) - 1.0 +0.03 * sin(20.0*p.y + iTime);
}

float dEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float length8 (in vec2 p)
{
    return pow(pow(p.x,8.0) + pow (p.y,8.0),1.0/8.0);
}


float length8 ( in vec3 p )
{
    return pow ( pow ( p.x, 8.0 ) + pow ( p.y, 8.0 ) + pow ( p.z, 8.0 ), 1.0/ 8.0 );
}


float dTorus (vec3 p, vec3 t)
{
    vec2  q=vec2(length8 (p.xz) - t.x,p.y);
    return length8(q) - t.y;
}

mat3 rotateX(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat3(vec3(1,0,0),
            vec3(0,c,-s),
            vec3(0,s,c));
}

mat3 rotateZ(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat3(vec3(c,-s,0),
            vec3(s,c,0),
            vec3(0,0,1));
}

mat3 rotateY(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat3(vec3(c,0,s),
            vec3(0,1,0),
            vec3(-s,0,c));
}

float sdf (in vec3 p,in mat3 m)
{
    vec3 q = m*p;
    vec3 o = q*rotateX(90.0);

    return smin(smin(dEllipsoid(q,vec3(1,5,1)),dCyl(rotateX(-90.0)*q,vec3(0,0,1)),7.0),smin(dSphere(q,vec3(0,5,0)),dSphere(q,vec3(0,-5,0)),8.0),9.0);
}  
vec3 generateNormal (vec3 z, float d,in mat3 m)
{
    float e  = max(d * 0.5,eps);
    float dx1 = sdf(z+ vec3(e,0,0),m);
    float dx2 = sdf(z- vec3(e,0,0),m);
    float dy1 = sdf(z+ vec3(0,e,0),m);
    float dy2 = sdf(z- vec3(0,e,0),m);
    float dz1 = sdf(z+ vec3(0,0,e),m);
    float dz2 = sdf(z- vec3(0,0,e),m);

    return normalize (vec3 (dx1 - dx2,dy1-dy2,dz1-dz2));
}



const float roughness = 0.4;
const vec3  r0   = vec3 ( 1.0, 0.92, 0.23 );
const vec3  clr  = vec3 ( 0.8, 0.4, 0.0 );
const float gamma = 2.2;
const float pi    = 3.1415926;
const float FDiel = 0.04;		// Fresnel for dielectrics

vec3 fresnel ( in vec3 f0, in float product )
{
	product = clamp ( product, 0.0, 1.0 );		// saturate

	return mix ( f0, vec3 (1.0), pow(1.0 - product, 5.0) );
}

float D_blinn(in float roughness, in float NdH)
{
    float m = roughness * roughness;
    float m2 = m * m;
    float n = 2.0 / m2 - 2.0;
    return (n + 2.0) / (2.0 * pi) * pow(NdH, n);
}

float D_beckmann ( in float roughness, in float NdH )
{
	float m    = roughness * roughness;
	float m2   = m * m;
	float NdH2 = NdH * NdH;

	return exp( (NdH2 - 1.0) / (m2 * NdH2) ) / (pi * m2 * NdH2 * NdH2);
}

float D_GGX ( in float roughness, in float NdH )
{
	float m  = roughness * roughness;
	float m2 = m * m;
	float NdH2 = NdH * NdH;
	float d  = (m2 - 1.0) * NdH2 + 1.0;

	return m2 / (pi * d * d);
}

float G_schlick ( in float roughness, in float nv, in float nl )
{
    float k = roughness * roughness * 0.5;
    float V = nv * (1.0 - k) + k;
    float L = nl * (1.0 - k) + k;

    return 0.25 / (V * L);
}

float G_neumann ( in float nl, in float nv )
{
	return nl * nv / max ( nl, nv );
}

float G_klemen ( in float nl, in float nv, in float vh )
{
	return nl * nv / (vh * vh );
}

float G_default ( in float nl, in float nh, in float nv, in float vh )
{
	return min ( 1.0, min ( 2.0*nh*nv/vh, 2.0*nh*nl/vh ) );
}

vec4 cookTorrance ( in vec3 p, in vec3 n, in vec3 l, in vec3 v )
{
    vec3  h    = normalize ( l + v );
	float nh   = dot (n, h);
	float nv   = dot (n, v);
	float nl   = dot (n, l);
	float vh   = dot (v, h);
    float metallness = 1.0;
    vec3  base  = pow ( clr, vec3 ( gamma ) );
    vec3  F0    = mix ( vec3(FDiel), clr, metallness );

			// compute Beckman
   	float d = D_beckmann ( roughness, nh );

            // compute Fresnel
    vec3 f = fresnel ( F0, nv );

            // default G
    float g = G_default ( nl, nh, nv, vh );

			// resulting color
	vec3  ct   = f*(0.25 * d * g / nv);
	vec3  diff = max(nl, 0.0) * ( vec3 ( 1.0 ) - f ) / pi;
	float ks   = 0.5;

	return vec4 ( pow ( diff * base + ks * ct, vec3 ( 1.0 / gamma ) ), 1.0 );
}


vec3 trace(in vec3 from, in vec3 dir, out bool hit, in mat3 m)
{
    vec3 p = from;
    float totalDist = 0.0;

    hit =false;

    for (int steps = 0; steps < maxSteps; steps++)
    {
        float dist = sdf(p,m);

        if(dist < 0.001)
        {
            hit = true;
        }
        totalDist+=dist;

        if(totalDist > 20.0)
            break;
           p+=dist*dir;
    }

    return p;
}
