const int accuracy = 25;
const float jlen = 2.;
const vec3 axis = vec3(0.,1.,1.);
const float battery = 0.02;


mat3 rotationMatrix(in vec3 axis, in float angle)
{
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(
    oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
    oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
    oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}


float boxIntersection( in vec3 ro, in vec3 rd, vec3 boxSize, out vec3 outNormal ) 
{
    vec3 m = 1.0/rd; // can precompute if traversing a set of aligned boxes
    vec3 n = m*ro;   // can precompute if traversing a set of aligned boxes
    vec3 k = abs(m)*boxSize;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.0) return (-1.0); // no intersection
    outNormal = -sign(rd)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);
    return (tN > 0.)?tN:tF;
}


float lengthToJulia(in vec2 p, in vec2 c){
    int i = 0;
    for(float t; i <= accuracy;){
        t = (p.x*p.x) - (p.y*p.y);
        p.y = (p.x*p.y) + (p.y*p.x);
        p.x = t;
        p += c;
        if(length(p) > jlen){ break; }
        i++;
    }
    return float(i) / float(accuracy);
}


float figureNoise(in vec2 x, float seed){
    return fract(tan(dot(x, vec2(10.58927, 10.89820471)/seed/50.)));
}

