#define TAU (6.283185307)
#define CELLS_AROUND (12.)

// A single iteration of Bob Jenkins' One-At-A-Time hashing algorithm.
uint hash(uint x) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}
// fade function defined by ken perlin
vec2 fade(vec2 t) {
  return t * t * t * (t * (t * 6. - 15.) + 10.);
}
// corner vector
vec2 cvec(vec2 uv, float time) {
  uint x = uint(mod(uv.x, 256.));
  uint y = uint(mod(uv.y, CELLS_AROUND));
  float n = (float(hash(x + hash(y))) / 256.) + (time * TAU);
  return vec2(cos(n), sin(n));
}
// perlin generator
float perlin(vec2 uv, float offset) {
  vec2 i = floor(uv);
  vec2 f = fract(uv);

  vec2 u = fade(f);

  return
  mix(
    mix(
      dot( cvec(i + vec2(0.0,0.0), offset ), f - vec2(0.0,0.0) ),
      dot( cvec(i + vec2(1.0,0.0), offset ), f - vec2(1.0,0.0) ),
    u.x),
    mix(
      dot( cvec(i + vec2(0.0,1.0), offset ), f - vec2(0.0,1.0) ),
      dot( cvec(i + vec2(1.0,1.0), offset ), f - vec2(1.0,1.0) ),
    u.x),
  u.y);
}
// stripes of color
const vec3[] colors = vec3[](
  vec3(142, 202, 230)/255.,
  vec3(33, 158, 188)/255.,
  vec3(2, 48, 71)/255.,
  vec3(255, 183, 3)/255.,
  vec3(251, 133, 0)/255.,
  vec3(105, 46, 22)/255.
);
vec3 stripes(float n) {
  return mix(
    colors[uint(n + 6.0) % 6u],
    colors[uint(n + 7.0) % 6u],
    smoothstep(15./16., 1.0, fract(n))
  );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float time = fract(iTime / 4.);
  // Normalized pixel coordinates (from 0 to 1)
  float scale = min(iResolution.x, iResolution.y);
  vec2 uv = (gl_FragCoord.xy - 0.50 * iResolution.xy) / scale;
  vec2 rt = vec2(2.1 * log(length(uv)), atan(uv.y, uv.x) * CELLS_AROUND / TAU);
  float  value = rt.x * 0.5;
  value += 0.5  * perlin(rt *  2.0, 0.);
  value += 0.8  * perlin(rt *  7.0, 0.8);
  // Output to screen
  fragColor = vec4(
    stripes(256. + value - time * 6.), 1
  );
}

#define TAU (6.283185307)
#define HEX(x) (vec3((x >> 16) & 255, (x >> 8) & 255, x & 255) / 255.)

// Reverse lerp and keeps the value between 0. and 1.
float map(float vmin, float vmax, float v) {
    return clamp(
        (v-vmin)/(vmax-vmin), // reverse lerp
        0., 1. // clamp
    );
}

// A single iteration of Bob Jenkins' One-At-A-Time hashing algorithm.
uint hash(uint x) {
    x &= 65535u;
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x & 65535u;
}
// Fade function defined by Ken Perlin
vec2 fade(vec2 t) {
  return t * t * t * (t * (t * 6. - 15.) + 10.);
}
// Corner vector for Perlin noise
vec2 cvec(vec2 uv, float time) {
  uint x = uint(mod(uv.x, 256.));
  uint y = uint(mod(uv.y, 256.));
  float n = (float(hash(x + hash(y))) / 65535. + time) * TAU;
  return vec2(
      sin(n), cos(n)
  );
}
// Perlin generator.
// Coordinates can loop an integer number of cells,
// including in only 1 coordinate
// * uv: coordinates
// * offset: time from 0 to 1 (will loop around)
// * loop: number of cells to loop around
float perlin(vec2 uv, float offset, vec2 loop) {
  // loop coordinates
  if (loop.x > 0.) {uv.x = mod(uv.x, loop.x);}
  if (loop.y > 0.) {uv.y = mod(uv.y, loop.y);}
  vec2 i = floor(uv); // floored coordinates
  vec2 n = floor(uv + 1.); // next coordinates
  // loop next coordinates for wrap-around
  if (loop.x > 0.) {n.x = mod(uv.x + 1., loop.x);}
  if (loop.y > 0.) {n.y = mod(uv.y + 1., loop.y);}
  // blending ratio
  vec2 f = fract(uv);
  vec2 u = fade(f);
  offset = fract(offset);
  // monstrous meta-mix expression
  return
  mix( // mix vertically
    mix( // mix top horizontally
      dot(cvec(i,              offset), f - vec2(0.0,0.0)),
      dot(cvec(vec2(n.x, i.y), offset), f - vec2(1.0,0.0)),
    u.x),
    mix( // mix bottom horizontally
      dot(cvec(vec2(i.x, n.y), offset), f - vec2(0.0,1.0)),
      dot(cvec(n,              offset), f - vec2(1.0,1.0)),
    u.x),
  u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Make sure this loops
    float time = fract(iTime / 6.0);
    // Normalized pixel coordinates
    vec2 uv = ( 2.* fragCoord - iResolution.xy ) / length(iResolution.xy);
    // use log distance for perspective/tunnel effect
    float dist = log(uv.x * uv.x + uv.y * uv.y);
    float angle = atan(uv.y, uv.x) / TAU;
    vec2 rt = vec2(0.5 + dist * 2., angle * 24. + dist * 0.2);
    // generate noise
    float noise = 1.0 * perlin(
        rt * 1. + vec2(-10., 16.) * time,
        time,
        vec2(10., 24.)
    );
    noise += 0.3 * perlin(
        rt * 3. + vec2(-20., 16.) * time,
        -2. * time,
        vec2(20., 24.)
    );
    //noise *= max(0.0, dist * 0.2 + 2.);
    // calculate the total "brightness" of the image
    float bright = noise + rt.x * 0.4;

    // add some smoldering from the perimeter
    float smolder = 
        sin(dist + (3. * time * TAU)) * 0.8
        + sin(dist + (3. * (time - 0.2) * TAU)) * 0.4;
    bright += smolder;

    bright = max(-2., bright);

    // separately, add little stars
    float stars = perlin(
        uv * 16. + vec2(0., -7.) * time,
        time,
        vec2(0., 7.)
    );
    stars += perlin(
        uv * 37. + vec2(0., -7.) * time,
        time,
        vec2(0., 7.)
    );

    // add symbol in the center
    float letterSize = 0.185 + (0.025 * sin(8. * time * TAU));
    // check if the region is within the square region around the center
    if (abs(uv.x) < letterSize * 0.9 && abs(uv.y) < letterSize * 0.9) {
        bright += 1. * texture(
            iChannel0, // the letter texture thing
            (uv / 2. // zoom out for +-2 unit side length
            / letterSize // zoom in to fit our cut-out region
            + 
                vec2(floor(time * 16.), floor(fract(time * 16.) * 16.)) // character offset
                             // take the ascii code in hex, y is high nybble, x is low nybble
                             // e.g. ? is (15, 3) because its ASCII code is 0x3F
                             // this texture has other symbols in place of control or whitespace characters
                             // like eighth note in 0x0A
                + vec2(0.5, -0.5)
            ) / 16. // zoom in for 16 characters tall and wide texture
        ).r;
    }

    // map the brightness to colors
    float bright0 = map(
        -1.5, -1.45, bright
    );
    float bright1 = map(
        -0.5, -0.45, bright
    );
    vec3 col = mix(
        HEX(0xfec157),
        mix(
            HEX(0xe5502f),
            mix(
                HEX(0x793c37), HEX(0xFFFF9f), step(
                    0.5, stars
                )
            ),
            bright1
        ), bright0
    );

    // Output to screen
    fragColor = vec4(
        col, 1.0
    );
}

#define _Speed (0.1)
#define _LuminanceJitterThreshold (0.5)
#define _Fading (0.7)

vec2 Speed()
{
    return vec2(_Speed, _Speed);
}

float randomNoise(vec2 c)
{
    return fract(sin(dot(c.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

vec2 iTimeV2()
{
    return vec2(iTime, iTime);
}


vec3 AnalogNoiseGlitch(vec2 uv)
{
    float noiseX = randomNoise(iTimeV2() * Speed() + uv);
    float noiseY = randomNoise(iTimeV2() * Speed() - uv);
    float noiseZ = randomNoise(iTimeV2() * Speed() + uv);

    vec3 col = texture(iChannel0, uv).xyz;
    vec3 noise_col = col;

    float luminance = dot(noise_col, vec3(0.22, 0.707, 0.071));

    if(randomNoise(vec2(iTime * _Speed, iTime * _Speed)) > _LuminanceJitterThreshold)
    {
        noise_col = vec3(luminance, luminance, luminance);
    }


    noise_col += 0.25 * vec3(noiseX, noiseY, noiseZ) - 0.125;

    noise_col = mix(col, noise_col, _Fading);
    return noise_col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = AnalogNoiseGlitch(uv);  

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    for(float i=0.,g,e=1.;++i<60.||e>.01;e<.01?O.xyz+=cos(d*p)/i:p){
        p=g*d;
        p=R(p, vec3(.577), iTime*.3);
        for(int j=0;j++<9;)
            p.xy=length(p.xy)*sin(vec2(0,1.57)+sin(atan(p.x,p.y)*3.)/3.),
            p.y-=7.,
            p=p.yzx,
            p=R(p,vec3(1,0,0),.2);
        g+=e=dot(abs(p),vec3(.3,.7,.2))-.8;
    }
}

// Debug feature:
