#define DISABLE_TEXTURES 0
// How many times should the textusre repeat as the angle makes 1 complete turn?
// For square textures, I recommend 4ish
#define TEXTURE_HLOOPS 4

float distHeart(float x, float y)
{
    float n = -0.5 * abs(x) + y;
    return log(
        x * x + 1.5 * n * n
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalizes coords so that center is (0,0) and diagonals are length 1
    vec2 uv = (fragCoord / length(iResolution.xy)
	- (iResolution.xy / length(iResolution.xy) / 2.)) * 2.;
    // weird hybrid distance function
    float r = length(uv);
    r = log(r) * 0.6 + r * -0.3;
    // angle from 0 to 1
    float angle = atan(uv.y, uv.x) / (2. * 3.1415927);
    float t = fract(iTime / 5.0);

    // the texture X and Y coords
    float spiral1 = r * 1.0 + angle * 1. + t * 1.;
    float spiral2 = r * 0.2 + angle * -float(TEXTURE_HLOOPS) + t * 2.;

    vec2 txUV = vec2(spiral2, spiral1);

    // Time varying pixel color
    #if DISABLE_TEXTURES
    vec3 col = vec3(fract(txUV).xy, 0.0);
    #else
    vec3 col = mix(
        vec3(0.2, 0.1, 0.0),
        vec3(0.6, 0.4, 0.1),
        texture(iChannel0, txUV).r
    );
            float rings = smoothstep(0.5, 1.0, sin(3.1415927 * 2. * (t * 5. + distHeart(uv.x, uv.y) * 0.5 )));
    col = mix(
        col,
        vec3(1.0, 0.2, 0.4),
        rings
    ) + 0.1 * rings;
    #endif
    // Output to screen
    fragColor = vec4(col,1.0);
}

float trapezoid(vec2 position, float halfWidth1, float halfWidth2, float halfHeight) {
    position.x = abs(position.x);
    position.x -= (halfWidth2 + halfWidth1) * 0.5;
    vec2 end = vec2((halfWidth2 - halfWidth1) * 0.5, halfHeight);
    vec2 segment = position - end * clamp(dot(position, end) / dot(end, end), -1.0, 1.0);
    float d = length(segment);
    if (segment.x > 0.0) {
        return d;
    }
    return max(-d, abs(position.y) - halfHeight);
}

