void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = fragCoord / iResolution.xy;
    position = (position * 2.0) - 1.0;
    position.x *= iResolution.x / iResolution.y;

    float halfWidth1 = 0.2 + 0.15 * sin(iTime * 1.3);
    float halfWidth2 = 0.2 + 0.15 * sin(iTime * 1.4 + 1.1);
    float halfHeight = 0.5 + 0.2 * sin(1.3 * iTime);
    float d = trapezoid(position, halfWidth1, halfWidth2, halfHeight);

    // same colorization that Inigo Quilez uses in his shaders
    vec3 color = vec3(1.0) - sign(d) * vec3(0.1, 0.4, 0.7); // base color
    color *= 1.0 - exp(-4.0 * abs(d)); // gradient
	color *= 0.8 + 0.2 * cos(120.0 * d); // ripples
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.015, abs(d)));
    if (iMouse.z > 0.001) {
        vec2 mousePosition = iMouse.xy / iResolution.xy;
        mousePosition = (mousePosition * 2.0) - 1.0;
        mousePosition.x *= iResolution.x / iResolution.y;
        float d = trapezoid(mousePosition, halfWidth1, halfWidth2, halfHeight);
        color = mix(color, vec3(1.0, 1.0, 0.0), 1.0 - smoothstep(0.0, 0.0025, abs(length(position - mousePosition) - abs(d)) - 0.0025));
    }
    fragColor = vec4(color, 1.0);
}

mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
float diam(vec3 p,float s){
     p = abs(p);
     return (p.x+p.y+p.z-s)*inversesqrt(3.);
}
vec2 sdf(vec3 p){
    p.xz*=rot(iTime*.1);
    vec2 h;
    h.x = 1000.+length(p)-1.;
    vec4 pp = vec4(p,1.);
    float s = 0.;
    for(float i=0.;i<=9.;i++){
        pp.xyz = abs(pp.xyz)-9.95;;
        pp*=1.7;
        pp.xz*=rot(.785+i);
        pp.xy*=rot(-.785+i);
        float q = (diam(pp.xyz,1.95))/pp.a;
        h.x = mod(i,2.)==0. ? mix(q,h.x,0.75): min(q,h.x);
        s+=clamp(cos(pp.y)+sin(pp.x),0.,1.);
    }
    s/=pp.a*1.8;
    h.x = min(length(p)-1.-s,h.x);
    h.y = 1.;
    return h;
}

#define q(s) s*sdf(p+s).x
#define ao(rp,n,k) (sdf(rp+n*k).x/k)
#define gao(rp,n) (ao(rp,n,.1)+ao(rp,n,.3)+ao(rp,n,.7))
vec3 norm(vec3 p,float ee){ vec2 e=vec2(-ee,ee) ; return normalize(q(e.xyy)+q(e.yxy)+q(e.yyx)+q(e.xxx));}
