}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = 0.04;

    float t = iTime;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord -.5* iResolution.xy) / iResolution.y;
    float theta = atan(uv.y,uv.x);

    float d = 2. * cos(t) * 0.75 * (abs(uv.x * uv.y) * (1. + 0.1 * cos(2. * theta + t)) + abs(uv.x) + abs(uv.y));


    float r = 0.5 * smoothstep(d + 0.1 * cos(cos(2. * theta +  t)), 
                         d + 0.054 + 0.023 * (1. + cos(t)),
                         0.35 + 0.15 * cos(t));
    r += 0.5 * smoothstep(d + 0. * cos(cos(1. * theta +  t)), 
                         d + 0.054 + 0.023 * (1. + cos(t)),
                         0.25 + 0.05 * cos(t));
    r *= 1. + cos(0.25 * 3.14159 * (cos(5. * theta ) - sin(7. * theta )) + t);

    t += a;

    float g = 0.5 * smoothstep(d + 0.1 * cos(cos(2. * theta +  t)), 
                         d + 0.054 + 0.023 * (1. + cos(t)),
                         0.35 + 0.15 * cos(t));
    g += 0.5 * smoothstep(d + 0. * cos(cos(1. * theta +  t)), 
                         d + 0.054 + 0.023 * (1. + cos(t)),
                         0.25 + 0.05 * cos(t));
    g *= 1. + cos(0.25 * 3.14159 * (cos(5. * theta ) - sin(7. * theta )) + t);

    t -= 2. * a;

    float b = 0.5 * smoothstep(d + 0.1 * cos(cos(2. * theta +  t)), 
                         d + 0.054 + 0.023 * (1. + cos(t)),
                         0.35 + 0.15 * cos(t));
    b += 0.5 * smoothstep(d + 0. * cos(cos(1. * theta +  t)), 
                         d + 0.054 + 0.023 * (1. + cos(t)),
                         0.25 + 0.05 * cos(t));
    b *= 1. + cos(0.25 * 3.14159 * (cos(5. * theta ) - sin(7. * theta )) + t);



    //e *= cos(t); //e *= 16. * d * d * (1. - d) * (1.-d);
    //e = 0.9 * cos(e);
    // Output to screen
    fragColor = vec4(g,r,b,1.);
}

