void initBG(float time){
    float bgTime = mod(NATIVEFPS * time, 253.0);

#ifdef WEBGL2
    int bgTimeInt = int(bgTime);
    int bgItem = 0;

    while(bgTimes[bgItem] <= bgTimeInt)
        bgItem++;
    bgItem--;

    float bgLerp = (bgTime - float(bgTimes[bgItem])) / (float(bgTimes[bgItem + 1] - bgTimes[bgItem]));
    col = mix(bgCols[bgItem], bgCols[bgItem + 1], bgLerp);
#else
    vec3 bgCol = vec3(81, 149, 249) / 255.0;
    vec3 nextCol = vec3(249, 159, 253) / 255.0;

    float closeTime = 0.0;
    float nextTime = 38.0;

    if(bgTime > 38.0){
        bgCol = nextCol;
        closeTime = nextTime;
        nextTime = 77.0;
        nextCol = vec3(233, 0, 0) / 255.0;
        if(bgTime > 77.0){
            bgCol = nextCol;
            closeTime = nextTime;
            nextTime = 149.0;
            nextCol = vec3(253, 255, 81) / 255.0;
            if(bgTime > 149.0){
                bgCol = nextCol;
                closeTime = nextTime;
                nextTime = 183.0;
                nextCol = vec3(20, 255, 9) / 255.0;
                if(bgTime > 183.0){
                    bgCol = nextCol;
                    closeTime = nextTime;
                    nextTime = 221.0;
                    nextCol = vec3(22, 255, 253) / 255.0;
                    if(bgTime > 221.0){
                        bgCol = nextCol;
                        closeTime = nextTime;
                        nextTime = 253.0;
                        nextCol = vec3(81, 149, 249) / 255.0;
                    }
                }
            }
        }
    }

    float bgLerp = (bgTime - closeTime) / (nextTime - closeTime);
    col = mix(bgCol, nextCol, bgLerp);
#endif
}


