float sdLine(in vec2 p, in vec2 a, in vec2 n) {
    return length((p - a) - dot((p - a), n) * n);
}


bool pointInTriangle(in vec3 p, in vec3 a, in vec3 b, in vec3 c) {
    return cross(b - a, p - a).z > 0. &&
        cross(c - b, p - b).z > 0. &&
        cross(a - c, p - c).z > 0.;
}


vec2 lineIntersection(in vec2 p1, in vec2 n1, in vec2 p2, in vec2 n2) {
  float a = n1[1] / (n1[0] + 1e-10);
  float c = p1[1] - a * p1[0];

  float b = n2[1] / (n2[0] + 1e-10);
  float d = p2[1] - b * p2[0];

  float x = (d - c) / (a - b);
  float y = a * x + c;

  return vec2(x, y);
}


