    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (-iResolution.xy + 2. * fragCoord) / iResolution.y; // -1 <> 1 by height

    float r = 0.01;

    vec2 a1 = vec2(0.7, 0.), n1 = vec2(N, N);
    vec2 a2 = vec2(0.7, 0.), n2 = vec2(-N, N);
    vec2 a3 = vec2(0., 0.), n3 = vec2(sin(iTime) , cos(iTime));


    float c = sdLine(p, a1, n1) - r;
    c = min(c, sdLine(p, a2, n2) - r);
    c = min(c, sdLine(p, a3, n3) - r);


    vec3 A = vec3(lineIntersection(a3, n3, a1, n1), 0.);
    vec3 B = vec3(lineIntersection(a1, n1, a2, n2), 0.);
    vec3 C = vec3(lineIntersection(a3, n3, a2, n2), 0.);

    float x = pointInTriangle(vec3(p, 0.), A, B, C) && c > 0.08 ? 1. : 0.;
    c = step(c, 0.02);
    c = max(c, x);
    c *= (1. - pow(length(p), 2.));

    fragColor = vec4(vec3(c),1.0);
}

vec3 circle(float aspect, vec2 uv, float radius, vec3 color, float blur, float animSpeed, vec2 pos) {
    float anim = abs(sin(iTime*animSpeed))*blur;
    vec3 result = vec3(smoothstep(radius-anim, radius+anim, distance(uv, vec2(pos.x*aspect, pos.y))));
    result = (1.0-result) * color;
    return result;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;

    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 green = vec3(0.0, 1.0, 0.0);
    vec3 blue = vec3(0.0, 0.0, 1.0);
    vec3 yellow = vec3(1.0, 1.0, 0.0);

    vec3 c1 = circle(aspect, uv, 0.2, red, 0.31, 4.1, vec2(0.81, 0.7));

    vec3 c2 = circle(aspect, uv, 0.15, green, 0.05, 1.0, vec2(0.12, 0.21));

    vec3 c3 = circle(aspect, uv, 0.23, blue, 0.03, 2.1, vec2(0.58, 0.3));

    vec3 c4 = circle(aspect, uv, 0.1, yellow, 0.1, 1.1, vec2(0.3, 0.5));

    vec3 col = c1 + c2 + c3 + c4;


    // Output to screen
    fragColor = vec4(col,1.0);
}

float noise(vec2 x){
    vec2 f = fract(x);
    vec2 u = f*f*f*(f*(f*6.0-15.0)+10.0);
    vec2 du = 30.0*f*f*(f*(f-2.0)+1.0);

    vec2 p = floor(x);
	float a = texture(iChannel0, (p+vec2(0.0, 0.0))/1024.0).x;
	float b = texture(iChannel0, (p+vec2(1.0,0.0))/1024.0).x;
	float c = texture(iChannel0, (p+vec2(0.0,1.0))/1024.0).x;
	float d = texture(iChannel0, (p+vec2(1.0,1.0))/1024.0).x;


	return a+(b-a)*u.x+(c-a)*u.y+(a-b-c+d)*u.x*u.y;
}

float fbm(vec2 x, int detail){
    float a = 0.0;
    float b = 1.0;
    float t = 0.0;
    for(int i = 0; i < detail; i++){
        float n = noise(x);
        a += b*n;
        t += b;
        b *= 0.7;
        x *= 2.0; 

    }
    return a/t;
}

float fbm2(vec2 x, int detail){
    float a = 0.0;
    float b = 1.0;
    float t = 0.0;
    for(int i = 0; i < detail; i++){
        float n = noise(x);
        a += b*n;
        t += b;
        b *= 0.9;
        x *= 2.0; 

    }
    return a/t;
}


