           + smoothstep(-0.09, -0.085, -uv2.y - 0.001/(1.0 - abs(uv2.x*2.0 - 1.0))), 1.0);

    k *= min(smoothstep(0.05, 0.3, 1.0 - abs(fract(uv2.x*16.0)*2.0 - 1.0))
