#define STEPS 150.0
#define MDIST 100.0
#define pi 3.1415926535
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec3 rdg = vec3(0);
float h21(vec2 p){
    return fract(43757.5453*sin(dot(p, vec2(12.9898,78.233))));
}
float h13(vec3 p3){
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}
float octa( vec3 p, float s){
  p = abs(p);
  return (p.x+p.y+p.z-s)*-tan(5.0*pi/6.0);
}
float box(vec3 p, vec3 b){
    vec3 d= abs(p)-b;
    return max(d.x,max(d.y,d.z));
}
bool filled(vec3 id){
    if(id.y<0.) return true;
    float cyc = floor(iTime*0.15);
    float hash = h13(id+cyc);
    cyc = mod(cyc,3.0);
    //I had to do this uglyness because compiler keeps tryning to inline everything
    //(this saves like 2 seconds of compile time)
    float w1 = max(abs(id.x),abs(id.z));
    float w2 = length(id.y*id.y);
    float w3 = 0.3;
    if(cyc==0.0) w1 = length(id.xz);
    if(cyc==2.0) {w2 = abs(id.y);w3 = 0.4; }
    return(hash+w1*w3+w2*0.01<0.85);
}
vec2 map(vec3 p){
    float t = iTime;
    vec3 po = p;
    vec2 a = vec2(9999.,1);
    vec2 b = vec2(2);

    vec3 id = floor(p);
    //float hash = h13(id+floor(t*0.1));
    vec3 dir = sign(rdg)*.5;
    vec3 q = fract(p)-.5;
    //q = vec3(q.x,q.y,q.z);
    vec3 rc = (dir-q)/rdg;

    float dc = min(rc.x,min(rc.y,rc.z))+0.01;
    bool ifilled = filled(id);
    if(max(id.x,id.z)<5.0){
        if(ifilled)a.x = box(q,vec3(0.5));
        float nbors = 0.;
        vec3 off = vec3(0);
        if(filled(id+vec3(1,0,0))){nbors++;off+=vec3(1,0,0);}
        if(filled(id+vec3(0,1,0))){nbors++;off+=vec3(0,1,0);}
        if(filled(id+vec3(0,0,1))){nbors++;off+=vec3(0,0,1);}
        if(filled(id+vec3(-1,0,0))){nbors++;off+=vec3(-1,0,0);}
        if(filled(id+vec3(0,-1,0))){nbors++;off+=vec3(0,-1,0);}
        if(filled(id+vec3(0,0,-1))){nbors++;off+=vec3(0,0,-1);}
        if(nbors==3.0&&!ifilled)a.x = max(box(q,vec3(0.5)),-(length(q+off*0.5)-1.)*0.6);
        float hh = h13(id+floor(iTime*0.15));
        if(nbors==2.0&&!ifilled&&hh>0.33){
            vec3 p2 = q+off*0.71;
            p2.xy*=rot((1.0-abs(off.z))*pi/4.);
            p2.yz*=rot((1.0-abs(off.x))*pi/4.);
            p2.zx*=rot((1.0-abs(off.y))*pi/4.);
            float cut = box(p2,vec3(1.0));
            a.x = max(box(q,vec3(0.5)),-cut*0.6)*0.7;
        }
        else if(hh>0.26&&nbors==2.0&&!ifilled)a.x = max(box(q,vec3(0.5)),-(length(q+off*0.5)-1.1)*0.6);

        if(nbors==2.0&&!ifilled&&off==vec3(0))a.x = box(q,vec3(0.5));
       // if(nbors==3.0&&!ifilled)a.x = box(q,vec3(0.5));
    }
    b.x = p.y;
    a=(a.x<b.x)?a:b;
    dc = max(dc,box(po,vec3(4,50,4)));
    a.x = min(a.x,dc);

    return a;
}
vec3 norm(vec3 p,float s){
    vec2 e = vec2(s,0);
    return normalize(map(p).x-vec3(
    map(p-e.xyy).x,
    map(p-e.yxy).x,
    map(p-e.yyx).x));
}
void render( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    vec3 ro = vec3(0,6,-13);
    if(iMouse.z>0.){
    ro.xz*=rot(10.0*iMouse.x/iResolution.x);
    }
    else ro.xz*=rot(iTime*0.3);

    vec3 lk = vec3(0,3.5,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = f*1.1+uv.x*r+uv.y*cross(f,r);
    rdg = rd;
    vec3 p = vec3(0);
    vec2 d = vec2(0);
    float dO= 0., shad = 0.;
    bool hit = false;

    for(float i = 0.0; i<STEPS; i++){
        p = ro+rd*dO;
        d = map(p);
        dO+=d.x;
        if((d.x)<0.005){
            hit = true;
            shad = i/STEPS;
            break;
        }
        if(dO>MDIST){
            break;
        }
    }
    if(hit){
        vec3 ld = normalize(vec3(0.5,1.01,-1));
        //ld.xz*=rot(iTime*0.3);
        vec3 n = norm(p,0.001);
        vec3 h = normalize(ld-rd);
        float spec = pow(max(dot(n,h),0.0),5.0);
        //float fres = pow(1. - max(dot(n, -rd),0.), 5.);
        float diff = dot(n, ld)*0.4+0.6;
        vec3 al = vec3(1);
        ld.xy*=rot(h21(uv)*0.005);
        ld.yz*=rot(h21(uv+1.)*0.005);
        ld.zx*=rot(h21(uv+2.)*0.005);
        float shadow = 1.;
        rdg = ld;
        for(float h = 0.05; h<20.;){
            float dd = map(p+ld*h).x;
            if(dd<0.001){shadow = 0.6; break;}
            h+=dd;
        }
        //shadow = max(shadow,0.8);

        //AO & soft shadow doesn't work because of the domain rep tricks, oh well

        diff -=(h21(uv)-0.4)*(pow(1.0-diff*shadow,9.0))*4.0;
        col=al*diff+pow(spec,2.0)*0.1*shadow;
        col*=shadow;
    }
    else{
    col = mix(vec3(0.6),vec3(0.647,0.647,0.694),uv.y);
    }
    //col = sqrt(col);
    fragColor = vec4(col,1.0);
}

//if you have a very good GPU you can crank up the AA and it looks a lot better
#if HW_PERFORMANCE==0
#define AA 1.0
#else
#define AA 2.0 
#endif

#define ZERO min(0.0,iTime)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float px = 1.0/AA;
    vec4 col = vec4(0);

    if(AA==1.0) {render(col,fragCoord); fragColor = col; return;}

    for(float i = ZERO; i <AA; i++){
        for(float j = ZERO; j <AA; j++){
            vec4 col2;
            vec2 coord = vec2(fragCoord.x+px*i,fragCoord.y+px*j);
            render(col2,coord);
            col.rgb+=col2.rgb;
        }
    }
    col/=AA*AA;
    fragColor = vec4(col);
}

const int MAX_STEP = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.001;
/* 交*/
float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

/* 并*/
float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

/* 差*/
float subtractSDF(float distA, float distB) {
    return max(distA, -distB);
}
/* 异或*/
float eorSDF(float distA, float distB) {
    return subtractSDF(unionSDF(distA,distB),intersectSDF(distA,distB));
}
/* 圆柱的SDF*/
float cylinderSDF(vec3 p, float h, float r) {
    // How far inside or outside the cylinder the point is, radially
    float inOutRadius = length(p.xy) - r;

    // How far inside or outside the cylinder is, axially aligned with the cylinder
    float inOutHeight = abs(p.z) - h/2.0;

    // Assuming p is inside the cylinder, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(inOutRadius, inOutHeight), 0.0);

    // Assuming p is outside the cylinder, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(vec2(inOutRadius, inOutHeight), 0.0));

    return insideDistance + outsideDistance;
}
/* 一个立方体的SDF */
float cubeSDF(vec3 p) {
    // If d.x < 0, then -1 < p.x < 1, and same logic applies to p.y, p.z
    // So if all components of d are negative, then p is inside the unit cube
    vec3 d = abs(p) - vec3(1.0, 1.0, 1.0);

    // Assuming p is inside the cube, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);

    // Assuming p is outside the cube, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

/* 一个球的SDF */
float sphereSDF(vec3 samplePoint){
    return length(samplePoint) - 1.414;
}

/* 场景的SDF，为球和立方体的交集 */
float sceneSDF(vec3 samplePoint){
    float res = subtractSDF(intersectSDF(sphereSDF(samplePoint),cubeSDF(samplePoint)),cylinderSDF(samplePoint,4.0,0.5));
    return res;
}

/* 从眼睛朝某一方向，发出光线，直到击中场景表面，之间的距离 */
float shortestDistanceFromEyeToSurface(vec3 eye, vec3 rayDirection, float start, float end){
    float depth = start;
    for(int i = 0; i < MAX_STEP;i++){
        float dist = sceneSDF(eye+depth*rayDirection);
        if(dist < EPSILON) 
            return depth;
        else 
            depth += dist;
        if(depth >=end)
           return end;

    }
    return end;
}
/* 根据视场角以及视口的尺寸，生成到每一个像素的光线方向 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/* 根据SDF的梯度来估计法线方向 
   p 点在SDF表面，n = (dp/dx,dp/dy,dp/dz)

*/
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}
/* 计算 Phong 光照 c = c_r(c_a + c_l*max(0.0,dot(N,L)) + c_l*c_p*pow(max(0.0,dot(R,E)),s))
*/
vec3 PhongLight(vec3 c_r, vec3 c_a, vec3 c_p, vec3 c_l,vec3 eye, vec3 p_l, vec3 p, float s){
    vec3 N = estimateNormal(p);
    vec3 L = normalize(p_l - p);
    vec3 E = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotNL = max(0.0,dot(N,L));
    float dotRE = max(0.0,dot(R,E));
    vec3 c = c_r*(c_a + c_l*dotNL + c_l*c_p*pow(dotRE,s));
    return c;
}
/* 计算光照，在这个函数设定光源参数，然后调用 PhongLight 计算光照
*/
vec3 illumination(vec3 c_r, vec3 c_a, vec3 c_p, vec3 eye, vec3 p, float s) {
    vec3 c_l = vec3(1.0);
    vec3 p_l = vec3(4.0 * sin(iTime),
                          1.0,
                          4.0 * cos(iTime));
    vec3 color = PhongLight(c_r,c_a,c_p,c_l,eye,p_l,p,s);    
    return color;
}
/* 摄像机的旋转矩阵 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 v = normalize(eye - center);
    vec3 r = normalize(cross(up,v));
    vec3 u = cross(v,r);
    vec3 t = vec3(-dot(eye,r),-dot(eye,u),-dot(eye,v));
    return mat4(
        vec4(r,t.x),
        vec4(u,t.y),
        vec4(v,t.z),
        vec4(0.0,0.0,0.0,1.0)
    );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(5.0, 5.0, 5.0);
    mat4 rotateMatrix = viewMatrix(eye,vec3(0.0),vec3(0.0,1.0,0.0));
    vec3 worldDir = (rotateMatrix*vec4(viewDir,0.0)).xyz;
    float dist = shortestDistanceFromEyeToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }
    vec3 p = eye + dist * worldDir;

    vec3 c_r = vec3(0.0, 1.0, 1.0);
    vec3 c_a = vec3(0.2, 0.2, 0.2);
    vec3 c_p = vec3(1.0, 1.0, 1.0);
    float s = 5.0;

    vec3 color =  illumination(c_r, c_a, c_p, eye, p, s);

    fragColor = vec4(color, 1.0);
}

