#define PI acos(-1.)
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec2 edge (vec2 p)
{
    vec2 p2 = abs(p);
    if (p2.x > p2.y) return vec2((p.x<0.)?-1.:1., 0.);
    else return vec2(0., (p.y<0.)?-1.:1.);
}

float box (vec3 p , vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}


float SDF (vec3 p)
{
    p.yz *= rot(-atan(1./sqrt(2.)));
    p.xz *= rot(PI/4.);

    vec2 center = floor(p.xz)+.5;
    vec2 neighbour = center + edge(p.xz-center);    
    float py = sin(length(center)+iTime)*1.5;

    float me = box(p-vec3(center.x, 0., center.y), vec3(.4,2.+py,.4))-.05;
    float next = box(p-vec3(neighbour.x, 0., neighbour.y), vec3(.4, 3.5, .4))-.05;
    float set = min(me,next);

    return set;
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.001,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

float AO (float eps, vec3 p, vec3 n)
{return clamp(SDF(p+eps*n)/eps,0.,1.);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(uv*3.,-30.), rd=vec3(0.,0.1,1.),p=ro,
    col=vec3(0.),l=normalize(vec3(1.,2.,-2.));

    bool hit=false;
    for (float i=0.; i<100.; i++)
    {
        float d= SDF(p);
        if (d<0.001)
        {hit=true;break;}
        p += d*rd;
    }

    if (hit)
    {
        vec3 n = getnorm(p);
        float light = dot(n,l)*.5+.5;
        float ao = AO(0.1,p,n)+AO(0.35,p,n)+AO(0.75,p,n);
        col = vec3(1.)*light*(ao/2.);
    }

    fragColor = vec4(sqrt(col),1.0);
}

#define S smoothstep
#define P 3.14159265
float b(vec2 u, vec2 R, float r, float d, float f) {
    vec2 U = (u-.5)*vec2(R.x/R.y,1.);
    float o = length(U),
          t = d+iTime*6.,
          l = r + S(.1,1.,abs(u.y))*(sin(t - atan(U.x,U.y)*7.)*f)*.08,
          w = .004;
    return S(l, l+.005,o)-S(l+w, l+w+.005,o);
}
void mainImage(out vec4 O, in vec2 I)
{
    vec2 R = iResolution.xy,
         u = I/R.xy;
    vec3 r = vec3(0.1, 0.1, 0.3)*1.-length(u-.5)*.2;
    for (float i = 0.;i < 6.;i++){
        r += vec3(0.9,.2+i*.1,.8)*b(u,R,.35-i*0.014,i,.2+i*.02);
    }
    O = vec4(r,1);
}

#define rainbow(u) .5 + .5 * cos( atan(u.x,u.y) + 6.3 * iTime + vec3(0,23,21) )

void mainImage( out vec4 O, in vec2 U )
{
    U -= .5* iResolution.xy;
    O = vec4(rainbow(U),1.0);
}

