const float maxd=80.0;

vec3 rot(inout vec3 p,vec3 axis,float theta){
	axis=normalize(axis);
	return mix(axis*dot(p,axis),p,cos(theta))+sin(theta)*cross(p,axis);
}


vec2 polarAbs(vec2 p,float n)
{
  n*=0.5;
  float a = asin(sin(atan(p.x,p.y)*n))/n;
  return vec2(sin(a),cos(a))*length(p);
}

float lpNorm(vec2 p, float n)
{
	p = pow(abs(p), vec2(n));
	return pow(p.x+p.y, 1.0/n);
}


float lpNorm(vec3 p, float n)
{
	p = pow(abs(p), vec3(n));
	return pow(p.x+p.y+p.z, 1.0/n);
}


#define hash(p)fract(sin(p*12345.5))

vec3 randVec(float s)
{
    vec2 n=hash(vec2(s,s+2315.3));
    return vec3(cos(n.y)*cos(n.x),sin(n.y),cos(n.y)*sin(n.x));
}

vec3 randCurve(float t,float n)
{
    vec3 p = vec3(0);
    for (int i=0; i<3; i++){
        p+=randVec(n+=365.)*sin((t*=1.3)+sin(t*.6)*.5);
    }
    return p;
}

vec3 targetVector0()
{
    return randVec(32892.+floor(iTime/2.));
}

vec3 axisVector()
{
    vec3 a = targetVector0();
    vec3 b = a.yzx;
    for(int i=0;i<100;i++)
    {
        if (all(equal(a, b))==false) break;
        b.z += 0.1;
        b = normalize(b);
    }
    vec3 w = a;
    vec3 u = normalize(cross(b,w));
    vec3 v = cross(w,u);
    return v;
}

vec3 targetVectorA()
{
    vec3 v = targetVector0();
    vec3 a = axisVector();
    float t = -iTime;
    return rot(v,a,t);    
}

vec3 targetVectorB()
{
    vec3 v = targetVector0();
    vec3 a = axisVector();
    float t = iTime;
    return rot(v,a,t);
}


float deA(vec3 p)
{
    float de =1.;
    vec3 target = targetVectorA();
    vec3 axis = axisVector();
    vec3 w = normalize(target);
    vec3 u = normalize(cross(axis,w));
    vec3 v = cross(w,u);
    //p = inverse(mat3(u,v,w)) * p;
    p = p * mat3(u,v,w);
    vec3 q=p;
    p.x -= clamp(p.x, -0.1, 0.1);
    p.y -= clamp(p.y, -0.02, 0.02);
    p.z -= clamp(p.z, 0.1, 1.0);
    de = min(de,length(p)-.01);
    q.xz=polarAbs(q.xz,24.);
    q.z-=1.12;
    return min(de,(lpNorm(q,5.0)-.091+q.z*.3)*.8);
}

float deB(vec3 p)
{
    float de=1.;
    vec3 target = targetVectorB();
    vec3 axis = axisVector();
    vec3 w = normalize(target);
    vec3 u = normalize(cross(axis,w));
    vec3 v = cross(w,u);
    p = vec3(dot(p,u), dot(p,v), dot(p,w));
    de=min(de,lpNorm(vec2(length(p.yz)-.95,p.x),5.0)-.07);
    de=min(de,lpNorm(vec2(length(p.xz)-1.,p.y),5.0)-.1);
    de=min(de,lpNorm(vec2(length(p.xz)-.15,p.y),5.0)-.07);
    p.x -= clamp(p.x, -0.1, 0.1);
    p.y -= clamp(p.y, -0.02, 0.02);
    p.z -= clamp(p.z, 0.1, 1.0);
    de=min(de, length(p)-.01);
    return de;
}

float deC(vec3 p)
{
    vec3 targetA = normalize(targetVectorA());
    vec3 targetB = normalize(targetVectorB());
    // cross() test
    vec3 cx = cross(targetA, targetB);
    vec3 axis = targetA;
    vec3 w = normalize(cx);
    vec3 u = normalize(cross(axis,w));
    vec3 v = cross(w,u);
    p = transpose(mat3(u,v,w)) * p;
    //p = p * mat3(u,v,w);       
    float len = length(cx); 
    p.z -= clamp(p.z, 0.0, len);
    return lpNorm(p,3.)-.1;
}

float map(vec3 p)
{
    float de = 1.;
    de = min(de, deA(p));
    de = min(de, deB(p));
    de = min(de, deC(p));
    de = min(de, p.y + 1.2);
    return de;
}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<100;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<0.001) return t;
        if (t>=far) return far;
    }
    return far;
}

float calcShadow( vec3 light, vec3 ld, float len ) {
	float depth = march( light, ld, 0.0, len );	
	return step( len - depth, 0.01 );
}

vec3 doColor(vec3 p)
{
    if(deC(p)<0.001) return vec3(1.8,0.5,0.2);
    return vec3(0.3,0.5,0.8)+cos(p*0.5)*.5+.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 ro=vec3(1.5);
    vec3 ta = randCurve(iTime*.3,1234.6)*.4;
    vec3 rd = normalize(vec3(uv,2.0));
    vec3 w = normalize(ta-ro);
    vec3 u = normalize(cross(w,vec3(0,1,0)));
    vec3 v = cross(u,w);
    rd = mat3(u,v,w) * rd;
    vec3 col= vec3(0.05,0.05,0.1);
    float t=march(ro,rd,0.0,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=doColor(p); 
    	vec3 n = calcNormal(p);      
		vec3 lightPos=vec3(5,5,1);
    	vec3 li = lightPos - p;
		float len = length( li );
		li /= len;
		float dif = clamp(dot(n, li), 0.0, 1.0);
    	float sha = min(1.,calcShadow( lightPos, -li, len )+.8);
        col *= max(sha*dif, 0.2);
        float rimd = pow(clamp(1.0 - dot(reflect(-li, n), -rd), 0.0, 1.0), 2.5);
		float frn = rimd+2.2*(1.0-rimd);
    	col *= frn*0.5;
    	col *= max(0.5+0.5*n.y, 0.0);
    	col *= exp2(-1.*pow(max(0.0, 1.0-map(p+n*0.3)/0.3),2.0));
    	col += vec3(0.8,0.6,0.2)*pow(clamp(dot(reflect(rd, n), li), 0.0, 1.0), 10.0);
    	col = mix(vec3(0.1,0.1,0.2),col, exp(-0.03*t*t));
    }
    fragColor.xyz = col;
}

const int STEPS = 150;
const float SURFACE = 0.001;
const float MAXIMUM = 5000.0;
const float SHADOW_FACTOR = 0.15;
const float BOUNCE_MARGIN = 0.01;
const float PI = 3.1415925;

const float MATERIAL_LIGHT = 5.0;

mat2 rotate(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(+c, -s, +s, +c);
}

vec2 minSample(vec2 a, vec2 b) {
    return a.x < b.x ? a : b;
}

vec2 maxSample(vec2 a, vec2 b) {
    return a.x > b.x ? a : b;
}

float distanceSphere(vec3 position, vec4 sphere) {
    return length(position - sphere.xyz) - sphere.a;
}

float distanceBox(vec3 position, vec3 center, vec3 size) {
    vec3 d = abs(center - position) - size;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float distanceCylinder(vec3 p, vec2 h)
{
    vec2 d = abs(vec2(length(p.xz),p.y)) - h;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec2 opDifference(vec2 a, vec2 b) {
    return vec2(max(a.x, -b.x), a.y);
}

vec2 opUnion(vec2 a, vec2 b) {
    if (a.x <= b.x) {
        return a;
    }
    return b;
}

vec2 opBlend(vec2 a, vec2 b, float k) {
    float mat = a.y;
    float h = clamp(0.5 + 0.5 * (a.x - b.x) / k, 0.0, 1.0);
    float d = mix(a.x, b.x, h) - k * h * (1.0 - h); 
    return vec2(d, mat);
}

vec2 sampleScene(vec3 position) {
    vec4 sphere1 = vec4(0.4, 0.8, 3.0, 0.4);
    vec4 sphere2 = vec4(-0.5, 0.9, 3.0, 0.4);
    sphere1.y += sin(iTime) * 0.4;
    sphere2.x += cos(iTime) * 0.8;
    sphere2.z += sin(iTime) * 0.8;
    vec3 box1Center = vec3(-1.0, 1.3, 0.9);
    vec3 box1Size = vec3(0.2) + cos(iTime) * 0.025;

    float fx = floor(mod(position.x, 2.0));
    float fz = floor(mod(position.z, 2.0));
    vec2 d0 = vec2(position.y, 1.0 + floor(mod(fx + fz, 2.0)));
    vec2 d1 = vec2(distanceSphere(position, sphere1), 2.0);
    vec2 d2 = vec2(distanceSphere(position, sphere2), 2.0);
    vec2 d3 = vec2(distanceBox(position, box1Center, box1Size), 3.0);
    vec2 d4 = vec2(abs(position.x + 2.0), 3.0);
    vec3 p = position - vec3(0.0, 1.0, 1.0);

    vec2 scene = vec2(1e9, 0.0);
    scene = minSample(scene, d0);
    scene = minSample(scene, d1);
    scene = minSample(scene, d2);
    scene = minSample(scene, d3);
    scene = minSample(scene, d4);

    vec4 c = vec4(0.5, 1.0, 1.0, 0.30);
    float ch = 0.35;
    int l1 = 10;
    int l2 = 24;

    vec2 d5 = vec2(distanceCylinder(c.xyz - position, vec2(c.w, ch)), 4.0);
    vec2 d6 = vec2(distanceCylinder(c.xyz - position, vec2(c.w * 0.99, ch + 0.01)), 4.0);
    vec2 d7 = vec2(distanceSphere(position, vec4(c.xyz, c.w * 0.25)), MATERIAL_LIGHT);
    d5 = opDifference(d5, d6);
    d5 = opUnion(d5, d7);
    if (d5.x < 0.01) {
        for (int i1 = 0; i1 < l1; ++i1) {
            float r1 = float(i1) / (float(l1) - 1.0);
            for (int i2 = 0; i2 < l2; ++i2) {
                float r2 = float(i2) / float(l2);
                float a = PI * 2.0 * r2;
                vec4 p = vec4(
                    c.x + cos(a) * c.w,
                    c.y - ch + ch * 2.0 * r1,
                    c.z + sin(a) * c.w,
                    0.99 * PI * c.w / float(l2)
                );
                vec2 d = vec2(distanceSphere(position, p), 1.0);
                d5 = opDifference(d5, d);
            }
        }
    }
    scene = minSample(scene, d5);

    return scene;
}

vec2 rayMarch(vec3 rayOrigin, vec3 rayDirection) {
    float rayDistance = 0.0;
    for (int i = 0; i < STEPS; ++i) {
        vec3 position = rayOrigin + rayDirection * rayDistance;
        vec2 scene = sampleScene(position);
        if (scene.x < SURFACE) {
            return vec2(rayDistance, scene.y);
        }
        if (rayDistance > MAXIMUM) {
            break;
        }
        rayDistance += scene.x;
    }

    return vec2(MAXIMUM, 0.0);
}

vec3 estimateNormal(vec3 position)
{
    vec3 step = vec3(0.0001, 0.0, 0.0);
    return normalize(vec3(
        sampleScene(position + step.xyy).x - sampleScene(position - step.xyy).x,
        sampleScene(position + step.yxy).x - sampleScene(position - step.yxy).x,
        sampleScene(position + step.yyx).x - sampleScene(position - step.yyx).x
    ));
}

float shadow(vec3 ro, vec3 rd, float mint, float maxt, float k)
{
    float res = 1.0;
    for (float t = mint; t < maxt; )
    {
        vec2 h = sampleScene(ro + rd * t);
        if( h.x < SURFACE)
        {
            return h.y == MATERIAL_LIGHT ? res : 0.0;
        }
        if (h.y != MATERIAL_LIGHT)
        {
            res = min(res, k * h.x / t);
        }
        t += h.x;
    }
    return res;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4[] lights = vec4[] (
        // vec4(2.0, 5.0, -2.0, 30.0),
        vec4(0.5, 1.0, 1.0, 15.0)
    );
    // lights[0].xz += vec2(cos(iTime), sin(iTime)) * 6.0;
    // lights[1].xz -= vec2(cos(iTime), sin(iTime)) * 4.0;

    vec2 uv = 2.0 * (fragCoord / iResolution.xx) - 1.0;

    vec3 cameraPosition = vec3(0.0, 1.85, -1.45);
    // cameraPosition.z += cos(iTime) * 1.6;
    // cameraPosition.z += 1.65;
    vec3 cameraRay = normalize(vec3(uv, 1.0));

    vec2 target = rayMarch(cameraPosition, cameraRay);
    float distance = target.x;
    vec3 point = cameraPosition + cameraRay * distance;
    vec3 normal = estimateNormal(point);
    float material = target.y;

    float intensity = 0.1;
    vec3 color;

    if (material == 0.0) {
        // Sky
        color = vec3(0.6, 0.7, 1.0);
        intensity = 0.4;
    } else if (material == MATERIAL_LIGHT) {
        color = vec3(1.0, 1.0, 1.0);
         intensity = 1.0;
   } else {
        if (material == 1.0) {
            color = vec3(0.8, 0.8, 0.8);
        }
        if (material == 2.0) {
            color = vec3(0.25, 0.3, 0.4);
        }
        if (material == 3.0) {
            color = vec3(0.85, 0.76, 0.65);
        }
        if (material == 4.0) {
            color = vec3(0.75, 0.81, 0.55);
        }

        for (int n = 0; n < lights.length(); ++n) {
            vec4 light = lights[n];
            vec3 lightToPoint = light.xyz - point;
            vec3 lightDirection = normalize(lightToPoint);
            float lightDistance = length(lightToPoint);
            vec3 start = point + lightDirection * BOUNCE_MARGIN;
            float factor = shadow(start, lightDirection, 0.0, 10.0, 240.0);
            float lightIntensity = (0.5+0.1*sin(3.0*iTime))* max(0.0, dot(normal, lightDirection)) * light.a / lightDistance / lightDistance;
            intensity += lightIntensity * (0.2 + 0.8 * factor);
        }
    }
    intensity = clamp(intensity, 0.0, 1.0);

    color *= intensity;
    fragColor = vec4(color, 1.0);
}

#define r(s) fract(43.*sin(s.x*13.+s.y*78.))

const vec3  eye      = vec3 ( 0, 0, 3 );
const vec3  light    = vec3  ( 0.0, 3.0, 5.0 );
const int   maxSteps = 70;
const float eps      = 0.01;

// Rotation matrix around the X axis.
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Rotation matrix around the Y axis.
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

float length8 ( in vec2 p )
{
    return pow ( pow ( p.x, 8.0 ) + pow ( p.y, 8.0 ), 1.0/ 8.0 );
}

float length8 ( in vec3 p )
{
    return pow ( pow ( p.x, 8.0 ) + pow ( p.y, 8.0 ) + pow ( p.z, 8.0 ), 1.0/ 8.0 );
}

float dBox ( vec3 p, vec3 b )
{
	return length8 ( max ( abs ( p ) - b, 0.0 ) );
}

float smin ( float a, float b, float k )
{
	float res = exp ( -k*a ) + exp ( -k*b );
	return -log ( res ) / k;
}

float dGreenDude (in vec3 q) 
{
    return min(min(min(dBox ( q, vec3 ( 0.5, 0.5, 0.7 )), 
      dBox ( q + vec3( 0.0, 0.0, 0.8), vec3 ( 0.4, 0.4, 0.4 ))),
      dBox ( q + vec3( - 0.7, 0.0, -0.9), vec3 ( 0.2, 0.5, 0.2 ))),
      dBox ( q + vec3( + 0.7, 0.0, -0.9), vec3 ( 0.2, 0.5, 0.2 )));
}

float sdf ( in vec3 p, in mat3 m )
{
   vec3 q = m * p + vec3(cos(iTime), sin(iTime), 0);

   return dGreenDude(q);
}

vec3 trace ( in vec3 from, in vec3 dir, out bool hit, in mat3 m )
{
	vec3	p         = from;
	float	totalDist = 0.0;

	hit = false;

	for ( int steps = 0; steps < maxSteps; steps++ )
	{
		float	dist = sdf ( p, m );

		if ( dist < 0.01 )
		{
			hit = true;
			break;
		}

		totalDist += dist;

		if ( totalDist > 10.0 )
			break;

		p += dist * dir;
	}

	return p;
}

vec3 generateNormal ( vec3 z, float d, in mat3 m )
{
    float e   = max (d * 0.5, eps );
    float dx1 = sdf(z + vec3(e, 0, 0), m);
    float dx2 = sdf(z - vec3(e, 0, 0), m);
    float dy1 = sdf(z + vec3(0, e, 0), m);
    float dy2 = sdf(z - vec3(0, e, 0), m);
    float dz1 = sdf(z + vec3(0, 0, e), m);
    float dz2 = sdf(z - vec3(0, 0, e), m);

    return normalize ( vec3 ( dx1 - dx2, dy1 - dy2, dz1 - dz2 ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 i = fragCoord;
    vec2 j = fract(i*=50./iResolution.x), 
        p1 = i-j+ vec2(2,floor(iTime*20.*fract(sin(i-j).x)));
    i      = abs(j-.5);
    vec4 o =  vec4(r(floor(p1*23.+5.*j))>.5&&i.x<.3&&i.y<.45 ?   1. - r(p1)*
         (2.-dot(i,i)*6.)  :  1.);

    // Normalized pixel coordinates (from 0 to 1)
    bool hit;
	vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5,iMouse.z-.5);
    mat3 m     = rotateX ( 6.0*mouse.y ) * rotateY ( 6.0*mouse.x);
    vec2 scale = 9.0 * iResolution.xy / max ( iResolution.x, iResolution.y ) ;
    vec2 uv    = scale * ( fragCoord/iResolution.xy - vec2 ( 0.5 ) );
	vec3 dir   = normalize ( vec3 ( uv, 0 ) - eye );
    vec4 color = vec4 ( 0, 0, 0, 1 );
    vec3 p     = trace ( eye, dir, hit, m );


	if ( hit )
	{
		vec3  l  = normalize        ( light - p );
        vec3  v  = normalize        ( eye - p );
		vec3  n  = generateNormal   ( p, 0.001, m );
		float nl = max ( 0.0, dot ( n, l ) );
        vec3  h  = normalize ( l + v );
        float hn = max ( 0.0, dot ( h, n ) );
        float sp = pow ( hn, 10.0 );

		color = 0.5*vec4 ( nl ) + 0.5 * sp * vec4 ( 0, 1, 0, 1 );
	}

    // Output to screen
    fragColor = 1.2*color - 0.18*o;
}

