void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = 0.5 * iTime;

    vec2 uv = (1.1 + 0.3 * cos(0.31 * t)) * (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float theta = atan(uv.y, uv.x);

    float x = uv.x + 0.3 * cos(2. * theta + 1.1 * t);
    float y = uv.y + 0.3 * sin(2. * theta + 1.1 * t);

    float d = max(abs(x) / abs(1.5 + y), abs(y) / abs(1.5 + x));
    float d2 = max(abs(x), abs(y));

    float e = step(d, 0.2);
    e *= (1.-5. * d);
    e *= cos(t + 3. * theta) * sin(theta + 2. * t);
    e = 4. * e * (1.-e);
    e *= e;

    float e2 = step(d2, 0.2);
    e2 *= (1.-5. * d2);
    e2 *= cos(t + 3. * theta) * sin(theta + 2. * t);
    e2 = 4. * e2 * (1.-e2);
    e2 *= e2;

    fragColor = vec4(0.5* (1. + cos(uv.x + t)), 0.5 * (1. + sin(uv.y + t)), 1., 1.) * (1. - 4. * abs(e-e2));


}

float Hash221(vec2 p)
{
  p = fract(p*vec2(234.34,456.23));
  p+= dot(p,p+34.32);
  return fract(p.x+p.y);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

        //Control Variables
        float tiling = cos(iTime/2.)*20.+20.;
        float t = iTime;
        float width = .1;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    //Rotation Variables
    float Angle = (3.141592653589*t)/10.;
    float A = cos(Angle);
    float S = sin(Angle);

    //Rotate UV
  uv*=mat2(A,-S,S,A);

  //Scale Uv by Tiling
    vec2 bv = uv*tiling;

    //Make Grid
    vec2 gv = fract(bv)-.5;

    //Set ids
    vec2 id = floor(bv);

    //Random Flipping
    float n = Hash221(id);
    if(n<sin(t/2.)*.5+.5)gv.x*=-1.;

    //Color masks
    float c = length(uv);
    float c2 = length(gv);

    //Get Distances for lines
    float d = abs(abs(gv.x+gv.y)-.5);
    float d2 = length(gv-sign(gv.x+gv.y+0.001)*.5)-.5;

    //SmoothLines
    float s = smoothstep(.01,-.01-1.5/iResolution.y,abs(d)-width); // Change d to d2 for Circular lines

    // visualize
    vec3 col = vec3(s);
    //if(gv.x >.48||gv.y>.48) col += 1.; //Draw Outline of each grid square
    col.r *= sin(c-t); // Animated red Channel
    col.g *= cos(c2+t)/2.; //Animated green channel
    col.rgb -= c/1.; // Create Vignette

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Desmos graph: https://www.desmos.com/calculator/627ebn1ycs
#define drawSDF(dist, col) color = mix(color, col.rgb, smoothstep(unit, 0.0, dist) * col.a)

// https://www.shadertoy.com/view/3lfGD7
float Gamma(in float x) {
    const float pi = 3.14159265359;
    float xx = x > 1.0 ? x : 1.0 - x;
    float sum = 2.506628275635 + 225.525584619175 / (xx + 1.0) - 268.295973841305 / (xx + 2.0) + 80.9030806934622 / (xx + 3.0) - 5.007578639705 / (xx + 4.0) + 0.0114684895435 / (xx + 5.0);
    float xh = xx + 5.65;
    float y = exp((xx + 0.5) * log(xh) - xh) * sum / xx;
    return x > 1.0 ? y : pi / (y * sin(pi * x));
}

// For general r, multiply by r^2
float squircleArea(in float n) {
    float a = Gamma(1.0 + 1.0 / n);
    return 4.0 * a * a / Gamma(1.0 + 2.0 / n);
}

float dfSquircle(in vec2 p, in float r, in float n) {
    return pow(pow(abs(p.x), n) + pow(abs(p.y), n), 1.0 / n) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 8.0;
    float unit = 16.0 / iResolution.y;
    vec3 color = vec3(0.0, 0.169, 0.212);

    // Shift the graph down and to the left side of the screen
    vec2 shift = vec2(4.0 * iResolution.x / iResolution.y, 4.0);
    uv += shift;

    // Grid
    drawSDF(abs(fract(uv.x + 0.5) - 0.5) + 0.25 * unit, vec4(0.0, 0.25, 0.5, 1.0));
    drawSDF(abs(fract(uv.y + 0.5) - 0.5) + 0.25 * unit, vec4(0.0, 0.25, 0.5, 1.0));

    // Draw the function
    vec2 y = vec2(squircleArea(uv.x), 0.0);
    y.y = (squircleArea(uv.x + 0.001) - y.x) / 0.001;
    drawSDF(abs(uv.y - y.x) / sqrt(1.0 + y.y * y.y) - 0.01, vec4(1.0, 0.0, 0.8, 1.0));

    uv -= shift;

    float n = mix(0.5, 10.0, 0.5 + 0.5 * sin(0.5 * iTime));
    float r = 2.0;

    float eps = 0.001;
    float squircle = dfSquircle(uv, r, n);
    squircle /= length((vec2(dfSquircle(uv + vec2(eps, 0.0), r, n), dfSquircle(uv + vec2(0.0, eps), r, n)) - squircle)) / eps;

    uv += shift;
    drawSDF(abs(uv.x - n), vec4(1.0, 0.0, 0.0, 1.0));
    drawSDF(length(uv - vec2(n, squircleArea(n))) - 0.1, vec4(1.0, 0.8, 0.0, 1.0));

    drawSDF(squircle, vec4(0.0, 1.0, 0.0, 0.25));
    drawSDF(abs(squircle), vec4(0.0, 1.0, 0.0, 1.0));

    fragColor = vec4(color, 1.0);
}

