vec4 getHitColor(vec3 ro, vec3 rd, float t, vec3 lightPos) {
    vec3 hit = ro + rd * t;
    vec3 norm = normal(hit);

    vec3 light = lightPos - hit;
    float lightDist = max(length(light), .001);
    float atten = 1. / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    light /= lightDist;

    float occ = occlusion(hit, norm);

    float dif = clamp(dot(norm, light), 0.0, 1.0);
    dif = pow(dif, 4.) * 2.;
    float spe = pow(max(dot(reflect(-light, norm), -rd), 0.), 8.);

    vec3 color = mapColor(hit, t) * (dif + .35  + vec3(.35, .45, .5) * spe) + vec3(.7, .9, 1) * spe * spe;

    return vec4(color, atten * occ);
}

vec3 getColor(vec2 uv, vec3 ro, vec3 dir, vec3 lightPos) {
    vec3 fwd = normalize(dir);
    vec3 rgt = normalize(vec3(fwd.z, 0, -fwd.x));
    vec3 up = (cross(fwd, rgt));

    vec3 rd = normalize(fwd + FOV*(uv.x*rgt + uv.y*up));

    float t = rayMarch(ro, rd, MAX_RAY);

    vec3 outColor = vec3(.0);

    if(t < FAR) {
        vec3 hit = ro + rd * t;
        vec3 norm = normal(hit);
        vec4 color = getHitColor(ro, rd, t, lightPos);

        vec3 ref = reflect(rd, norm);
        float refQ = mapRef(hit, t);
        float t2 = refQ <= .001 ? .0 : rayMarch(hit + ref * .1, ref, MAX_REF);
        vec4 color2 = refQ <= .001 ? vec4(.0) : getHitColor(hit + ref * .1, ref, t2, lightPos);

        outColor = (color.xyz * (1. - refQ) + refQ * color2.xyz * color2.w) * color.w;
    }

    outColor = mix(min(outColor, 1.), vec3(.0), 1. - exp(-t*t/FAR/FAR*10.));

    return outColor;
}

#define CAMERA_SPEED .5

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy / iResolution.xy - 0.5);
    uv.x *= iResolution.x / iResolution.y;

    vec3 pos = vec3(1. + 3. * cos(iTime * CAMERA_SPEED), 1. + sin(iTime * CAMERA_SPEED) * .2, 3. * sin(iTime * CAMERA_SPEED));
    vec3 dir = vec3(3. * cos(iTime * CAMERA_SPEED + PI), -1.5, 3. * sin(iTime * CAMERA_SPEED + PI));
    vec3 light = vec3(.0, 4., .0);

    vec3 c = getColor(uv, pos, dir, light);

    fragColor = vec4(sqrt(c),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float s = 16.0;
    // Time varying pixel color
    vec3 col = 4.0*cos(iTime+uv.xyx*vec3(s*sin(iTime*0.01),s*cos(iTime*0.02),s*cos(uv.x+iTime*0.06)));
       col = abs(col);
    // Output to screen
    fragColor = vec4(vec3(col.x*col.y*col.z),1.0);
}

void wave(inout float x, inout float y, inout float z, float T, int octaves, float a)
{
	float R=8.;
	float S=.03;
	float W=-.05;
	#define RRRRS R*=.72;S*=1.27;W*=1.21;
	for(int s=0;s<octaves;s++)
	{
		float da=1.8+(sin(T*0.021)*0.1+.41*sin(float(s)*.71+T*0.02))*a;
		float dx=cos(da);
		float dy=sin(da);
		float t=-dot(vec2(x-320.,y-240.),vec2(dx,dy));
		float sa=sin(T*W+t*S)*R;
		float ca=cos(T*W+t*S)*R;

		x-=ca*dx*2.;
		y-=ca*dy*2.;
		z-=sa;
		RRRRS
	}
}

float chkr(vec2 p, float t)
{
    vec2 fxy=vec2(p.x,p.y);
  	fxy=abs(fract((fxy+0.5)/2.0)-0.5)-0.25;
    fxy=clamp(fxy*t+0.5,0.0,1.0);
    float f=mix(fxy.x,1.0-fxy.x,fxy.y);
    return f;
}

float tex(vec2 p)
{
    //return chkr(p*.1,100.);
    float z=0.;
    p=vec2(p.x+p.y*.3,p.y-p.x*.3);
    p.x=p.x+17.;
    p.y=p.y-150.;
    wave(p.x, p.y, z, 12.21-iTime*.01,6,10.);
    p.x*=18.;
    p.y*=19.;
    return sin(p.y*0.04+.1*z)*z*.1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*vec2(640.,480.);
    vec2 uv0=uv;
    float z=0.;
    wave(uv.x, uv.y, z, iTime*20., 17,1.);
    z=z+22.;
    z*=0.018;
    vec3 col = vec3(.3+z*1.2,.2+z*.9,.1+z*.6);
    uv=uv0+0.5*(uv-uv0);
    //col.rgb*=.9+.1*vec3(.75+.25*tex(vec2(uv0.x,uv.y+iTime*10.)));
    fragColor = vec4(col,1.0);
}

