float SmoothMax (float a, float b, float r);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Noisefv2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

float tCur, dstFar, tWav;
int idObj;
const int idFace = 1, idEdge = 2;
const float pi = 3.1415927;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

vec3 DodecSym (vec3 p)
{
  vec2 csD;
  p.yz = Rot2D (p.yz, 0.067 * pi);
  csD = sin (0.5 * atan (2.) + vec2 (0.5 * pi, 0.));
  p.xz = Rot2Cs (vec2 (p.x, abs (p.z)), csD);
  p.xy = Rot2D (p.xy, -0.1 * pi);
  p.x = - abs (p.x);
  for (int k = 0; k <= 3; k ++) {
    p.zy = Rot2Cs (p.zy, vec2 (csD.x, - csD.y));
    p.y = - abs (p.y);
    p.zy = Rot2Cs (p.zy, csD);
    if (k < 3) p.xy = Rot2Cs (p.xy, sin (-2. * pi / 5. + vec2 (0.5 * pi, 0.)));
  }
  p.xy = sin (mod (atan (p.x, p.y) + pi / 5., 2. * pi / 5.) - pi / 5. +
     vec2 (0., 0.5 * pi)) * length (p.xy);
  p.xz = - vec2 (abs (p.x), p.z);
  return p;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, a1, a2, s;
  dMin = dstFar;
  q = DodecSym (p);
  a1 = 0.5 * acos (-1. / sqrt (5.));
  a2 = 0.5 * acos (- (80. + 9. * sqrt (5.)) / 109.);
  s = abs (dot (q.yz, sin (a1 - a2 + vec2 (0., 0.5 * pi))) + 2.);
  d = SmoothMax (s - 0.07, min (dot (q.yz, sin (a1 - pi + vec2 (0., 0.5 * pi))),
     dot (q.xy, sin (pi / 5. + vec2 (0.5 * pi, 0.)))) - 0.03, 0.02);
  DMIN (idEdge);
  d = s - 0.02;
  DMIN (idFace);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0005, -0.0005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float WaveHt (vec2 p)
{
  mat2 qRot;
  vec4 t4, v4;
  vec2 t2;
  float wFreq, wAmp, ht;
  qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  wFreq = 1.;
  wAmp = 1.;
  ht = 0.;
  for (int j = 0; j < 4; j ++) {
    p *= qRot;
    t4 = (p.xyxy + tWav * vec2 (1., -1.).xxyy) * wFreq;
    t4 += 2. * vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw)).xxyy - 1.;
    t4 = abs (sin (t4));
    v4 = (1. - t4) * (t4 + sqrt (1. - t4 * t4));
    t2 = 1. - sqrt (v4.xz * v4.yw);
    t2 *= t2;
    t2 *= t2;
    ht += wAmp * dot (t2, t2);
    wFreq *= 2.;
    wAmp *= 0.5;
  }
  return ht;
}

vec4 WaveNfH (vec2 p)
{
  vec3 v;
  vec2 e;
  e = vec2 (0.01, 0.);
  p *= 8.;
  for (int j = VAR_ZERO; j < 3; j ++) v[j] = WaveHt (p + ((j == 0) ? e.yy : ((j == 1) ? e.xy : e.yx)));
  return vec4 (- normalize (vec3 (0.4 * (v.x - v.yz), e.x)), v.x);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, hn4;
  vec3 col, vn, ltDir;
  vec2 u;
  float dstObj, rAz, rEl;
  tWav = 0.2 * tCur;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == idEdge) {
      col4 = vec4 (0.95, 0.95, 1., 0.2);
      vn = VaryNf (32. * ro, vn, 1.);
    } else if (idObj == idFace) {
      rAz = atan (ro.x, ro.z);
      rEl = asin (ro.y / length (ro));
      u = vec2 (rAz + pi, tan (2. * atan (0.5 * rEl))) / (2. * pi);
      hn4 = mix (WaveNfH (u), WaveNfH (u - vec2 (1., 0.)), u.x);
      vn = StdVuMat (rEl, rAz) * normalize (hn4.xyz);
      col4 = vec4 (mix (vec3 (1., 1., 0.3), vec3 (1., 0.4, 0.), smoothstep (0.3, 1., hn4.w)), 0.2);
    }
    ltDir = normalize (- ro);
    col = col4.rgb * (0.1 + 0.9 * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
  }
  return clamp (col, 0., 1.);
}

#define AA  0

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv;
  float el, az, asp, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az -= 0.01 * pi * tCur;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -1.8);
  zmFac = 1.5;
  dstFar = 20.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = vec2 (2. * tan (0.5 * atan (uv.x / (asp * zmFac))) * asp * zmFac, uv.y);
    rd = vuMat * normalize (vec3 (uvv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  }
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

vec3 rotate(vec3 pos, vec2 rotation) {
    vec2 m4 = vec2(length(pos.xz), length(pos.xyz));

    vec2 angle = vec2(acos(pos.x/m4.x), asin(pos.z/m4.y));
    float l = length(pos);
    vec3 poss = pos;
    float xx = poss.x*cos(rotation.y)+poss.z*sin(rotation.y);
    float yy = poss.y;

    float zz = -pos.x*sin(rotation.y)+poss.z*cos(rotation.y);

    return vec3(xx, yy, zz);
}

vec3 fractal(vec3 coords, int iterations, float degreee, float xRot) {
    float pi = 3.1415926;
    float degree = 7.0;
    vec3 coordsMod = coords.xyz;
    for (int i = 0; i < iterations; i++) {
        vec3 UV = vec3(coordsMod.xyz);
        UV = vec3(1.0*length(UV), abs(UV.y)/UV.y*acos(UV.x/length(UV.xy))+xRot, abs(UV.z)/UV.z*asin(abs(UV.z)/length(UV)));
        coordsMod = coords.xyz+pow(UV.x, degree)*vec3(cos(degree*UV.y)*cos(degree*UV.z), sin(degree*UV.y)*cos(degree*UV.z), sin(degree*UV.z));

    }
    if (length(coordsMod) < 2.0) {
        return vec3(0.3, 0.5, 1.0);
        //return vec3(1.0);
    } else {
        return vec3(0.0);
    }


}

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 rayCast(vec3 dir, vec3 pos, float xRot, vec2 coord) {
    float density = 500.0;
    float anisotropy = 0.0;
    float pi = 3.1415926;
    vec3 o = vec3(0.0);
    float stepSize = 0.02;
    float lightingStepSize = 0.05;
    vec3 p = pos+dir*stepSize*rand(coord.xy);
    vec3 n = vec3(0.0);
    vec3 lightingDir = vec3(cos(-xRot+iTime*0.5), sin(-xRot+iTime*0.5), -sin(iTime*2.718/4.0));
    lightingDir = lightingDir/length(lightingDir);

    float a = dot(dir, dir);
    float b = 2.0*dot(dir, pos);
    float c = dot(pos, pos) - pow(2.0, 2.0);
    float discriminant = b*b-4.0*a*c;

    if (discriminant > 0.0) {
        vec3 inPos = (-b - sqrt(discriminant))/(2.0*a)*dir+pos;
        vec3 outPos = (-b + sqrt(discriminant))/(2.0*a)*dir+pos;
        p = inPos+dir*stepSize*rand(coord.xy);
        for (float j = 0.0; j < length(inPos-outPos); j += stepSize) {
            p += dir*stepSize;
            vec3 pp = p+lightingStepSize*lightingDir*rand(coord.xy);
            vec3 l = vec3(0.0);
            vec3 m;
            if (length(p) < 2.0) {
                m = fractal(p, 4, 8.0, xRot+iTime*0.0)*stepSize;
                o += m;
                if (length(o) > 1.0) {
                    j = 2.0 + length(pos);
                }
            } else {
                m = vec3(0.0);
            }
            if (length(m) > 0.0) {
                for (vec3 k = p; length(pp) < 2.0; pp -= lightingStepSize*lightingDir) {
                    l += fractal(pp, 4, 8.0, xRot)*stepSize;
                    if (length(l) > 1.0) {
                        pp = vec3(2.0);

                    }
                }
            }

            vec3 q = vec3(0.0);
            q.x = stepSize*m.x*density*pow(2.0, -density*stepSize*length(o.x))*pow(2.0, -density*stepSize*length(l.x));
            q.y = stepSize*m.y*density*pow(2.0, -density*stepSize*length(o.y))*pow(2.0, -density*stepSize*length(l.y));
            q.z = stepSize*m.z*density*pow(2.0, -density*stepSize*length(o.z))*pow(2.0, -density*stepSize*length(l.z));
            float cloudValAdjust = 0.3;
            float cloudVal = 0.0;
            float minV = 0.0;
            float maxV = 1.0;
            anisotropy = clamp((length(o*cloudValAdjust)/(length(o*cloudValAdjust)+1.0)-minV)/(maxV-minV), 0.0, 1.0)*-cloudVal+clamp(1.0-(length(o*cloudValAdjust)/(length(o*cloudValAdjust)+1.0)-minV)/(maxV-minV), 0.0, 1.0)*cloudVal;
            if (anisotropy >= 0.0) {
                q*=(1.0-anisotropy+pow(2.7, -anisotropy/(1.0-anisotropy)*(1.0-dot(-dir, lightingDir))));
            } else {
                q*=(1.0+anisotropy+pow(2.7, anisotropy/(1.0+anisotropy)*(1.0-dot(dir, lightingDir))));
            }
            n.x+=q.x;
            n.y+=q.y;
            n.z+=q.z;
        }
    }
    return n;

    //return vec3(dot(dir, lightingDir));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
