    vec3 camPos = rotate(vec3(r, 0.0, 0.0), -vec2(-iTime*0.2, -s*(mouse.y+pi/2.0)));

    vec3 camDir = rotate(vec3(-1.0, dof*x, dof*y), vec2(iTime*0.2, s*(mouse.y+pi/2.0)));

