#define PI 3.141592653589

float Xor(float a, float b)
{
    return a*(1.-b)+b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

//Control Variables
float t = iTime;
float tiling = 4.;
float tiles = 4.;
float r = .3;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    //Rotation variables
    float A = PI*t/10.;
    float e = cos(A);
    float u = sin(A);

    //Rotate uv using rotation matrice
    uv *= mat2(e,-u,u,e);

    //scale uv
    vec2 bv = uv*tiling;

    //create grid
    vec2 gv = fract(bv)-.5;

    //make ids
    vec2 id = floor(bv);
    vec2 id2 = fract(id/(tiles))-.5;

    //Make Circles
    float d = length(uv);
    float d2 = length(gv);
    float d3 = length(id);

    //Smooth Circles
    float c = smoothstep(r,r-1.5/iResolution.y,d);
    float c2 = smoothstep(r,r-1.5/iResolution.y,d2);
    float q = 0.;

    //Take in account circles adjacent to each other for overlapping
    for(float y=-1.;y<=1.;y++)
    {
        for(float x=-1.;x<=1.;x++)
        {
            vec2 offs = vec2(x,y);
            float d5 = length(gv - offs);
            float d4 = length(id2+offs)*.3;

            r =  mix(.3,1.5,sin(t-d4)*.5+.5); 
            q =Xor(q, smoothstep(r,r-1.5/iResolution.y,d5));

        }
    }




   //Visualize
    vec3 col = vec3(0.);
    col += q;
    col.r *= sin(t-d)*2.;
    col.g *= cos(t+d2)*3.;
    col.b *= cos(col.r*col.g)*.5+.5;

    // Output to screen
    fragColor = vec4(col,1.0);
}

