// Signed distance and line segment

// Segment:              https://www.shadertoy.com/view/3tdSDj
// and many more here:   http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm

float line_segment(in vec2 p, in vec2 a, in vec2 b) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float normalizedX = (p.x - a.x) / (b.x - a.x);
    float lineCenterY = mix (a.y, b.y, smoothstep(0.0f, 1.0f, normalizedX));
    return lineCenterY - p.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 pos = (fragCoord - iResolution.xy * .5) / iResolution.y;
	float zoom = 2.5;
	pos *= zoom;

	vec2 v1 = cos(iTime + vec2(0.,5.));
	vec2 v2 = cos(iTime + vec2(0.,5.) + 3.1);
	float thickness = .2 * (.5 + .5 * sin(iTime * 1.));

	float d = line_segment(pos, v1, v2) - thickness;

	vec3 color = vec3(1.) - sign(d) * vec3(0., 0., 0.);
	color *= 1.5 - exp(.5 * abs(d));
	color *= .5 + .3 * cos(120. * d);
	color = mix(color, vec3(1.), 1. - smoothstep(.0, .015, abs(d)));

	fragColor = vec4(color, 1.);
}

const int COLORS_SIZE = 4;
const vec3 ERR_COLOR = vec3(0.0, 0.0, 0.0);

const int SAMPLES_S = 4;
const int N_SAMPLES = SAMPLES_S * SAMPLES_S;

const float M_PI = 3.1415926535897932384626433832795;

float function(float x, float y, float t)
{
    float t2 = t / 2.0;
    float t5 = t / 4.0;
    float st2 = sin(t2);
    float st5 = sin(t5);
    float ct5 = cos(t5);
    float tt5 = tan(t5);

    //return (x - st5 * 1.5*y) / (sin(st2 * x + y) * tan(st5 * x - y) + 3.0*sin(ct5 * x + y) + 0.1*st2*y);
    //return (x - st5 * y) / (sin(x + st2 * y) * tan(st5 * x - y) + 4.0*sin(x + y) + 0.1*st2*y);
    //return (x - y) / (sin(x + y) * tan(t7 * x - y) + 4.0*sin(x + y) + 0.1*t2 * y);

    //return (x - y) / (sin(x + y) * tan(x - y) + 4.0*sin(x + y) + 0.1*y);
    //return (x - y) / (sin(t2 + x + y) * tan(x - y) + 4.0*sin(x + y) + 0.1*y);
    return (tt5 + x - st5 * 1.5*y) / (sin(t2 + x + y) * tan(st5 * x - y) + 4.0*sin(x + y) + 0.1*st2*y);
    //return tt5 * (x - 1.5*y) / (sin(x + y) * tan(x - y) + st5 * 3.0*sin(x + y) + 0.1*y);
}

vec2[N_SAMPLES] supersampling(vec2 coord, float pixSize)
{
    float sampleSize = pixSize / float(SAMPLES_S);
    vec2 leftUp = coord - pixSize / 2.0 + sampleSize / 2.0; 

    vec2 samples[N_SAMPLES];
    for (int i = 0; i < SAMPLES_S; i++)
    {
        for (int j = 0; j < SAMPLES_S; j++)
        {
            vec2 sampl = leftUp + vec2(sampleSize * float(j), sampleSize * float(i)); 
            samples[i * SAMPLES_S + j] = sampl;
        }
    }
    return samples;    
}

float atan2(float y, float x)
{
    if (x > 0.0)
    {
        return atan(y / x);
    }
    else if (x < 0.0 && y >= 0.0)
    {
        return atan(y / x) + M_PI;
    }    
    else if (x < 0.0 && y < 0.0)
    {
        return atan(y / x) - M_PI;
    }
    else if (x == 0.0 && y > 0.0)
    {
        return M_PI / 2.0;
    }
    else if (x == 0.0 && y < 0.0)
    {
        return -M_PI / 2.0; 
    }
    return 0.0; // undefined
}

// transform classic uv into carthesian coordinates and use defined domains
vec2 uvToCarthesian(vec2 uv, vec2 xDomain, vec2 yDomain)
{
    float x = xDomain.x + uv.x * (xDomain.y - xDomain.x);
    float y = yDomain.x + uv.y * (yDomain.y - yDomain.x);
    return vec2(x, y);
}

// transform quadrants into polar coordinates and use defined domains
vec2 quadToPolar(vec2 quad, vec2 rDomain, vec2 phiDomain)
{   
    float r = sqrt(dot(quad, quad));
    float extR = r * (rDomain.y - rDomain.x) + rDomain.x;
    // Normalize phi and revert
    float phi = 1.0 - abs(atan2(quad.y, quad.x)) / M_PI; 
    float extPhi = phi * (phiDomain.y - phiDomain.x) + phiDomain.x;
    return vec2(extR, extPhi);
}

vec3 getColor(float value, vec2 range, vec4 colors[COLORS_SIZE])
{
    // Clamp to range
    value = clamp(value, range.x, range.y);

    float len = range.y - range.x; 
    float norm = (value - range.x) / len;

    // Get two closest colors
    vec3 color = ERR_COLOR;
    for (int i = 0; i < COLORS_SIZE-1; i++)
    {
        vec4 curr = colors[i];
        vec4 next = colors[i+1];
        if (curr.w <= norm && norm <= next.w)
        {
            float btw = (norm - curr.w) / (next.w - curr.w);
            color = mix(curr.xyz, next.xyz, btw);
            break;
        }
    }
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Function domain
    const vec2 xDomain = vec2(0.0, 50.0);
    const vec2 yDomain = vec2(-0.5, 22.0);

    // Function range
    const vec2 range = vec2(-10.0, 1.75);

    // Colors
    vec4 colors[COLORS_SIZE] = vec4[](
        vec4(0.0, 0.0, 0.0, 0.0),
        vec4(0.95, 0.01, 0.218, 0.15),
        vec4(0.45, 0.0, 0.8, 0.65),
        vec4(0.0, 0.0, 0.0, 1.0)
    );

    // uv - normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    uv.y = 1.0 - uv.y;

    // maxUv - one coordinate with maximal resolution is normalized other is trimmed
    // quadrants - derived from maxUv - point (0.0, 0.0) is in the middle of the picture
    float maxRes = max(iResolution.x, iResolution.y);
    vec2 qRes = iResolution.xy / maxRes;
    vec2 maxUv = fragCoord / maxRes;
    maxUv.y = qRes.y - maxUv.y;
    vec2 quadrants = 2.0 * maxUv - qRes;
    float quadPixSize = 2.0 / maxRes;

    // Supersampling
    vec2 samples[] = supersampling(quadrants, quadPixSize);

    vec3 totalColor = vec3(0.0);
    for (int i = 0; i < N_SAMPLES; i++)
    {
        // Calculate coord
        vec2 coord = quadToPolar(samples[i], xDomain, yDomain);

        float value = function(coord.x, coord.y, iTime);

        totalColor = totalColor + getColor(value, range, colors);
    }
    vec3 avgColor = totalColor / float(N_SAMPLES);    

    // Output to screen
    fragColor = vec4(avgColor, 1.0);
}

// I made some more variants of iq/Hooper simplex gradient

// iq's looped simplex gradient, loop idea by Thomas Hooper
// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm "An Important Implementation Detail"

float dscene(vec3 q);

#define IZERO min(0, iFrame)

// WIP spalmer variant of Thomas Hooper's looped variant of iq's simplex gradient
// works, not super efficient though with extra multiply by e.w etc.
vec3 gscene4(vec3 q, float h)
{
	vec3 n = vec3(0); vec4 e = vec4(1,1,1,-1);
	for (int i = IZERO + 4; i-- > 0; e = e.yzwx)
		n -= dscene(q - h * e.w * e.xyz) * e.w * e.xyz;
	return n;
}

// these were originally in March 3D https://shadertoy.com/view/tlGXDK

// spalmer variant of Thomas Hooper's looped variant of iq's simplex gradient
vec3 gscene3(vec3 q, float h)
{
	vec3 n = vec3(0);
	for (int i = IZERO; i < 4; ++i) {
		vec3 e = vec3((ivec3(i+3, i, i+i)&2) - 1);
		n += dscene(q + e * h) * e;
	}
	return n;
}

// a variation of iq/Hooper's simplex gradient made by Clément Baticle (a.k.a. Klems):
vec3 gscene2(vec3 p, float h) // for function f(p)
{
	vec3 n = vec3(0);
	for (int i=IZERO; i<4; ++i)
	{
		vec3 e = (2.*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.);
		n += e * dscene(p + e*h);
	}
	return n;
}

// unlooped variation for comparison:

// iq's simplex gradient, unlooped, so stresses compiler inlining scene:
// I took from mandelbulb https://shadertoy.com/view/ltfSWn
vec3 gscene1(vec3 q, float h)
{
	vec2 e = h * vec2(1,-1);
	return e.xyy * dscene(q + e.xyy) + 
	       e.yyx * dscene(q + e.yyx) + 
	       e.yxy * dscene(q + e.yxy) + 
	       e.xxx * dscene(q + e.xxx);
}

// standard central difference gradient for comparison
vec3 gscene0(vec3 q, float h)
{
    vec2 e = vec2(h * sqrt(3.), 0);  // sqrt(3.) to match lack of sqrt(1./3.) in others
    return .5 * (vec3(
       dscene(q + e.xyy),
       dscene(q + e.yxy),
       dscene(q + e.yyx)
       ) - vec3(
       dscene(q - e.xyy),
       dscene(q - e.yxy),
       dscene(q - e.yyx)
       ));
}
// you can tell on left edge of sphere bottom that it
// works differently than the simplex gradient does


float dscene(vec3 q)
{
    return min(min(length(q) - 2., 6. - length(q)), q.y + 1.)
        // any ideas to exhibit the technique better? maybe some patterned surface?
        + dot(sin(q*16.), vec3(1)) * .015
    ;
}

