// fork of Fabrice's helix https://shadertoy.com/view/XsdBW8

void mainImage(out vec4 O, vec2 p)
{
    vec3 R = iResolution, r = R, q = 5./r.yyz;
    // TODO maybe rotating the camera would allow witnessing artifacts on all sides
    r = normalize(vec3(.5*r.xy - p, r.y) / r.y); // strangely backward
    q -= 3. * r;
    for (int i = 96 + IZERO; i-- > 0 && abs(O.a) > .01;
         q -= r * (O.a = dscene(q)));
    // really precision shouldn't be constant but should depend on the distance to camera etc.
    // here just change w time so can see the effect it has.
    // you can only really notice the gradient methodology when precis is large,
    // otherwise it tends to just look "correct".
    float precis = (1. - abs(sin(iTime))) * .1 + 1e-2; // * (1. + blur); // not too small, beware inaccuracies in far distance
    // normalized gradient (normal) of scene at p
    float split = sin(.3*iTime) * .5 + .5 - p.x / R.x; //r.x - .5*sin(.3*iTime); // splitscreen
    vec3 g = split < 0. // compare
        ? gscene3(q, precis) // right
        : gscene0(q, precis) // left
    , n = normalize(g);
    O = vec4(1);
//    O.rgb *= dot(n, vec3(sqrt(1./3.))); // lit
    O.rgb *= n * .5 + .5;
    O.rgb *= 1. - exp2(-4.* R.y * abs(split));
//    n *= 1.001; //.999; // TEST
    O.gb *= exp2(-5e3 * abs(1. - length(n))); // detect unnormalized normals
}

