#define TILE_LENGTH ((1. - 2. * ( MARGIN + PADDING)) / 3.)
#define LEFT_CENTER   vec2(MARGIN + .5 * TILE_LENGTH                               , MARGIN + .5 * TILE_LENGTH)
#define CENTER_CENTER vec2(MARGIN + .5 * TILE_LENGTH +       PADDING + TILE_LENGTH , MARGIN + .5 * TILE_LENGTH)
#define RIGHT_CENTER  vec2(MARGIN + .5 * TILE_LENGTH + 2. * (PADDING + TILE_LENGTH), MARGIN + .5 * TILE_LENGTH)
#define TEXT_WIDTH   (0.068)
#define TEXT_START_Y (2. * MARGIN + TILE_LENGTH)
#define TEXT_START_X (.3)
// gaussian
#define SIGMA_MIN .75
#define SIGMA_MAX 3.
#define GAUSSIAN_CONST_GAIN 0.3989422804  // 1./sqrt(2*PI)
#define GAUSSIAN_TRANSITION_PERIOD 3.8
// carrier
#define CARRIER_WAVELENGTH_MIN .2
#define CARRIER_WAVELENGTH_MAX .8
#define CARRIER_WAVELENGTH_PERIOD 3.8
#define CARRIER_ROTATION_PERIOD 20.


vec3 displayGamma(vec3 rgb) {
  // gamma(sRGB)
  // https://knowledge.shade3d.jp/knowledgebase/%E3%82%AC%E3%83%B3%E3%83%9E2-2%E3%81%A8srgb%E3%81%AE%E9%81%95%E3%81%84
  vec3 val1 = 12.92 * rgb;
  vec3 val2 = vec3(pow(rgb.x, 1.0 / 2.2),pow(rgb.y, 1.0 / 2.2),pow(rgb.z, 1.0 / 2.2)) * 1.055 - 0.055;
  return (1.0 - step(0.0031308,rgb)) * val1 + step(0.0031308,rgb) * val2;
}

float errorCheckUV(vec2 checkUV){
  return float((checkUV.x < .0)||(checkUV.y < .0)||(checkUV.x > 1.)||(checkUV.y > 1.));
}

vec2 errorCheckUV(vec2 checkUV, vec2 returnUV){
  float errorFlg = errorCheckUV(checkUV);
  return step(0.1,errorFlg) * (-1.,-1.) + (1.0 - step(0.1,errorFlg)) * returnUV;
}

float errorCheckLocalUV(vec2 checkUV){
  return float((abs(checkUV.x) > .5 * TILE_LOCAL_LENGTH)||(abs(checkUV.y) > .5 * TILE_LOCAL_LENGTH));
}

vec2 errorCheckLocalUV(vec2 checkUV, vec2 returnUV){
  float errorFlg = errorCheckLocalUV(checkUV);
  return step(.1, errorFlg) * (-1. * TILE_LOCAL_LENGTH, -1. * TILE_LOCAL_LENGTH) + step(.1, 1. - errorFlg) * returnUV;
}

vec2 uv2LocalUV(vec2 uv, vec2 centerPoint){
  vec2 localUV = TILE_LOCAL_LENGTH * vec2((uv.x - centerPoint.x)/ TILE_LENGTH, 
                                          (uv.y - centerPoint.y)/ TILE_LENGTH);
  return errorCheckLocalUV(localUV, localUV);
}

float waveValue(float period,float  minVal,float  maxVal){
  return (cos(2. * PI * iTime / period) + 1.) * .5 * (maxVal - minVal) + minVal;
}

vec2 localUV2uv(vec2 localUV, vec2 startPoint){
  vec2 gainedLocalUV = vec2(localUV.x * TILE_LENGTH, 
                            localUV.y * TILE_LENGTH);
  vec2 uv = vec2((gainedLocalUV.x + startPoint.x) , 
                 (gainedLocalUV.y + startPoint.y) );
  return errorCheckUV(localUV, uv);
}

vec3 gaussian(vec2 uv, float sigma){
  float channelValue = GAUSSIAN_CONST_GAIN * exp(-dot(uv,uv)/(2. * sigma * sigma)); // / sigma; // Here I don't apply normalization because of visibility.
  return vec3(channelValue, channelValue, channelValue);
}

vec3 carrier(vec2 uv, float carrier_wavelength, float rotation_angle){
  vec2[2] rotation_matrix = vec2[](
    vec2(cos(rotation_angle),-sin(rotation_angle)),
    vec2(sin(rotation_angle), cos(rotation_angle))
  );
  vec2 rotatedUV = vec2(dot(rotation_matrix[0],uv),
                        dot(rotation_matrix[1],uv));
  float channelValue = (cos(rotatedUV.x / carrier_wavelength) ) ;
  return vec3(channelValue, channelValue, channelValue);
}

float lineMask(vec2 uv, vec2 linePoint1,vec2 linePoint2, float threshold){
  // rotate line to y-axis
  vec2 v1 = (linePoint1 - linePoint2);
  float lineLength = sqrt(dot(v1,v1));
  v1 = v1 / lineLength;
  float theta = atan(v1.x, v1.y);
  vec2[2] rotateMat = vec2[](
    vec2(cos(theta), -sin(theta)),
    vec2(sin(theta),  cos(theta))
  );
  vec2 rotatedUV = vec2((dot(rotateMat[0], (uv - linePoint2))),
                        (dot(rotateMat[1], (uv - linePoint2))));
  return float(
               (abs(rotatedUV.x ) < threshold)&&
               (rotatedUV.y       > -threshold)&&
               (rotatedUV.y       < lineLength + threshold)
               );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  // const
  float sigma = waveValue(GAUSSIAN_TRANSITION_PERIOD, SIGMA_MAX, SIGMA_MIN);
  float carrier_wavelength = waveValue(CARRIER_WAVELENGTH_PERIOD, CARRIER_WAVELENGTH_MAX, CARRIER_WAVELENGTH_MIN);
  float rotation_angle = mod(2. * PI * iTime / CARRIER_ROTATION_PERIOD, 2. * PI);

  // normalize position(x,y:.0~1.0)
  vec2 uv = fragCoord.xy/ iResolution.x;

  // get local positions
  vec2 leftUV = uv2LocalUV(uv, LEFT_CENTER);
  vec2 centerUV = uv2LocalUV(uv, CENTER_CENTER);
  vec2 rightUV = uv2LocalUV(uv, RIGHT_CENTER);

  // draw gaussian (center)
  vec3 leftGaussianRegion = gaussian(leftUV, sigma);
  // draw gaussian (center)
  vec3 centerCarrierRegion = carrier(centerUV, carrier_wavelength, rotation_angle);

  // draw gaussian (center)
  vec3 rightGaussianRegion = gaussian(rightUV, sigma);
  vec3 rightCarrierRegion = carrier(rightUV, carrier_wavelength, rotation_angle);

  // draw left side(image(iChannel0))
  float leftMask = 1. - errorCheckLocalUV(leftUV);
  float centerMask = 1. - errorCheckLocalUV(centerUV);
