                      lineMask(uv, vec2(.07   + TEXT_START_X, .025 + TEXT_START_Y),
                                   vec2(.075  + TEXT_START_X, .025 + TEXT_START_Y), .003) +
                      //// A
                      lineMask(uv, vec2(.055  + TEXT_WIDTH + TEXT_START_X, .05 + TEXT_START_Y),
                                   vec2(.075  + TEXT_WIDTH + TEXT_START_X, .05 + TEXT_START_Y), .003) + 
                      lineMask(uv, vec2(.055  + TEXT_WIDTH + TEXT_START_X, .01 + TEXT_START_Y),
                                   vec2(.055  + TEXT_WIDTH + TEXT_START_X, .05 + TEXT_START_Y), .003) + 
                      lineMask(uv, vec2(.075  + TEXT_WIDTH + TEXT_START_X, .01 + TEXT_START_Y),
                                   vec2(.075  + TEXT_WIDTH + TEXT_START_X, .05 + TEXT_START_Y), .003) + 
                      lineMask(uv, vec2(.055  + TEXT_WIDTH + TEXT_START_X, .03 + TEXT_START_Y),
                                   vec2(.075  + TEXT_WIDTH + TEXT_START_X, .03 + TEXT_START_Y), .003) +
                      //// B
                      lineMask(uv, vec2(.056  + 2. * TEXT_WIDTH + TEXT_START_X, .05  + TEXT_START_Y),
                                   vec2(.075  + 2. * TEXT_WIDTH + TEXT_START_X, .036 + TEXT_START_Y), .003) + 
                      lineMask(uv, vec2(.055  + 2. * TEXT_WIDTH + TEXT_START_X, .01  + TEXT_START_Y),
