const int MAX_STEPS = 100;
const float MAX_DIST = 50.0;
const float MIN_DIST = 0.001;
const float PI = 3.1415;

mat2 rot(float r) {
    float s = sin(r), c = cos(r);
    return mat2(c, -s, s, c);
}

float rand1(vec3 p) {
    return fract(sin(dot(p, vec3(12.9898, 78.233, 42.1581))) * 43758.5453123);
}

float sdTorus(vec3 p, float t) {
  return length(vec2(length(p.xz) - t, p.y));
}

float sdTruchet(vec3 p) {
    return min(min(sdTorus(p - vec3(0.5, 0.0, 0.5), 0.5), sdTorus(p.yxz + vec3(0.5, 0.0, 0.5), 0.5)), sdTorus(p.yzx + vec3(-0.5, 0.0, 0.5), 0.5));
}

float map(vec3 p) {
    vec3 i = floor(p);
    vec3 m = p - i - 0.5;
    m.xz *= rot(floor(rand1(i) * 4.0) * PI * 0.5);
    m.xy *= rot(floor(rand1(i + 62.0) * 4.0) * PI * 0.5);
    m.yz *= rot(floor(rand1(i + 25.0) * 4.0) * PI * 0.5);
    return sdTruchet(m) - 0.0625;
}

float march(vec3 ro, vec3 rd) {
    float d = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        float ds = map(ro + (rd * d));
        d += ds;
        if(d > MAX_DIST) return MAX_DIST;
        if(abs(ds) <= MIN_DIST) return d;
    }
    return MAX_DIST;
}

vec3 normal(vec3 p) {
    vec2 e = vec2(MIN_DIST, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

float shadedPointLight(vec3 p, vec3 n, vec3 lp, float r) {
    vec3 ln = lp - p;
    float d = length(ln);
    ln /= d;

    return max(0.0, dot(n, ln) * (1.0 - (d / r)) * ((march(p + ((n * MIN_DIST) * 2.0), ln) > d) ? 1.0 : 0.0));
}

void mainImage(out vec4 o, vec2 i) {
    vec2 uv = (i - (0.5 * iResolution.xy)) / min(iResolution.x, iResolution.y);

    float t = (iTime * 0.5) + 50.0;

    vec3 ro = vec3(0.0, 0.0, t);
    vec3 rd = normalize(vec3(uv, 1.0));

    rd.yz *= rot(sin(t * PI * 0.05) * PI * 0.0625);
    rd.xz *= rot(cos(t * PI * 0.05) * PI * 0.0625);

    float d = march(ro, rd);
    vec3 p = ro + (rd * d);
    vec3 n = normal(p);

    vec3 col = vec3(sin(p * PI * 0.5) * 0.25 + 0.5);

    vec3 light = vec3(0.0);

    light += shadedPointLight(p, n, ro + (sin(t * vec3(0.5, 0.2, 0.7)) * 0.25), 8.0);
    light += shadedPointLight(p, n, ro + (sin(t * vec3(0.3, 0.8, 0.4)) * 0.25), 8.0);

    col *= light;

    col = mix(col, vec3(0.0), smoothstep(0.0, MAX_DIST, d));

    col *= smoothstep(1.0, -0.5, length(uv));

    o = vec4(col, 1.0);
}

