// Create a Gradient that moves vertically.
vec3 questionOne(vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(.1, uv.y, .5);
    return col;
}
// Create a split-screen of four quadrants. Choose any 4 colors.
vec3 questionTwo(vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0,1,0);
    if (uv.x > .5) {
        col = vec3(1,0,0);
    }
    if (uv.y > .5) {
        col = vec3(0,0,1);
    }
    if (uv.y > .5 && uv.x > .5) {
        col = vec3(1,1,0);
    }
    return col;
}

float magnitudeCalc(vec3 color) {
    return sqrt(pow(color.x, 2.0) + pow(color.y, 2.0) + pow(color.z, 2.0));
}
// Create a "heat" finder. Basically, if the magnitude/brightness of any color is greater than X then turn it red. 
// X should be a variable. By default, set it to 0.5.
vec3 questionThree(vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(0, uv.x, uv.x);
    float X = 0.5;
    if (magnitudeCalc(color) >= X) {
      color = vec3(1,0,0);
    }
    return color;
}
// Bonus: Create a striped pattern of any two colors. The colors should be variable.
vec3 questionFour(vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    // Random color code from Shadertoy example.
    vec3 randColOne = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 randColTwo = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(4,2,0));
    vec3 col = randColOne;
    if (int(uv.x * float(10)) % 2 == 1) {
        col = randColTwo;
    } else {
        col = randColOne;
    }
    return col;
}

