float box(vec3 p,vec3 b){
    vec3 q = abs(p)-b;
    return length(max(vec3(0.),q))+min(0.,max(q.x,max(q.y,q.z)));
}
mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
vec2 sdf(vec3 p){
  p.z +=mod(iTime,100.);
      vec2 id = floor(p.xz);
      p.xz = asin(sin(p.xz)*.8);
  vec2 h;


    vec4 pp = vec4(p,1.);
    float mm = 10000.;
    for(float i =0.;i<=16.;i++){
            pp.xyz = abs(pp.xyz)-1.1;
            pp.xz *=rot(.785*i+i);

            pp.yz *=rot((.29)*i);
            float m =  min(pp.x,min(pp.y,pp.z));
            mm = min(mm,m); // This was on nusan video I recently saw. Quite nice
            pp*=1.11;
            pp.xy = pp.x < pp.y ? pp.yx:pp.xy;
            pp.x -=3.1;
    }
    h.x =     (length(pp.xyz)-1.5-mm*.01)/pp.a;
    h.x = max(abs(length(p)-2.)-1.1,-h.x);
    h.x = mix(box(p,vec3(1.)),h.x*.7,.5);
    h.y = 1.;
    h.x = max(-(length(pp.xz)-3.6)/pp.a,h.x);

    vec2 t;
    t.x = dot(p,vec3(0.,1.,.0))-sin(pp.x*.1+pp.z*.1)*.1;
    t.y = 2.;
    h = t.x < h.x  ? t:h;
    return h;
}
#define ao(rp,n,k) (sdf(rp+n*k).x/k)
#define AO(rp,n) (ao(rp,n,.1)+ao(rp,n,.5)+ao(rp,n,.7))
#define q(s) s*sdf(p+s).x
vec3 norm(vec3 p,float ee){vec2 e= vec2(-ee,ee);return normalize(q(e.xyy)+q(e.yxy)+q(e.yyx)+q(e.xxx));}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(.1);
    vec3 ro = vec3(0.,2.+sin(iTime*.33+3.0)*1.4,-5.);
    vec3 rt = vec3(cos(iTime*.33)*2.,sin(iTime*.5),0.);
    vec3 z = normalize(rt-ro);
    vec3 x = normalize(cross(z,vec3(0.,-1.,0.)));
    vec3 y = normalize(cross(z,x));

    vec3 rp = ro;
    uv *=rot(cos(iTime*.3));
    vec3 rd = mat3(x,y,z)*normalize(vec3(uv,1.));
    float dd =0.;
    vec2 d;
    d.y =-1.;
    vec3 light = vec3(1.,2.,-3.);
    for(float i=0.;i<=128.;i++){
        d = sdf(rp);
        dd +=d.x;
        if(dd > 10.) {d.y= -1.; break;}
        if(d.x<.001){
            vec3 n = norm(rp,.0003);
            if(d.y == 2.){
                rd = normalize(reflect(rd,n));
                rp +=rd*.01;
                col +=vec3(.1,.1,.4);
                continue;
            }
            float diff = max(0.,dot(normalize(light-rp),n));
            col += vec3(1.)*diff;
            col = mix(vec3(.1,.1,.2),vec3(1.,.9,.7),AO(rp,n)/1.7);
            break;
        }
    rp+=rd*d.x;
    }
    if(d.y==-1.) col += mix(vec3(.2,.4,.8),vec3(.5,.6,.9),uv.y);
    fragColor = vec4(col,1.0);
}

vec3 rotate(vec3 pos, vec2 rotation) {
    vec2 m4 = vec2(length(pos.xz), length(pos.xyz));

    vec2 angle = vec2(acos(pos.x/m4.x), asin(pos.z/m4.y));
    float l = length(pos);
    vec3 poss = pos;
    float xx = poss.x*cos(rotation.y)+poss.z*sin(rotation.y);
    float yy = poss.y;
    float zz = -pos.x*sin(rotation.y)+poss.z*cos(rotation.y);

    return vec3(xx, yy, zz);
}

float DE(vec3 pos, int Iterations, float Power, float xRot, bool limit) {
    vec3 z = pos;
	float dr = 1.0;
	float r = 0.0;
    vec3 c = vec3(sin(iTime*0.17), cos(iTime*0.193), sin(iTime*0.237));
    // convert to polar coordinates
	float phi1 = atan(z.y,z.x)+xRot;
    // convert back to cartesian coordinates
    z = vec3(length(z.xy)*vec2(sin(phi1), cos(phi1)), z.z);
    c = z;
	for (int i = 0; i < Iterations ; i++) {
		r = length(z);
		if (r>2.0) break;

		// convert to polar coordinates
		float theta = acos(z.z/r);
		float phi = atan(z.y,z.x);
		dr =  pow(r, Power-1.0)*Power*dr + 1.0;

		// scale and rotate the point
		float zr = pow(r, Power);
		theta = theta*Power;
		phi = phi*Power;

		// convert back to cartesian coordinates
		z = zr*vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
		z+=c;
	}
    if (limit == true) {
        return max(0.5*log(r)*r/dr, 0.0009);
    } else {
        return abs(0.5*log(r)*r/dr);
    }
}

vec3 rayCast(vec3 dir, vec3 pos, float xRot, vec2 coord, float threshold, int maxSteps, vec3 lightDir, int fractalSteps, float degree) {
    float j = 0.0;
    vec3 p = pos;
    int i = 0;
    bool intersect = false;
    float f;
    float lightInfo = 1.0;
    vec3 normal = vec3(0.0);
    float ambientLight = 0.1;
    float a = dot(dir, dir);
    float b = 2.0*dot(dir, pos);
    float c = dot(pos, pos) - pow(2.0, 2.0);
    float discriminant = b*b-4.0*a*c;
    vec3 inPos = (-b - sqrt(discriminant))/(2.0*a)*dir+pos;
    vec3 outPos = (-b + sqrt(discriminant))/(2.0*a)*dir+pos;
    // convert to polar coordinates
	float phi1 = atan(lightDir.y,lightDir.x)+xRot;
    // convert back to cartesian coordinates
    lightDir = vec3(length(lightDir.xy)*vec2(sin(phi1), cos(phi1)), lightDir.z);
    p = inPos;
    if (discriminant > 0.0) {
        for (int i = 0; i < maxSteps && length(p) < 2.01; i++) {
            f = DE(p, fractalSteps, degree, xRot, false);
            p += dir*f*0.9;

            if (f < threshold*1.0) {
                float fa = 3.1415926;
                float fb = 3.1415926;
                //get normals
                float nx = DE(p - threshold*vec3(1.0, 0.0, 0.0), fractalSteps, degree, xRot, false);
                float ny = DE(p - threshold*vec3(0.0, 1.0, 0.0), fractalSteps, degree, xRot, false);
                float nz = DE(p - threshold*vec3(0.0, 0.0, 1.0), fractalSteps, degree, xRot, false);
                normal = -normalize((vec3(nx, ny, nz) - f));
                i = maxSteps + 1;
                vec3 l = vec3(0.0);

                intersect = true;
            }
            j++;
        }
        if (intersect == true) {
        f = 0.0;
        vec3 l;
        float fa = 3.1415;
        float fb = 3.1415;
            for (int k = 0; k < maxSteps && length(p+l) < 2.0; k++) {
                float ff = DE(l+p, fractalSteps, degree, xRot, true);
                f += ff;
                l = -f * lightDir;
                fa = ff/f;
                //if (k > 1) {
                    fa = min(fa, fb);
                    fb = fa;
                    lightInfo = min(clamp(fa*10.0, ambientLight, 1.0), (clamp(-dot(normal, lightDir), 0.0, 1.0)*(1.0-ambientLight)+ambientLight));

                //}
                if (k > 0 && f < threshold) {
                    lightInfo = ambientLight;
                    k = maxSteps;
                }



            }
            return vec3(5.0/pow(1.02, j)*lightInfo);
            return normal;
        } else {
            return vec3(0.0);
        }

    } else {
        return vec3(0.0);
    }



}

