void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    uv*=rot(.13*iTime);
    vec3 ro = vec3(20.*sin(iTime*.033),0.,20.*cos(iTime*.033));

    vec3 rp = ro;

    vec3 rt  = vec3(.1+10.*sin(iTime*.033),0.1,.1+20.*cos(iTime*.033));

    vec3 z = normalize(rt-ro);
    vec3 x = normalize(cross(z,vec3(0,-1,0)));
    vec3 y = normalize(cross(z,x));

    vec3 rd = mat3(x,y,z)*normalize(vec3(uv,.95));
    vec3 light = vec3(1.+20.*sin(iTime*.033),2.,-1.+20.*cos(iTime*.033));
    vec3 col = vec3(.01);
    vec3 acc = vec3(0.);
    for(float i=0.;i<=128.;i++){
        vec2 d = sdf(rp);
        if(d.y ==2.){
            acc +=vec3(.01)*exp(-abs(d.x))/(15.+sin(atan(rp.x,rp.z)*40.)*14.);
            d.x = max(0.,abs(d.x));
        }
        if(length(rp) > 100.) break;
        if(d.x <.0001 && d.y != 2.){
            vec3 n = norm(rp,.0003);
            vec3 nn = norm(rp,.003);
            float dif = dot(normalize(light-rp),n);
            float spc = max(0.,dot(normalize(ro-rp),reflect(normalize(rp-light),n)));
            spc = pow(spc,32.);

            col += max(0.,1.-dot(n,nn))*max(dif,spc)+vec3(1.9+sin(rp.y*10.),.9,1.9+cos(rp.x*10.))*spc*.59;
            break;
        }
        rp +=rd*d.x;

    }
    col = max(acc,col);
    fragColor = vec4(sqrt(col),1.0);
}

#define TAU (6.283185307)
#define LOOPLEN 4.

// adjusts the saturation
vec3 sat(vec3 rgb)
{
    // Algorithm from Chapter 16 of "OpenGL Shading Language"
    const vec3 W = vec3(0.2125, 0.7154, 0.0721);
    vec3 intensity = vec3(dot(rgb, W));
    return mix(intensity, rgb, 2.5); // adjust strength here
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Make sure this loops
    float time = fract(iTime / LOOPLEN);
    // Simulate lower framerate
    time = floor(iTime * 20.) / 20. / LOOPLEN;
    // Normalized pixel coordinates
    vec2 uv = ( 2.* fragCoord - iResolution.xy ) / length(iResolution.xy);
    // use log distance for perspective/tunnel effect
    float dist = log(uv.x * uv.x + uv.y * uv.y);
    float angle = atan(uv.y, uv.x);

    vec3 chanTime = 0.01 * vec3(-1, 0, 1) + time;
    vec3 zoomTime = 1. * chanTime + 2.5 * sin(TAU * chanTime + dist * 0.5);

    vec3 col = smoothstep(-1.0, 1.0, sin(
        dist * 7. + zoomTime * TAU
    ));

    col += 0.3 * sin(dist * 4.5 + 3. * angle + (time + vec3(1, 0, 1) / 12.) * TAU);

    // Output to screen
    fragColor = vec4(
        sat(col), 1.0
    );
}

