// from https://www.shadertoy.com/view/WtGXDD
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}


// from https://www.shadertoy.com/view/WtGXDD
vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}


// 2d rotation matrix
vec2 rot2D(vec2 v, float a)
{
    mat2x2 rot = mat2x2(cos(a), -sin(a), sin(a), cos(a));
    return v * rot;
}


// takes a uv coord and mouse pos and converts it to the rays direction
vec3 Transform(vec2 uv, vec2 m, float roll)
{
    vec3 ro = vec3(0, 1, -1) * 20.;
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    vec3 rd = GetRayDir(rot2D(uv, roll), ro, vec3(0,0.,0), 1.);
    return rd;
}


// takes in a vec3 and returns a randomized vec3 (from another shader)
vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}


// from another shadertoy shader
vec4 noised( in vec3 x )
{
    // grid
    vec3 i = floor(x);
    vec3 w = fract(x);

    // cubic interpolant
    vec3 u = w*w*(3.0-2.0*w);
    vec3 du = 6.0*w*(1.0-w);

    // gradients
    vec3 ga = hash( i                   );
    vec3 gb = hash( i+vec3(1.0,0.0,0.0) );
    vec3 gc = hash( i+vec3(0.0,1.0,0.0) );
    vec3 gd = hash( i+vec3(1.0,1.0,0.0) );
    vec3 ge = hash( i+vec3(0.0,0.0,1.0) );
	vec3 gf = hash( i+vec3(1.0,0.0,1.0) );
    vec3 gg = hash( i+vec3(0.0,1.0,1.0) );
    vec3 gh = hash( i+vec3(1.0,1.0,1.0) );

    // projections
    float va = dot( ga, w                   );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolations
    return vec4( va + u.x*(vb-va) + u.y*(vc-va) + u.z*(ve-va) + u.x*u.y*(va-vb-vc+vd) + u.y*u.z*(va-vc-ve+vg) + u.z*u.x*(va-vb-ve+vf) + (-va+vb+vc-vd+ve-vf-vg+vh)*u.x*u.y*u.z,    // value
                 ga + u.x*(gb-ga) + u.y*(gc-ga) + u.z*(ge-ga) + u.x*u.y*(ga-gb-gc+gd) + u.y*u.z*(ga-gc-ge+gg) + u.z*u.x*(ga-gb-ge+gf) + (-ga+gb+gc-gd+ge-gf-gg+gh)*u.x*u.y*u.z +   // derivatives
                 du * (vec3(vb,vc,ve) - va + u.yzx*vec3(va-vb-vc+vd,va-vc-ve+vg,va-vb-ve+vf) + u.zxy*vec3(va-vb-ve+vf,va-vb-vc+vd,va-vc-ve+vg) + u.yzx*u.zxy*(-va+vb+vc-vd+ve-vf-vg+vh) ));
}


// gets the distance to the ground
float dstGround(vec3 p)
{
    float dst = p.y - noised(p * 0.2 + vec3(0., 1., 0.)).r + 4.;

    return dst;
}


// gets the distance to a place with a linear slope across the x and z
float dstPlane(vec3 p, vec2 slope, vec3 pos, vec2 s)
{
    vec2 np = p.xz - pos.xz;
    float dstY = abs(p.y - (slope.x * np.x + slope.y * np.y + pos.y)) - 0.01;
    float dstXZ = max(abs(np.x) - s.x, abs(np.y) - s.y);
    return max(dstY, dstXZ) * 0.7;
}


// takes the plane and bends it into sheet metal
float dstSheetMetal(vec3 p, vec2 slope, vec3 pos, vec2 s)
{
    float dst = dstPlane(p + vec3(0., sin(p.x * 23.) * 0.025, 0.), slope, pos, s);
    return dst * 0.7;
}


// the same as the sheet metal but wooden planks, it also positions them virtically
float dstWoodenPlank(vec3 p, vec2 slope, vec3 pos, vec2 s)
{
    float dst = dstPlane(vec3(p.x, p.z, p.y), slope, vec3(pos.x, pos.z, pos.y), s);
    return dst;
}


// the same as the orther plank but a different direction (idk if its north, north would be positive z)
float dstWoodenPlankNorth(vec3 p, vec2 slope, vec3 pos, vec2 s)
{
    float dst = dstPlane(vec3(p.z, p.x, p.y), slope, vec3(pos.z, pos.x, pos.y), s);
    return dst;
}


// specular reflection and diffusion (im not using the diffusion in this shader, its a hit or miss)
struct SpecularLight
{
    float highlight;
    float diffuse;
};


SpecularLight Specular(float smoothness, vec3 normal, vec3 rd, vec3 sun_dir)
{
    float specularAngle = acos(dot(normalize(sun_dir - rd), normal));
    float specularExponent = specularAngle / (1. - smoothness);
    float specularHighlight = exp(-specularExponent * specularExponent);
    float diffseLighting = clamp(dot(normal, sun_dir), 0., 1.);
    return SpecularLight(specularHighlight, diffseLighting);
}


