// gets the distance to a cilander
float distToCilander(vec3 p, vec3 lineA, vec3 lineB, float r) {
	vec3 LineLine = lineB - lineA;
    vec3 LinePoint = p - lineA;

    float t = dot(LineLine, LinePoint) / dot(LineLine, LineLine);

    vec3 center = lineA + (t * LineLine);

    float x = length(p - center) - r;
    float y = (abs(t - 0.5) - 0.5) * length(LineLine);
    float e = length(max(vec2(x, y), 0.));

    float i = min(max(x, y), 0.);

    return e + i;
}


// using triplanar mapping to find the uv coord for a texture on any surface
vec2 GetUV(vec3 p, vec3 normal)
{
    // the sharpness of the blending between different axises
    float blendSharpness = 100.;
    // finding the different axise's color
    vec2 colX = p.zy;
    vec2 colY = p.xz;
    vec2 colZ = p.xy;

    // finding the blending amount for each axis
    vec3 bw = pow(abs(normal), vec3(blendSharpness));
    // making it so the total (x + y + z) is 1
    bw /= dot(bw, vec3(1.));

    // finding the final color
    return colX * bw.x + colY * bw.y + colZ * bw.z;
}


// gets the distance to the walls of the cabin (wooden walls)
float dstCabin(vec3 p)
{
    vec3 p2 = vec3(p.x, p.y, abs(p.z + 1.1) - 3.6);  // making 2 walls for the price of one using a repeating coordinate space
    vec3 p3 = vec3(abs(p.x + 0.2) - 4.2, p.y, p.z);  // making 2 walls for the price of one using a repeating coordinate space
    float dst1 = dstWoodenPlank(p2, vec2(0.1, 0.025), vec3(-2.  + 3., -0.75, -2.), vec2(0.25, 2.));
    float dst2 = dstWoodenPlank(p2, vec2(-0.1, -0.1), vec3(-2.6 + 3., -0.75, -2.), vec2(0.25, 2.));
    float dst3 = dstWoodenPlank(p2, vec2(0.1, 0.), vec3(-3.2 + 3.   , -0.75, -2.), vec2(0.25, 2.));
    float dst4 = dstWoodenPlank(p2, vec2(0., 0.05), vec3(-3.8 + 3.  , -0.75, -2.), vec2(0.25, 2.));
    float dst5 = dstWoodenPlank(p2, vec2(0.2, 0.), vec3(-4.4 + 3.   , -0.75, -2.), vec2(0.25, 2.));

    float dst = min(min(dst1, dst2), min(dst3, min(dst4, dst5)));

    dst1 = dstWoodenPlankNorth(p3, vec2(0.1, 0.025), vec3(-2.5, -0.75, -1.  + 1.), vec2(0.25, 2.));
    dst2 = dstWoodenPlankNorth(p3, vec2(-0.1, -0.1), vec3(-2.5, -0.35, -1.6 + 1.), vec2(0.25, 2.4));
    dst3 = dstWoodenPlankNorth(p3, vec2(0.1, 0.)   , vec3(-2.5, -0.25, -2.2 + 1.), vec2(0.25, 2.75));
    dst4 = dstWoodenPlankNorth(p3, vec2(0., 0.05)  , vec3(-2.5, -0.35, -2.8 + 1.), vec2(0.25, 2.4));
    dst5 = dstWoodenPlankNorth(p3, vec2(0.2, 0.)   , vec3(-2.5, -0.75, -3.4 + 1.), vec2(0.25, 2.));
    float dst6 = dstWoodenPlankNorth(p3, vec2(0.2, 0.)   , vec3(-2.5, 0.75, -2. + 0.25), vec2(2., 0.25));

    dst = min(min(min(dst1, dst2), min(dst3, min(dst4, min(dst5, dst6)))), dst);

    return dst;
}


// gets the distance to the roof and floor of the cabin (sheet metal)
float dstCabinRoof(vec3 p)
{
    vec3 p2 = vec3(p.x, p.y, abs(p.z) - 0.9);  // making 2 sides of the roof/floor for the price of one using a repeating coordinate space
    float dst1 = dstSheetMetal(p2, vec2(0., -0.9), vec3(0., 2., 0.), vec2(2., 0.8));
    float dst2 = dstSheetMetal(p2, vec2(-0.025, 0.1), vec3(0., -1., 0.), vec2(2., 0.8));
    // the light bulb
    float dst3 = distToCilander(p + vec3(0.3, -0.6, 0.0), vec3(0., 0., 0.), vec3(0., 0.2, 0.), 0.01);

    float dst = min(dst1, min(dst2, dst3));

    return dst;
}


// gets the distance to the scene (there is a commented out function where i was messing with emmition in other ways then im using now and the raw version was that but without emission, this is the left over from that failed atempt)
float GetDstRaw(vec3 p)
{
    float dst1 = dstCabinRoof(p + vec3(-3.5, 2.8, 4.));
    float dst2 = length(p + vec3(-3.56 , 2.8, 4.) + vec3(0.3, -0.6, 0.0)) - 0.02;
    float dst3 = dstCabin(p + vec3(-3.5, 3., 3.));
    float dst4 = dstGround(p);

    float dst = min(min(dst1, dst2), min(dst3, dst4));

    return dst;
}


// gets the material properties so the material can be created
MatProperties GetMat(vec3 p, vec3 normal, vec3 sun_dir)
{
    float dst1 = dstCabinRoof(p + vec3(-3.5, 2.8, 4.));
    float dst2 = length(p + vec3(-3.56 , 2.8, 4.) + vec3(0.3, -0.6, 0.0)) - 0.02;
    float dst3 = dstCabin(p + vec3(-3.5, 3., 3.));
    float dst4 = dstGround(p);

    float dst = min(min(dst1, dst2), min(dst3, dst4));

    // the strength of the light
    vec3 l = mix(vec3(1.75, 1.25, 0.), vec3(0.), pow(clamp(dst2 * 0.2 + max(floor(dot(sun_dir, vec3(0., 1., 0.)) + 0.4), 0.), 0., 1.), 0.4));
    // checking which surface was hit
    if (dst == dst1)  // sheet metal
    {
        vec3 col = texture(iChannel0, GetUV(p, normal) * 0.2).rgb;
        float mask = abs(max(col.r - col.g, col.r - col.b)) + 0.9;
        return MatProperties(col + l / mask, l.r / mask * 0.5, mask, 0.);
    }
    else if (dst == dst2)  // light bulb
    {
        vec3 col = vec3(1., 1., 0.5) * 4.;
        return MatProperties(col * clamp(l.r, 0., 1.), 0., 1., 1.);
    }
    else if (dst == dst3)  // wooden plank
    {
        vec3 col1 = texture(iChannel2, GetUV(p, normal) * 0.4).rgb;
        vec3 col2 = texture(iChannel1, GetUV(p, normal) * 0.4).rgb;
        vec3 col = mix(col1, col2, 0.3 + col2 * 0.1);
        float mask = length(col);
        return MatProperties(col + l * 2.5, l.r * 0.5, 1., 0.);
    }
    else if (dst == dst4)  // ground (grass)
    {
        vec3 col = (texture(iChannel3, GetUV(p, normal) * 0.6).r * 0.6 + 0.4) * vec3(0.2, 0.8, 0.2);
        float mask = abs(max(col.r - col.g, col.r - col.b)) + 0.7;
        return MatProperties(col + l / mask * 1.3, l.r / mask * 1.1, 1., 0.);
    }

    return MatProperties(vec3(0.), 0., 0., 0.);
}


