vec3 GetNormal ( vec3 z)
{
    float e   =  0.01;
    float dx1 = GetDstRaw(z + vec3(e, 0, 0));
    float dx2 = GetDstRaw(z - vec3(e, 0, 0));
    float dy1 = GetDstRaw(z + vec3(0, e, 0));
    float dy2 = GetDstRaw(z - vec3(0, e, 0));
    float dz1 = GetDstRaw(z + vec3(0, 0, e));
    float dz2 = GetDstRaw(z - vec3(0, 0, e));

    return normalize ( vec3 ( dx1 - dx2, dy1 - dy2, dz1 - dz2 ) );
}


// marches a ray through the scene
Ray MarchRay(vec3 ro, vec3 rd, vec3 sun_dir)
{
    float dst;
    vec3 p = ro;
    float dfs = 0.;
    float total_s = 0.;
    vec3 glow = vec3(0.);
    bool collided = false;
    for (int s = 0; s < 200; s++)  // 200 steps
    {
        //vec4 data = GetDst(p);
        //dst = data.w;
        dst = GetDstRaw(p);
        //glow = max(data.rgb, glow);
        p += rd * dst;
        dfs += dst;

        total_s = float(s);

        if (dfs > 20.) break;
        if (dst < 0.02)  // a percision of 0.02
        {
            collided = true;
            break;
        }
    }

    //glow /= total_s;
    if (collided)  // checking if the ray collided to know if the color/material should be found
    {
        vec3 normal = GetNormal(p);

        MatProperties matProps = GetMat(p, normal, sun_dir);
        SpecularLight spec = Specular(matProps.smoothness, normal, rd, sun_dir);
        Mat mat = Mat((matProps.col + spec.highlight * (1. - matProps.shade_strength)) * mix(max(dot(normal, sun_dir) * 0.7 + 0.3 + matProps.glow, 0.), 1., matProps.shade_strength), normal, glow, matProps.smoothness, spec);
        return Ray(mat, p, ro, rd, dfs, collided);
    }
    else  // the ray did not hit and a blank material will be created as there is nothing to render
    {
        return Ray(Mat(vec3(0.), vec3(0.), glow, 0., SpecularLight(0., 0.)), p, ro, rd, dfs, collided);
    }
}


// renders soft shadows (credit to some other shader for this function)
float SoftShadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    float ph = 1e20;
    for( float t=mint; t<maxt; )
    {
        float h = GetDstRaw(ro + rd*t);
        if( h<0.001 )
        {
            return 0.0;
        }
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}


// converts a vector3 to a float while randomizing it
float hash31(vec3 p)
{
    return fract(sin(dot(p, vec3(64.25375463, 23.27536534, 86.29678483))) * 59482.7542);
}


// rendering the image
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // the uv coordinate
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    // the mouse position
    vec2 m = iMouse.xy / iResolution.xy;

    // the sun direction
    vec3 sun_dir = normalize(vec3(0.5, sin(iTime * 0.2 - 1.), 0.));

    // setting the fov based on the scene
    if (iTime < 11.) uv *= 3.75;
    else uv *= 2.;

    // finding the ray's direction
    vec3 rd = Transform(uv, m, 0.);
    // finding the camera's position based on the scene
    vec3 ro;
    if (iTime < 11.) ro = vec3(3., -2.5, -4.);
    else ro = vec3(0., -1.5, -1.);

    // the color (setting it to the sky color for starters)
    vec3 col = vec3(0.05, 0.05, (rd.y * sun_dir.y) * 0.8 + 0.2) * 1.6;
    // adding the sun
    col = max(mix(col, vec3(1.5), pow(max(dot(rd, sun_dir), 0.), 275.)), 0.);

    // marching the ray
    Ray ray = MarchRay(ro, rd, sun_dir);
    // checking if the ray hit anything
    if (ray.collided)
    {
        // adding soft shadows
        float shadow = SoftShadow(ray.cp + ray.mat.normal * 0.05, sun_dir, 0.05, 15., 30.) * 0.6 + 0.4;
        ray.mat.col *= shadow;
        // setting the color to the objects color
        col = ray.mat.col;
        col = pow(col, vec3(1. / 1.6));
    }
    //col += ray.mat.glow;

    // if it didnt collide then stars are rendered
    if (!ray.collided)
    {
        // noise to make the stars blink
        float sky_noise = round(noised(rd * 25. + iTime * 0.25).r * 0.5 + 0.5);
        // rendering the stars
        col = mix(col, vec3(1.), ceil(pow(hash31(floor(rd * 200.) / 200.), 12000.)) * clamp(clamp(rd.y + 0.2, 0., 1.) * pow(1. - col.b, 7.), 0., 1.) * sky_noise);

        // adding the moon
        // the moons position (you have to rotate the ray's direction to not distort the value)
        vec3 mp = vec3(rd.x, rot2D(vec2(rd.y, rd.z), 0.9 + iTime * 0.1));
        vec3 mp2 = vec3(rd.x, rot2D(vec2(rd.y, rd.z), 0.99 + iTime * 0.1));
        mp2 = vec3(rot2D(vec2(mp2.x, mp2.y), -0.075), mp2.z);
        // the mask for the moon
        float moon_mask = smoothstep(0.2, 0.15, length(mp.xz)) * smoothstep(0.15, 0.2, length(mp2.xz));
        // adding the moon to the final color
        col = mix(col, vec3(1.), clamp(moon_mask * clamp(clamp(rd.y + 0.2, 0., 1.) * pow(1. - col.b, 7.), 0., 1.), 0., 1.));
    }

    //col = col + (pow((1. - length(col)) * 0.35, 2.));

    // the final color
    fragColor = vec4(col, 1.0);
}

