  float spt = 0.56+sct;
    float d = box(p+vec3(0.,-0.3,-1.),vec3(spt*4.));

  float dc = 0.1;
  for(int i = 0;i<1;i++){    
    p = sin(clamp(p,-spt*PI,spt*PI))-0.1;
    p.xz *= rot(0.3+sca);
    p.yz *= rot(0.4+sca);
    p.xy *= rot(sca);
    p = abs(p)+0.;
    }
    d = max(d,box(p-vec3(0.3,0.6,0.3),vec3(0.2,0.5,0.2)));


    p = sin(clamp(p,-spt*PI,spt*PI))-0.1;
    p.xz *= rot(0.3+sca);
    p.yz *= rot(0.4+sca);
    p.xy *= rot(sca);
    p = abs(p)+0.;
    vec3 sssp = p;

        p = sin(clamp(p,-spt*PI,spt*PI))-0.1;
    p.xz *= rot(0.3+sca);
