#define eps 1./iResolution.y
void mainImage( out vec4 O, in vec2 fragCoord )
{

    vec2 uv = alohamora; //uv from -1. to 1.
    vec2 global_uv = alohamora; //do it again just for fun
    float scale = 2.; //starting scale 
    float radius = 0.34; //circle radius
    float dist = 100.; //starting distance to circle 
    vec2 circle_coords = wingardium_leviosa;//move the circle around

    //if mouse is pressed, then you control the circle
    if(iMouse.z > 0.5) circle_coords = mobiliarbus;

    //starting color
    O = vec4(0.0);
    O.r = .25;

    //find distance estimation to circle 
    float circle_dist = length(uv-circle_coords) - radius;

    //the plan is simple

    for(int i = 0; i < max_layers; i++){

        //we get a scaling variable of 2^n where n is layer number
        scale = pow(scale_factor,float(i));
        //we scale the global uv by it so from -1 -> 1
        //we go to -2 -> 2 and then -4 -> 4 and so on
        vec2 local_uv = global_uv*scale;
        //we get the floor coordinates of this system for an id
        //each QUAD then is a cell from one integer to the next (in 2d)

        // x: 1 -> 2 and y: 1 -> 2 is one quad
        vec2 id = floor(local_uv);
        //we use that id to get the centers for each quad local space
        //and then immediately convert to global space by dividing again
        //by our scale
        //now we have a center coordinate, in global space, for 
        //every quad created in this layer
        vec2 global_quad_center = (id + 0.5)/scale;
        //we then find the lenght of one side in global space
        //which is 1./scale (for example, if scale is 1, we have
        //only 1 quad, so length is one
        //if scale is 2, we have 4 quads with these dimentions
        //
        // x:0->0.5, y:0.5->1.  x:0.5->0.5, y:0.5->1.
        //
        // x:0->0.5, y:0->0.5   x:0.5->1., y:0.->0.5
        //
        //and you can see the math works because 1./2. gives a length
        //of 0.5 which each quad has

        float quad_length = 1./scale;

        //we get the diagonal the cool way
        float diagonal = quad_length*sqrt_of_2;

        //half it
        float half_diagonal = diagonal/2.;

        //we want to find if our circle intersects the quad 
        //based on our quad's id (used to get center, blah blah blah)
        //and that is approximated by this:
        //if the distance between the quad center and the circle center
        //is less than the sum of half the quad's diangona and
        //the circle's radius, then the circle intersects the quad
        float max_dist = half_diagonal + radius;

        //distance between circle center and quad center
        float dist_between = length(circle_coords - global_quad_center);


       //adding a sort of white vignette from one
       //of iq's tileing shaders based on xo
        vec2 u01 = fract(local_uv);
        float vig = pow((1.-u01.x*u01.y*(1.-u01.x)*(1.-u01.y)
        *1.*(float(max_layers-i)/float(max_layers))),300.3);


       //the check, if true, we stop at this layer for this quad
       //not more subdivisions
        if(dist_between > max_dist) 
        {

        O.rgb += vig;
        break;


        }
        //here I create borders for each quad and the math
        //could be prettier but I transform the coords in every 
        //quad to be from 0 to 1. It's just the quads local coords
        //minus the id. (or fract could work too)
        vec2 local_0_to_1 = local_uv - id;
        //then I translate left 0.5 so it's -0.5 -> 0.5
        //and then abs so it's 0.5 <- 0 -> 0.5
        vec2 abs_local = abs(local_0_to_1-0.5);
        //I get the max like in a square sdf and if 
        //the value for that coord is more than 0.49 I say it's a border
        //so make it white and break
        //I try to subtracta small fraction of layer num over max layers
        //but it doesn't really add anythin
        if(max(abs_local.x,abs_local.y) > 0.49 
        - float(i)/(20.*float(max_layers))) {
            O ++; //stolen from fabrice
            O.rgb += vig;
            break;
        }
        //at the end of each round increase the color value by some standard
        //amount
        O.rbg += vec3(0.05, 0.1,0.08);

    }

   //I through another circle on top like it fabrice's version to hind 
   //how the borders become a problem later on.
   float circle_mask = 1.00-smoothstep(radius -eps, radius +eps,  length(uv-circle_coords) - radius*0.01);
   vec3 circle_color =vec3(0.9);
   O.rgb += circle_color*circle_mask/1.;


   }

#define PI 3.1415

vec2 rotate(vec2 p, float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(c, s, -s, c) * p;
}

float smoothMin(float a, float b, float k) {
    return -log2(exp2(-k * a) + exp2(-k * b)) / k;
}

float sdfFw(float d) {
    return smoothstep(0., fwidth(d) * 1.5 * 720. / iResolution.y, d);
}

float sdf(float d) {
    return smoothstep(0., .002 * 720. / iResolution.y, d);
}

float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

float sdLine(vec2 p, vec2 a, vec2 b){
    vec2 pa = p - a;
    vec2 ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
    return length(pa - ba * h);
}

float sdWing(vec2 q) {
    float f1 = 5. * smoothstep(-0.9, 1.1, sin(iTime * 1.5));
    float f2 = (sin(iTime * 18.) + 1.) * 1.5;

    q = rotate(q, ((f1 * f2) / 19.) + PI / 2.02);
    q.x -= sin(q.y * 22. + 1.5) * 0.01;
    return sdLine(q, vec2(0., -0.45), vec2(0)) - 0.05 * smoothstep(-0.23, 0., q.y);
}

float sdTail(vec2 q) {
    float f = 5. * smoothstep(0.8, -1.9, 2. * sin(iTime*1.5));

    q = rotate(q, f / 40. + PI / 2.25);
    q.x -= sin(q.y * 7. + 1.) * 0.005 + sin(q.y * 15.) * 0.003;
    return sdLine(q, vec2(0., -0.35), vec2(0)) - 0.035 * smoothstep(-0.23, 0., q.y);
}

float sdBird(vec2 p) {
    float d, d1;

    // head
    vec2 q = p - vec2(0.17, -0.045);
    d = sdCircle(q, 0.045);

    q = p - vec2(0.205, -0.0415);
    d1 = sdCircle(q, 0.03);
    d = smoothMin(d, d1, 180.);

    // nose
    q = p - vec2(0.2125, -0.055);
    q = rotate(q, -PI / 3.5);
    q.x -= sin(q.y * 0.01 + 1.) * 0.005 + sin(q.y * 0.01) * 0.005;
    d1 = sdLine(q, vec2(0., -0.03), vec2(0)) - 0.0175 * smoothstep(-0.03, 0., q.y);
    d = min(d, d1);

    // eye
    q = p - vec2(0.21, -0.05);
    d1 = sdCircle(q, 0.008);
    d = max(d, -d1);

    // tail
    q = p - vec2(0.08, -0.045);
    d1 = sdTail(q);
    d = min(d, d1);

    q = p - vec2(0.08, -0.05);
    q.y *= -1.;
    d1 = sdTail(q);
    d = min(d, d1);

    // wings
    q = p - vec2(0.105, -0.015);
    d1 = sdWing(q);
    d = min(d, d1);

    q = p - vec2(0.105, -0.075);
    q.y *= -1.;
    d1 = sdWing(q);
    d = min(d, d1);

    return d;
}

vec3 hsv2rgb(vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x * 6. + vec3(0., 4., 2.), 6.) - 3.) - 1., 0., 1.);
	return c.z * mix(vec3(1.), rgb, c.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord - iResolution.xy)/ max(iResolution.x, iResolution.y);
    uv.y += sin(iTime * 1.55) / 10.;

    float signF = (step(fract(iTime / 5.) * 2. - 1., 0.) * 2. - 1.);
    float bla = signF * (uv.x / 3. + fract(iTime / 2.5) * 2. - 1.);

    uv = rotate(uv, sin(iTime * 1.55 + PI / 3.5) / 10.);       
    float k = 20.;
    float birdD = smoothMin(sdBird(uv * 1.1), 1., 8.);

    float fd = mix(1. - sdf(smoothMin(birdD, -bla, k)), 
                   sdf(smoothMin(birdD, bla, k)), 
                   sdfFw(bla));

    vec3 c = vec3(sin(iTime / 5.) * 0.5 + 0.5, 0.8, 0.2);
    fragColor = vec4((fd + 0.2) * (hsv2rgb(c) + 0.6), 1.);
}

float Hash21(vec2 p){
    p = fract(p*vec2(234.34,435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 screenUV = fragCoord.xy/iResolution.xy;

    uv += iTime*.1;
    uv *= 5.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float n = Hash21(id); //random number between 0-1
    float width =.25;

    if(n<.5) gv.x *= -1.;
    float d = abs(abs(gv.x+gv.y)-.5);
    vec2 cUv = gv-sign(gv.x+gv.y+.001)*.5;
    d = length(cUv);
    float mask = smoothstep(.01,-.01, abs(d-.5)-width);
    float angle = atan(cUv.x,cUv.y); // -pi to pi
    float checker = mod(id.x+id.y,2.)*2.-1.;
    float flow = sin(iTime+checker*angle*2.);

    float x = checker*angle/1.57-iTime*.3;
    float y = (d-(.5-width))/(2.*width);
    y = abs(y-.5)*2.;
    vec2 tUv = vec2(x/2.,y);

    vec4 col = texture(iChannel0,fract(tUv))*mask;
    if(col.a < .75) col = vec4(0.05,0,0.025,1);

    col.a = 1.;
    //if(gv.x>.45 || gv.y>.45) col = vec3(1,0,0);
    fragColor = col;
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)cos((h)*6.3+vec3(0,23,21))*.5+.5
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(
        float i=0.,g=0.,e,s;
        ++i<99.;
        O.rgb+=mix(r/r,H(log(s)),.7)*.05*exp(-.45*i*i*e))
    {
        p=g*d-vec3(.05*sin(iTime*.5),.1,.7);
        p=R(p,normalize(vec3(1,-2,2)),iTime*.5);
        s=4.;
        vec4 q=vec4(p,sin(iTime*.4)*.5);
		for(int j=0;j++<8;)
            q=abs(q),
            q=q.x<q.y?q.zwxy:q.zwyx,
            s*=e=1.35/min(dot(q,q),0.54),
            q=q*e-vec4(0,4,.8,3);
        g+=e=min(
            length(q.w)/s,
            length(cross(q.xyw,normalize(vec3(1,2,3))))/s-.0002
        );
    }
    O=pow(O,vec4(5));
 }

