struct Triangle{
vec2 A;
vec2 B;
vec2 C;
};

struct Square{
vec2 _A;
vec2 _B;
vec2 _C;
vec2 _D;
};

float _signum(vec2 _a, vec2 _b, vec2 _c, vec2 _d){
//calculate vectors AB and AC from points abc
vec2 _AB = _b-_a;
vec2 _AC = _c-_a;
vec2 _AD = _d-_a;

vec3 _AB_3D = vec3(_AB, 0.0);
vec3 _AC_3D = vec3(_AC, 0.0);
vec3 _AD_3D = vec3(_AD, 0.0);

//calculate cross product
vec3 _crossProduct = cross(_AB_3D, _AD_3D);

return sign(_crossProduct.z);
}

float signum(vec2 a, vec2 b, vec2 c){
//calculate vectors AB and AC from points abc
vec2 AB = b-a;
vec2 AC = c-a;

vec3 AB_3D = vec3(AB, 0.0);
vec3 AC_3D = vec3(AC, 0.0);

//calculate cross product
vec3 crossProduct = cross(AB_3D, AC_3D);

return sign(crossProduct.z);
}

bool isInSquare(in vec2 point, in Square sq){

float d1 = signum(sq._A, sq._B, point);
float d2 = signum(sq._B, sq._C, point);
float d3 = signum(sq._C, sq._D, point);
float d4 = signum(sq._D, sq._A, point);

if(((d1>0.0)&&(d2>0.0)&&(d3>0.0)&&(d4>0.0))||((d1<0.0)&&(d2<0.0)&&(d3<0.0)&&(d4<0.0)))
return true;
else
return false;
}

bool isInTriangle(in vec2 point, in Triangle tr){

float d1 = signum(tr.A, tr.B, point);
float d2 = signum(tr.B, tr.C, point);
float d3 = signum(tr.C, tr.A, point);

if(((d1>0.0)&&(d2>0.0)&&(d3>0.0))||((d1<0.0)&&(d2<0.0)&&(d3<0.0)))
return true;
else
return false;
}

