int idObj;
const int idVin = 1, idCol = 2, idCap = 3, idFlr = 4;
const float pi = 3.14159, sqrt3 = 1.7320508;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, cRad, cLen, hp, br, tw, a, s, r;
  dMin = dstFar;
  p.xz -= HexToPix (gId[0] * hgSize[0]);
  cRad = 0.1;
  cLen = 1.;
  tw = 1.;
  d = dMin;
  vLev = 0.;
  for (float k = float (VAR_ZERO); k < 5.; k ++) {
    tw = - tw;
    s = (k + 1.) / 5.;
    hp = tw * (16. - 10. * s) + bRad - 0.5;
    br = (0.015 - 0.01 * s + 0.005 * (bRad - 0.75)) * (1.5 - 0.3 * p.y / cLen);
    q = p;
    q.y -= (bDel + 1.3 * s) * hp;
    a = 2. * pi * q.y / hp;
    q.xz = Rot2D (q.xz, (5. - 3. * bDel) * pi * (sign (bRad - 0.5) * a +
       (0.01 * bDel + 0.3 * s) * sin (3. * a)));
    q.x -= cRad + br;
    r = length (q.xz) - br;
    if (r < d) vLev = s;
    d = SmoothMin (d, r, 0.005);
  }
  d = max (d, abs (p.y - cLen) - cLen);
  DMIN (idVin);
  q = p;
  q.y -= cLen;
  d = PrCylDf (q.xzy, cRad, cLen);
  DMIN (idCol);
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (6. * atan (q.z, - q.x) / (2. * pi) + 0.5) / 6.));
  q.y -= 2. * cLen + 0.065 - 0.1 * cos (pi * length (q.xz) / (0.5 * sqrt3 * hgSize[0]));
  q.z = abs (q.z) - 0.04;
  d = PrRoundBox2Df (q.yz, vec2 (0.025, 0.01), 0.01);
  DMIN (idCap);
  q = p;
  q.y = abs (q.y - cLen) - cLen + 0.03;
  d = PrCylDf (q.xzy, cRad + 0.05, 0.03);
  DMIN (idCap);
  return 0.7 * dMin;
}

void SetObjConf ()
{
  vec2 fRand;
  fRand = Hashv2v2 (gId[0] + 27.1);
  bRad = 0.5 + 0.5 * fRand.x;
  bDel = fRand.y;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize[0] / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]), dot (ro.xz, edN[2])) / hgSize[0];
  pM = HexToPix (PixToHex (ro.xz / hgSize[0]));
  gIdP = vec2 (-999.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId[0] = PixToHex (p.xz / hgSize[0]);
    if (gId[0] != gIdP) {
      gIdP = gId[0];
      SetObjConf ();
    }
    d = ObjDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar || p.y < 0. || p.y > 3.) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float FObjDf (vec3 p)
