		col += vec3(.18) * vec3(.8, .3, .2) * sky_dif * occ;
		col += vec3(.18) * vec3(.7, .3, .2) * bou_dif * occ;
	}
	else col = env_color(rd);

	fragColor = vec4(pow(col, vec3(.4545)), 1);
}

