float AABBDist(vec3 lower, vec3 upper, vec3 p) {
    p -= (lower+upper) * 0.5;
    vec3 b = upper - lower;
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
