//https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float OpSmoothUnion(float d1, float d2, float k) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float SpheresUnion(vec3 p) {
    vec3 s1c = vec3(0, sin(iTime / 2.0), cos(0.5*iTime));
    vec3 s2c = vec3(0, cos(iTime / 10.0), sin(iTime));
    vec3 s3c = vec3(sin(iTime), sin(iTime), 0.5);
    float r = 1.0;
    float d1 = SphereDist(s1c, 1.0, p);
    float d2 = SphereDist(s2c, 1.0, p);
    float d3 = SphereDist(s3c, 0.8, p);
    float d4 = AABBDist(vec3(-0.25, -0.25, 0.25), vec3(0.25, 0.25, 0.75), p);
    return OpSmoothUnion(OpSmoothUnion(OpSmoothUnion(d1, d2, 0.5), d3, 0.5), d4, 0.5);
}

#define f(p) SpheresUnion(p)

//https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 CalcNormal( in vec3 p ) // for function f(p)
{
    const float h = 0.0001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*f( p + k.xyy*h ) + 
                      k.yyx*f( p + k.yyx*h ) + 
                      k.yxy*f( p + k.yxy*h ) + 
                      k.xxx*f( p + k.xxx*h ) );
}

Inter RayMarch(Ray r) {
    const int maxIter = 50;
    vec3 p = r.o;
    float t = 0.0;
    float prevMinDist = 1e9;
    for (int i=0; i<maxIter; i++) {
        p = r.o + t * r.d;
        float minDist = SpheresUnion(p);
        if (minDist < 1e-2) {
            return Inter(t, CalcNormal(p), vec3(0, 1, 0));
        }
        if (minDist - prevMinDist > 10.0) {
            return Inter(1e9, vec3(0), vec3(0));
        }
        prevMinDist = minDist;
        t += minDist;
    }
    return Inter(1e9, vec3(0), vec3(0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 camPos = vec3(vec2(cos(iTime), sin(iTime)) * 5.0, 10);
    vec3 camTarget = vec3(0, 0, 0);
    vec3 L = normalize(camTarget - camPos);
    const vec3 E = vec3(0.9, 0.9, 0.9);
    Ray r = CameraRay(fragCoord, camPos, camTarget);

    Inter i = RayMarch(r);
    vec3 col = vec3(0);

    if (i.t < 1e9) {
        col = dot(-L, i.n) * E * i.c;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

