//built from palm tree tutorial by Inigo Quilez, with grid shader by World of Zero, animation by me

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //time variable bounce
    float b = 0.5*cos(iTime);    

    //Center of origin
    vec2 q = uv - vec2(0.3,0.7);

    // Time varying background color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    //variable radius
    float r = 0.2;

    //float scale = 15.0 + 5.0*cos(iTime); 
    float scale=15.0;
    float gridPointX = step(float(mod(uv.x * scale, 0.75)), 0.1);
    float gridPointY = step(float(mod(uv.y * scale, 1.0)), 0.1);
    float gridPoint = min(1.0, gridPointX + gridPointY);

    // Output to screen
//    fragColor = vec4(gridPoint, gridPoint, gridPoint, 1.0);

    //modulated wave on radius r
    r +=0.1*cos(atan(q.y,q.x-0.025*b)*10.0 + 20.0*(q.x+0.01*b) + 1.0 + 0.05*b);

    //render canopy
    col *= smoothstep(r,r+0.005,length(q+.01*b));

    //trunk width
    float w = 0.02;
    //trunk texture
    w += 0.002*cos(120.0*q.y);
    //trunk taper
    w -= 0.005*cos(2.0*q.y);
    //ground union
    w+= exp(-40.0*uv.y);    
    //trunk curve
    float c = 0.2 * sin(2.0 * (q.y+0.05*b));

    //render trunk
    col *= 1.0 -(1.0-smoothstep(w,w+0.001,abs(q.x - c)))*(1.0-smoothstep(0.0,0.001,q.y));

    //superimpose black grid
    col -= (gridPoint,gridPoint,gridPoint);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Instead of generating regular hexagons right away,
// it generates a hexagon made of 45 degree angles
// after squishing the input coordinates to get the right proportions.
// This resulting hexagon will be 2 units wide and 4 units tall,
// and tiles diagonally at coordinate offset (1, 3).

// square root of 3 because hexagon stuff
#define SQRT3 (1.7320508)

// converts rgb hex code to a vec3
#define HEX(x) (vec3((x >> 16) & 255, (x >> 8) & 255, x & 255) / 255.)

// calculate the distance from the center of a hexagon
float hex(vec2 uv) {
    // loop around to tile
    uv = abs(
        mod(
            uv + vec2(1.0, 3.0),
            vec2(2.0, 6.0)
        ) - vec2(1.0, 3.0)
    );
    // return whichever is closer:
    // distance towards the top right side,
    // or distance towards the right side
    // (i.e. absolute value of x coordinate)
    return max(
        (uv.x + uv.y) / 2.,
        uv.x
    );
}

float map(float minv, float maxv, float x) {
    if (minv == maxv) {return step(minv, x);}
    return clamp(0., 1., (x - minv) / (maxv - minv));
}

// map the range [0, 1) to stripes of colors
// you can change the number of colors by changing the constant here
// and adding more colors and thresholds
#define COLOR_COUNT 4
// mixing sharpness. set to 0.0 to make it pixely
#define BLEND_FACT 0.02
vec3 colorize(float value) {
    const vec3 color[COLOR_COUNT] = vec3[](
        HEX(0x000000),
        HEX(0x300060),
        HEX(0x8000C0),
        HEX(0xFF66FF)
    );
    const float thres[COLOR_COUNT] = float[](
        0.0, // ignored
        0.5,
        0.8,
        0.95
    );
    // loop around
    value = fract(value);
    // i don't like using for loops, but it's the best way
    // i could think of to adapt to any number of colors.
    vec3 color_out = mix(
        color[COLOR_COUNT - 1],
        color[0],
        map(0.0, BLEND_FACT, value)
    );
    for (int i = 1; i < COLOR_COUNT; i++) {
        if (value > thres[i]) {
            color_out = mix(
                color[i - 1],
                color[i],
                map(thres[i], thres[i] + BLEND_FACT, value)
            );
        } else {
            return color_out;
        }
    }
    return color_out;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // makes it loop
    float time = fract(iTime / 2.);
    // Scales coords so that the diagonals are all a known distance from center
    vec2 uv = ( 2.* fragCoord - iResolution.xy ) / length(iResolution.xy);
    // Scales coords to the right proportions, and also scale the hexagons down
    uv *= vec2(1., SQRT3) * 8.;

    // time changing colors
    vec3 col = colorize(
        // i make 2 rectangle grids, because 1 triangle grid is too hard
        min(
            // grid with a hexagon centered at the screen
            hex(uv),
            // grid with 4 hexagons surrounding the center
            hex(uv + vec2(1.0, 3.0))
        // and finally make the colors shift outward with time
        ) * 0.5 - time
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}

float signedDistance(vec3 pos) {
    vec4 sphere = vec4(0, 0, 0, 0.3);
    return length(pos - sphere.xyz) - sphere.w;
}

//from https://www.shadertoy.com/view/lscSzl
vec3 encodeSRGB(vec3 linearRGB)
{
    vec3 a = 12.92 * linearRGB;
    vec3 b = 1.055 * pow(linearRGB, vec3(1.0 / 2.4)) - 0.055;
    vec3 c = step(vec3(0.0031308), linearRGB);
    return mix(a, b, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //fragment position on the view plane
    vec2 fragPos = (fragCoord - iResolution.xy * 0.5) / iResolution.yy;
    vec3 cameraPos = vec3(0, 0, 2);
    vec3 cameraForward = vec3(0, 0, -1);
    vec3 viewDir = normalize(vec3(fragPos, 0) - cameraPos);

    //ray march
    float maxDepth = 10.0;
    float depth = 0.0;
    float signedDist;
    int i = 0;
    for(; i < 1000; i++) {
        signedDist = signedDistance(cameraPos + viewDir * depth);
        if (signedDist < 0.0001) {
            break;
        }
        depth += signedDist;
        if (depth >= maxDepth) {
            depth = -1.0;
            break;
        }
    }

    //lighting
    vec3 color = vec3(0);

    if (depth > 0.0) {
        vec3 hit = cameraPos + viewDir * depth;
        vec3 normal = normalize(vec3(
                signedDistance(hit + vec3(0.00001, 0, 0)) - signedDistance(hit - vec3(0.00001, 0, 0)),
                signedDistance(hit + vec3(0, 0.00001, 0)) - signedDistance(hit - vec3(0, 0.00001, 0)),
                signedDistance(hit + vec3(0, 0, 0.00001)) - signedDistance(hit - vec3(0, 0, 0.00001))
            ));

        float ambientLight = 0.01;

        float diffuseIntensity = 0.7;
        vec3 lightDir = normalize(vec3(cos(iTime), 0.5 * cos(iTime - 1.0), sin(iTime)));
        float diffuseLight = diffuseIntensity * clamp(dot(normal, lightDir), 0.0, 1.0);

        color = vec3(ambientLight + diffuseLight);
    }

    // Output to screen
    if (fragPos.y < -0.1) color = encodeSRGB(color);
    else if (fragPos.y < 0.1) color = pow(color, vec3(0.4545));


    if (fragPos.y < -0.35) {
        color = vec3(fragCoord.x / iResolution.x);
        if (fragPos.y < -0.45) {
            color = encodeSRGB(color);
        } 
        else if (fragPos.y < -0.4) {
            float gamma = 2.2;
            color = pow(color, vec3(1.0 / gamma));
        }
    }


    fragColor = vec4(color,1.0);
}

#define MAX_STEP 100
#define MAX_DIST 100.
#define SURF_DIST 0.001
#define Sat(a) clamp(a, 0., 1.)

float smin(float a, float b, float k){
    float h = Sat(0.5 + 0.5 * (b - a) / k);
    return mix(b, a, h) - k * h * (1. - h);
}

float sdSphere(vec3 p, vec4 s){
    return length(p - s.xyz) - s.w;
}

float sdBox(vec3 p, vec3 b){
    p = abs(p) - b;
    return length(max(vec3(0.), p)) + min(max(p.x, max(p.y, p.z)), 0.);
}

float GetDist(vec3 p){
    float sphere = sdSphere(p, vec4(0., 0., 0., 0.8));
    float box = sdBox(p - vec3(sin(iTime * 0.5)* 2., 0., 0.), vec3(0.5));
    float plane = p.y + 1.2;

    float d = smin(sphere, box, 0.3);

    d = min(plane, d);
    return d;
}

vec3 GetNormal(vec3 p){
    vec2 e = vec2(0.001, 0.);
    return normalize(vec3(
                        GetDist(p + e.xyy) - GetDist(p - e.xyy),
                        GetDist(p + e.yxy) - GetDist(p - e.yxy),
                        GetDist(p + e.yyx) - GetDist(p - e.yyx)
                    ));
}


float RayMarch(vec3 ro, vec3 rd){
    vec3 p = ro;
    float d = 0., sd = 0.;
    for(int i = 0; i < MAX_STEP; i++){
        d = GetDist(p);
        sd += d;
        if(d < SURF_DIST || sd > MAX_DIST) break;
        p = p + d * rd;
    }
    return sd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0., 2., -8.);

    vec3 rd = normalize(vec3(uv.x, uv.y - 0.5, 2.));

    float d = RayMarch(ro, rd);

    if(d < MAX_DIST){
        vec3 p = ro + rd * d;
        vec3 lightPos = vec3(3., 4., -5.);
        vec3 N = GetNormal(p);
        vec3 L = normalize(lightPos - p);

        float dif = Sat(dot(N, L));

        d = RayMarch(p + N * SURF_DIST * 2., L);
        if(d < length(lightPos - p)) dif *= 0.5;

        col = vec3(dif);

    }

    fragColor = vec4(col,1.0);
}

const float PI = 3.14159265;
const float PI_2 = PI / 2.;

float fold(float x) {
    return abs(mod(x, 2.0) - 1.0);
}

float foldPlus(float x) {
    return log(0.5 + fold(x));
}

float smoothThres(float x, float strength) {
    return smoothstep(0.5 - strength, 0.5 + strength, x);
}

float smoothFold(float x, float strength) {
    return smoothThres(fold(x), strength);
}

vec3 colMap(float x, vec3 a, vec3 b) {
    return a * (1.0 - x) + b * x;
}

float star(float angle, float d, float roundness) {
    return foldPlus(angle * 10.) + d * roundness;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime / 8.);

    // Normalized pixel coordinates
    float scale = min(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / scale;
	uv -= iResolution.xy / scale / 2.;
    uv *= 2.;
    float dist = log(uv.x*uv.x+uv.y*uv.y); // not real distance, but useful for log spirals
    float angle = t - atan(uv.x, -uv.y) / PI / 2.;
    float angleLayer = t / 5.;
    float offsetLayer = t * 3.;
    float spaceLayer = 0.5;
    vec3 colA = vec3(1.0, 0.0, 0.6);
    vec3 colB = vec3(0.1, 0.5, 1.0);
    vec3 colC = vec3(1.0, 0.8, 0.0);
    vec3 col = vec3(fold(t * 16.));

    if (dist >= -8.) {
        for (float iRing = 0.; iRing < 25.; iRing += 1.) {
            if (star(angle - (iRing * angleLayer), dist, 1.1) < spaceLayer * (iRing - offsetLayer) - 7.){
                switch (int(iRing) % 3) {
                    case 0:
                        col = colA;
                        break;
                    case 1:
                        col = colB;
                        break;
                    case 2:
                        col = colC;
                }
                break;
            }
        }
    }

    // Output to screen
    fragColor = vec4(col, 1.0);
}

