float crys(vec3 p, vec3 pp) //makes a cool fluidy warping crystal thingy, p = result centre, pp = kifs centre
{
  for(float i=0.;i<4.;i++) //blah blah blah kifs yay funky lots of cubes
  {
      pp.xy*=rot(i - 2.+0.1); //rotate it and do fun stuff with "i"
      pp.xy=abs(pp.xy)-i*0.2; //free duplication tactics here
      pp.xy=abs(pp.xy)-2.1; //more high performance cloning
      pp.yz*=rot(i); //some more rotation just to make sure it's REALLY messed up by now
  }
  float b=bx(pp*0.9,vec3(2))-0.03; //create a rando cube in the middle lets go
  b=mix(b,bx(p,vec3(1)),0.5); //mix is actually amazing with SDFs, this turns the whole mess of cubes into something actually interesting and cool. comment out to see the mess instead
  float g=length(p); //i just calculated the distance to the centre of this world or something
  glw+=0.01/(0.01+g*g)*mix(vec3(1,0,0.2),vec3(0.2,0.5,1),sin(t/4.)*0.5+0.5); //glow glow glow glow glow glow glow glow glow
  return b; //"box"
}

vec2 mp(vec3 p) //scene goes here yes
{  
  float i = clamp(10.-t,0.,10.); //smooth intro morph variable yay cool
  float z = (sin(t/3.)*0.5+0.5)*1.5; //slow z warping for better effect
  float ta = 1.-pow(1.-mod(t,3.) * 0.3,2.);
  vec3 co=mix(vec3(1,0,0.2),vec3(0.2,0.5,1),sin(-t/4.)*0.5+0.5); //funky colours yup
  vec3 pp=p-vec3(0,0,i+z); //second position for things yes
  vec3 ppp=p; //more positions because why not
  pp.xy*=rot(-t/3.); //rotate it
  ppp.xy*=rot(t*0.1); //rotate it too
  vec2 cry = vec2(crys(ppp,pp),1.); //it's a glowy crystal, go cry about it - but if you wanna see a different varient, swap ppp and pp in this line
  pp=p;pp.yz*=rot(1.57); //reset pp so we can reuse it and also rotate it aprox 90 degrees
  float tr = tor(pp-vec3(0,12.*ta,0), vec2(16.*ta,0.01)); //donut time!
  cry.x=min(cry.x,tr); //add donut we don't really care about the material it's just for glow
  glw+=0.01/(0.01+tr)*co; //do the glowy :D
  pp=p-vec3(0,9,12);pp.x=abs(pp.x)-11.;pp.x*=0.3;//hooray for free cloning technology
  float br=bx(pp,vec3(0.5,30,100))-0.2; //make background walls exist
  pp.z-=mod(t*5.,20.)+5.; pp.z=abs(pp.z)-10.;pp.z=abs(pp.z)-10.;//bars, he says, BARS!
  float g=bx(pp,vec3(0.8,30,2));//glow amount for bars
  br=min(br,g);//combine so that we don't get messyness
  vec3 coco=vec3(1,1,1)-co; //inverted col
  glw+=0.01/(0.01+g)*coco*0.2; //weird outer glowy stuff idk either but hey it looks neat
  ppp=p;ppp.y=abs(ppp.y)-5.;//get position make position mirror on Y;
  vec2 flr = vec2(bx(ppp-vec3(0,4,0),vec3(50,1,100)),2); //make floor/roof stuff
  flr.x=min(flr.x,br); //actually add the side bits to the raymarch so they don't just get sad and become messy
  return flr.x<cry.x?flr:cry; //and that's that - i wanted to add more bits on the floor/roof but it was causing issues with lighting :(
}

vec2 tr(vec3 ro, vec3 rd, float f) //standard raymarching stuff with a distance field multiplier so we can flip things inside out for transparency
{
  vec2 d = vec2(0); //d=distance, duh. But it also keeps track of the material ID
  for(int i = 0; i < 256; i++) //loopy loopy looping loop
  {
    vec3 p=ro+rd*d.x; //the position is HERE!
    vec2 s=mp(p);s.x*=f; //grab the scene SDF
    d.x+=s.x;d.y=s.y; //move and update material id
    if(d.x>64.||s.x<0.001)break; //break if we reached something or went too far
  }
  if(d.x>64.)d.y=0.;return d; //return distance and ID
}

vec3 nm(vec3 p)
{
  vec2 e = vec2(0.001,0); return normalize(mp(p).x-vec3(mp(p-e.xyy).x,mp(p-e.yxy).x,mp(p-e.yyx).x)); //normals hooray
}

vec4 px(vec4 h, vec3 rd, vec3 n) //pixel.. shader? it shades pixels, okay? got it? cool.
{
  vec4 bg=vec4(0.1,.1,0.1,1); //background colour
  if(h.a==0.)return bg; //oh no we hit the background how sad
  vec4 a=h.a==1.?vec4(0.,1.,1.,0.2):vec4(0.1,0.1,0.15,1); //give it some colour, except not because the glow does most of it
  float d=dot(n,-rd); //ahha this surface is pointy - OR IS IT?!
  float dd=max(d,0.); //diffuse lighting that makes things actually look like things instead of not looking like things
  float f=pow(1.-d,4.); //reflecty edges that aren't really reflecty but look reflecty because they're edges
  float s=(pow(abs(dot(reflect(rd,n),-rd)),40.)*10.); //light bright BUT ONLY WHERE IT NEEDS TO BE
  if(h.a>1.)s*=0.05;//make id 2 more matt instead of shiny
  float ao=clamp(1.-mp(h.xyz+n*0.1).x*14.,0.,1.)*0.9; //sorry, we don't want lighting where things are too close to each other :(
  return vec4(mix(a.rgb*(dd-ao)+s,bg.rgb,f),a.a); //mix it all together - you can replace the whole thing with a vec4(a.rgb*oneofthevalueshere,1.) if you just wanna see what a thing looks like
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) //time to do all the stuff
{
  t=iTime; //time is only temporary
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y); //u? v? u,v, uv!
  uv -= 0.5; uv /= vec2(iResolution.y / iResolution.x, 1.); //but now in the middle
  float ts=1.,io=1.14; //ooh fun variables to track transparency bits
  vec3 ro=vec3(0,0,-10),rd=normalize(vec3(uv,1.)),oro=ro,ord=rd,cp,cn,rc,cc; //cam pos and ray dir and even more variables for the transparency loop
  for(int i=0;i<4;i++) //transparency loop! increase the iterations for more transparency depth but less performance if you feel like it. 4 seems like a good middle to me.
  {
    vec2 fh=tr(oro,ord,1.); //go find front of object or something
    cp=oro+ord*fh.x;cn=nm(cp); //figure out the position and normal of the hit
    vec4 c=px(vec4(cp,fh.y),ord,cn); //grab shaded colour for pixel
    if(fh.y==0.||c.a==1.) {cc=mix(cc,c.rgb,ts); break;} //oh no it's the background we gotta get out of here!!
    ro=cp-cn*0.01;rd=refract(ord,cn,1./io); //inject ourselves into the object so we can go THROUGH it!
    vec2 bh=tr(ro,rd,-1.);//go through inverted distance field to find the exiting face of the object we're currently traversing because yes.
    cp=ro+rd*bh.x;cn=nm(cp); //figure out position and normal again because we forgot it already duh
    oro=cp+cn*0.01;ord=refract(rd,-cn,io); //inject ourselves back.. out? outject ourselves? whatever, we back outside again now
    if(dot(ord,ord)==0.)ord=reflect(rd,-cn); //TOTAL INTERNAL REFRACTION D: (but actually it looks sick so whatever)
    cc=mix(cc,c.rgb,ts);ts-=c.a; //finally we update the colour
    if(ts<=0.)break; //if we're still transparent, keep going. If not... time to BREAK OUT OF THIS LOOP!
  }
  fragColor=vec4(cc+glw,1.); //dude we gotta see things somehow, this does that
}

// Maximum number of iterations.
#define MAX 1024

// Point at which to stop and consider it will not converge.
#define INF 1.0e20

// Any value >1 will give that number of sub-samples on each of X and Y.
#define AA 0

precision highp float;


// Sorry for the state of the code.

int iterations( vec2 x0 )
{
    float r = length( x0 );
    float theta = atan( x0.y, x0.x );
    float ln_r = log( r );

    float a = x0.x;
    float b = x0.y;

    for ( int i = 0; i < MAX; i++ )
    {
        float r1 = exp( a * ln_r - b * theta );
        float theta1 = b * ln_r + a * theta;
        if ( abs( r1 ) > INF )
        {
            return i;
        }

        a = r1 * cos( theta1 );
        b = r1 * sin( theta1 );
    }
    return MAX;
}

vec4 run( vec2 p )
{
    float scale = 20.0;
    vec2 origin = vec2( -0.25, 0.0 );

    vec2 zoomPoint = vec2( 0.2795586372245285, -0.15761016844585213 );

    float frame = 400.0 * ( 1.0 - cos( iTime * 0.5 ) );

    float zoomFactor = pow( 0.99, frame );

    scale *= zoomFactor;

    origin = ( 1.0 - zoomFactor ) * zoomPoint + zoomFactor * origin;

    p = p * scale - origin;

    int it = iterations( p );
    float r = ( cos( float( it ) / 16.0 ) + 1.0 ) * 0.5;
    float g = ( cos( float( it ) / 16.0 + 2.094395102 ) + 1.0 ) * 0.5;
    float b = ( cos( float( it ) / 16.0 - 2.094395102 ) + 1.0 ) * 0.5;

    return it == MAX ? vec4(0.0, 0.0, 0.0, 1.0 ) : vec4(r, g, b, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

#if AA > 1
    vec4 result = vec4( 0.0 );
    for ( int j = 0; j < AA; j++ )
    {
        for ( int i = 0; i < AA; i++ )
        {
            vec2 offset = vec2( i, j ) / float( AA ) - 0.5;
            vec2 p = ( ( fragCoord + offset ) * 2.0 / iResolution.x ) - vec2( 1.0, iResolution.y / iResolution.x );
            result += run( p );
        }
    }
    fragColor = result / float( AA * AA );
#else
    vec2 p = ( fragCoord * 2.0 / iResolution.x ) - vec2( 1.0, iResolution.y / iResolution.x );
    fragColor = run( p );
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;
    vec2 q= p-vec2(0.5,0.5);
    // Time varying pixel color
    //vec3 col =mix(vec3 (0.8, 0.5,0.9),vec3(1.0, 0.8, 0.5),sqrt(p.y));
    vec3 col =mix(vec3 (0.8, 0.5,0.9),vec3(1.0, 0.8, 0.5),sin(iTime+p.y));
    //vec3 col = 0.8 + 0.5*cos(iTime+p.xyx+vec3(0,2,4));


    //create spikes
    float r = 0.2+0.1*cos(atan(q.y,q.x)*(1.0+iTime)+220.0*q.x+iTime*2.0);


    col *= smoothstep(r,r+0.01, length(q));


    r=0.02;

    //openning
    col *= smoothstep(iTime*0.1, 0.001, abs(q.x+0.1*sin(20.0*q.y)));

    //background stuffs
    col *= smoothstep(r, r+0.01, abs(q.x+1.0*sin(220.0*(q.y-0.08)+iTime*0.02)));
    col *= smoothstep(r, r+0.001, abs(q.x+1.0*cos(22.0*(q.x-0.7)+iTime*0.005)));
    col *= smoothstep(r, r+0.07, abs(2.0*cos(22.0*(q.x-0.7)+1.0*sin(22.0*(q.y-0.08))+iTime*0.5)));


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define iTime (0.25 * iTime)

#define PI 3.14159265359
#define AA 2

// Convenience structs
struct Ray {
    vec3 pos;
    vec3 dir;
};

struct TraceInfo {
    float dist;
    vec3 pos;
    vec3 nor;
    int id;
};

struct Plane {
    vec3 pos;
    vec3 nor;
};

#define N 10
Plane planeFitting(in vec3[N] points) {
    float Jxx = 0.0, Jyy = 0.0;
    float Jxy = 0.0, Jxz = 0.0, Jyz = 0.0;
    float Jx = 0.0, Jy = 0.0, Jz = 0.0;
    for (int n=0; n < N; n++) {
        vec3 p = points[n];
        Jxx += p.x * p.x, Jyy += p.y * p.y;
        Jxy += p.x * p.y, Jxz += p.x * p.z, Jyz += p.y * p.z;
        Jx += p.x, Jy += p.y, Jz += p.z;
    }

    // Precomputation
    float P = float(N);
    float c1 = Jx * Jyy - Jy * Jxy;
    float c2 = Jx * Jy  - P  * Jxy;
    float c3 = Jx * Jyz - Jy * Jxz;
    float c4 = Jx * Jxy - Jy * Jxx;

    // Solve for plane coefficients: z = ax + by + c
    float a = (c1 * (Jx * Jz - P * Jxz) - c2 * c3) / (c1 * (Jx * Jx - P * Jxx) - c2 * c4);
    float b = (c3 - c4 * a) / c1;
    float c = (Jxz - Jxx * a - Jxy * b) / Jx;

    // Compute position and normal
    vec3 n = vec3(a, b, -1.0);
    float l2 = a * a + b * b + 1.0;
    return Plane(-n * c / l2, n / sqrt(l2));
}

// Double integral of mod(floor(x) + floor(y), 2) from a to b
float integrateCheckers(in vec2 a, in vec2 b) {
    vec2 u = b - a, v = abs(mod(b, 2.0) - 1.0) - abs(mod(a, 2.0) - 1.0);
    return 0.5 * (u.x * u.y - v.x * v.y);
}

// 2D rotation matrix
mat2 Rotate2D(in float theta) {
    float c = cos(theta), s = sin(theta);
    return mat2(c, s, -s, c);
}

// Primitive intersectors
void addPlane(in vec3 pos, in vec3 nor, in int id, in Ray ray, inout TraceInfo scene) {
    float d = dot(ray.dir, nor);
    if (d != 0.0) {
        float t = dot(pos - ray.pos, nor) / d;
        if (t > 0.0 && (scene.id != 0 ? t < scene.dist : true)) {
            scene.dist = t;
            scene.pos = ray.pos + ray.dir * t;
            scene.nor = nor;
            scene.id = id;
        }
    }
}

void addSphere(in vec3 pos, in float radius, in int id, in Ray ray, inout TraceInfo scene) {
    ray.pos -= pos;

    float a = dot(ray.dir, ray.dir);
    float b = dot(ray.pos, ray.dir);
    float c = dot(ray.pos, ray.pos) - radius * radius;

    float discr = b * b - a * c;
    if (discr > 0.0) {
        float t = (-b - sign(c) * sqrt(discr)) / a;
        if (t > 0.0 && (scene.id != 0 ? t < scene.dist : true)) {
            scene.dist = t;
            scene.pos = ray.pos + ray.dir * t;
            scene.nor = scene.pos / radius;
            scene.id = id;
            scene.pos += pos;
        }
    }
}

// Main scene
TraceInfo traceScene(in Ray ray) {
    TraceInfo scene = TraceInfo(-1.0, vec3(0.0), vec3(0.0), 0);

    vec3 o = vec3(sin(iTime + 1.0), cos(2.0 * iTime - 1.0), sin(iTime - 1.0));
    vec3 d = normalize(vec3(sin(2.0 * iTime), cos(iTime), sin(iTime) * cos(3.0 * iTime)));

    vec3[N] points;
    for (float n=0.0; n < float(N); n++) {
        float s1 = 0.2 * n, s2 = cos(n), s3 = sin(n);
        vec3 point = 5.0 * vec3(sin(s1 * iTime) * cos(s1 * iTime), cos(s2 * iTime), sin(s3 * iTime));
        point -= d * dot(point - o, d);
        point += d * sin(point.x) * cos(point.y);
        addSphere(point, 0.2, 1, ray, scene);
        points[int(n)] = point;
    }

    Plane fitting = planeFitting(points);
    addPlane(fitting.pos, fitting.nor, 2, ray, scene);

    return scene;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 mouse = ivec2(iMouse.xy) == ivec2(0) ? vec2(0.0) : (iMouse.xy - center) / iResolution.y * PI;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    // Mouse rotation
    mat2 yaw = Rotate2D(-mouse.x);
    mat2 pitch = Rotate2D(-mouse.y);

    // Take multiple subsamples for antialiasing
    float aaStep = 1.0 / float(AA);
    for (float xOffs=0.0; xOffs < 1.0; xOffs += aaStep) {
        for (float yOffs=0.0; yOffs < 1.0; yOffs += aaStep) {
            vec2 uv = (fragCoord + vec2(xOffs, yOffs) - center) / iResolution.y;
            vec3 color;

            Ray camRay = Ray(vec3(0.0, 0.0, 8.0), normalize(vec3(uv, -1.0)));

            // Rotate with mouse
            camRay.pos.yz *= pitch;
            camRay.pos.xz *= yaw;
            camRay.dir.yz *= pitch;
            camRay.dir.xz *= yaw;

            // Render
            TraceInfo scene = traceScene(camRay);
            if (scene.id != 0) {
                vec3 mat = vec3(1.0, 0.0, 0.0);
                if (scene.id == 2) {
                    vec3 u = normalize(vec3(-scene.nor.z, 0.0, scene.nor.x));
                    vec3 v = cross(u, scene.nor);
                    vec2 texCoords = scene.pos * mat2x3(u, v);
                    float filt = scene.dist * scene.dist / 2500.0; // Hack filter
                    mat = vec3(integrateCheckers(texCoords - filt, texCoords + filt) / (4.0 * filt * filt));
                }

                float shade = max(0.1, dot(scene.nor, -camRay.dir));
                if (scene.id == 2) shade = 1.0;
                color = mat * shade;
            }

            else color = mix(vec3(0.25, 0.25, 1.0), vec3(1.0), 0.5 + 0.5 * camRay.dir.y);
            fragColor.rgb += pow(color, vec3(0.75));
        }
    }

    // Average
    fragColor.rgb /= float(AA * AA);
}

mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
float rand(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}
float noise(vec3 p)
{
    vec3 fp = floor(p);
	vec3 fs = fract(p);
    vec3 sf = vec3(1.,0.,0.);

    float a = rand(fp);
    float b = rand(fp + sf);
    float c = rand(fp + sf.yxz);
    float d = rand(fp + sf.xxz);
    float e = rand(fp + sf.yyx);
    float f = rand(fp + sf.xyx);
    float g = rand(fp + sf.yxx);
    float h = rand(fp + sf.xxx);

    vec3 u = smoothstep(0.,1.,fs);
  //  u = pow(fs,vec3(3.));

    return mix(mix(
        		mix(a,b,u.x),
        		mix(c,d,u.x),u.y),
               mix(
                mix(e,f,u.x),
                mix(g,h,u.x),u.y),u.z
            );
}
// 2D Random
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}
float fbm(vec3 p)
{
    float v = 0.;
    float a = .5;
    float f = 0.;
    for(int i=0;i<3;i++)
    {
        v+=a*noise(p);
        p*=2.;
        a*=.5;
    }
    return v;
}
vec2 map(vec3 p)
{
    float o = 10.;
    float id = 0.;

    vec2 dir = vec2(1.,1.);
    vec2 wave = vec2(0.);
    for(int i = 0; i< 3;i++)
    {
        dir *= rot(.2);
    	wave += dir * iTime/3.;
    	p.y -= (1. - pow(noise(p.xz/(3. + float(i) ) + wave),4. ) )/2.;
    }

    o = -p.y + 0.;
    if(o < 1.)
    {
        o -= fbm(p/2.1 + iTime/1.)/3.;
    }
    return vec2(o,id);
}

vec2 march(vec3 cp , vec3 rd)
{
    float depth = 0.;
    for(int i = 0 ; i< 256 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        if(abs(d.x) < 0.01)
        {
            return vec2(depth,d.y);
        }
        if(depth > 200.)break;
        depth += d.x;
    }
    return vec2(-depth , 0.);

}
vec3 skyColor(vec3 rd,vec3 sun,float starToggle)
{
    vec3 sky = rd;
    rd.y = abs(rd.y);
    vec3 sunPos = sun;
    sky = vec3(0.);
    float ld = dot(sun,vec3(0.,1.,0.));
    float sd = abs( dot(sun,vec3(0.,0.,1.)) );
    vec3 rd2 = rd;
    rd2.xz *= rot(iTime/18.);
    vec3 star = vec3(0.,0.5,1.) + sin( rand(rd2)*10. );
    star *= step(fbm(rd2*200.), 0.2);
    star *= (sin(iTime*1. + fbm(rd2*50.)*30. ) + 1.)/2.;
    star = clamp( star - .05,vec3(0.),vec3(1.)) * starToggle;

    sky += vec3(0.,0.1,.4);
    sky += vec3(0.3,0.5,0.5) * ld;
    sky += max(0.,pow((1. - ld),8.) ) * star;
    sky += vec3(0.6,0.0,-0.1) * sd;
    float siz = .1;
    float srd = clamp(dot(sunPos,rd) - siz,0.,1.) + siz;
    float s = mix(.3,1.3,ld);
    srd = pow( srd , 3.) - .7 + pow( srd , 120.);
    sky += max(0.01 , srd);
    return sky;
}

vec3 seaCol(vec3 rd,vec3 N,vec3 dist,vec3 pos,vec3 l)
{
    vec3 o = vec3(0.);
    float dd = max(dot(rd,vec3(0.,1.,0.)),0.);
    float diff = max(0.,dot(-l,N));
    float fr = clamp(1.0 - dot(N, -rd), 0.0, 1.0);
    fr = pow(fr, 13.0) * 0.65;
    vec2 e = vec2(1.0, -1.0) * 0.05;
    vec3 N2 = normalize(
              e.xyy * map(pos + e.xyy).x +
              e.yyx * map(pos + e.yyx).x +
              e.yxy * map(pos + e.yxy).x +
              e.xxx * map(pos + e.xxx).x);
    vec3 ref = reflect(rd,N2);
    vec3 sky = vec3(.8,.8,1.);

    sky = skyColor( mix(rd,ref, 1.) ,l,1.);
    vec3 sea = vec3(.1,.3,.7)/2.;
    o =  mix(vec3(0.),vec3(1.),dd);
    o += mix(sea,vec3(0.,0.,0.),dd);
    o += mix(dd,1.,.3) * fr * mix(sea * 3. , sea , dd ) + fr/10.;
    o += max(sky/2. - vec3(.5) , 0.);
    return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec3 cp = vec3(0.,0.,-6.);
    vec3 target = vec3(0.);

    vec3 col = vec3(0.);

    vec3 cd = normalize(vec3(target - cp));
    vec3 cs = normalize(cross(cd , vec3(0.,1.,0.)));
    vec3 cu = normalize(cross(cd,cs));

    float fov = 2.5;

    vec3 rd = normalize(cd * fov + cs * p.x + cu * p.y);

    vec2 d = march(cp,rd);
    vec3 sun = normalize(vec3(0.,0.1,1.));
    sun.yz *= rot(iTime/6.);
    vec3 sky  = skyColor(rd,sun,1.);
    col = sky;
    if( d.x > 0.)
    {
        vec2 e = vec2(1.0, -1.0) * 0.0005;
        vec3 pos = cp + rd * d.x;
        vec3 N = normalize(
                  e.xyy * map(pos + e.xyy).x +
                  e.yyx * map(pos + e.yyx).x +
                  e.yxy * map(pos + e.yxy).x +
                  e.xxx * map(pos + e.xxx).x);
        col = N;

    	// diff = mix(diff , 1.,.1);
        vec3 mat = seaCol(rd,N,pos - cp,pos,sun);
        col = mat;
        sky  = skyColor(rd,sun,0.);
		float t = 1. - exp(-.000003 * d.x * d.x * d.x);
        col = mix(mat, sky, t);
    }

    fragColor = vec4(col, 1.0);
}

#define PI 3.14159265
